import React, { Suspense } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { Header, Footer, Sidebar } from '@/components';
import { AuthProvider } from './contexts/AuthContext';
import { RBACProvider } from './contexts/RBACContext';
import { ProtectedRoute, PublicRoute, AdminRoute } from './components/routing';
import { LoadingSpinner } from './components/ui/LoadingSpinner';

// Lazy load components for better performance
const Landing = React.lazy(() => import('./pages/Landing'));
const Login = React.lazy(() => import('./pages/Login'));
const Register = React.lazy(() => import('./pages/Register'));
const Dashboard = React.lazy(() => import('./pages/home/Dashboard'));
const Settings = React.lazy(() => import('./pages/settings/Settings'));
const AdminPanel = React.lazy(() => import('./pages/admin/AdminPanel'));

// Loading fallback component
const PageLoader: React.FC = () => (
  <div className="min-h-screen flex items-center justify-center bg-secondary-50">
    <div className="text-center">
      <LoadingSpinner size="lg" />
      <p className="mt-4 text-secondary-600">Loading...</p>
    </div>
  </div>
);

// Layout components
const AuthenticatedLayout: React.FC<{ children: React.ReactNode }> = ({ children }) => (
  <div className="min-h-screen flex flex-col">
    <Header />
    <div className="flex flex-1">
      <Sidebar className="w-64 hidden md:block" />
      <main className="flex-1">
        {children}
      </main>
    </div>
  </div>
);

const PublicLayout: React.FC<{ children: React.ReactNode; showFooter?: boolean }> = ({ 
  children, 
  showFooter = true 
}) => (
  <div className="min-h-screen flex flex-col">
    <Header />
    <div className="flex-grow">
      {children}
    </div>
    {showFooter && <Footer />}
  </div>
);

function App() {
  return (
    <AuthProvider>
      <RBACProvider>
        <Suspense fallback={<PageLoader />}>
          <Routes>
            {/* Public Routes - Only accessible when not authenticated */}
            <Route 
              path="/login" 
              element={
                <PublicRoute>
                  <PublicLayout showFooter={false}>
                    <Login />
                  </PublicLayout>
                </PublicRoute>
              } 
            />
            <Route 
              path="/register" 
              element={
                <PublicRoute>
                  <PublicLayout showFooter={false}>
                    <Register />
                  </PublicLayout>
                </PublicRoute>
              } 
            />

            {/* Protected Routes - Require authentication */}
            <Route 
              path="/dashboard" 
              element={
                <ProtectedRoute>
                  <AuthenticatedLayout>
                    <Dashboard />
                  </AuthenticatedLayout>
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/settings" 
              element={
                <ProtectedRoute>
                  <AuthenticatedLayout>
                    <Settings />
                  </AuthenticatedLayout>
                </ProtectedRoute>
              } 
            />

            {/* Admin Routes - Require admin role */}
            <Route 
              path="/admin" 
              element={
                <AdminRoute>
                  <AuthenticatedLayout>
                    <AdminPanel />
                  </AuthenticatedLayout>
                </AdminRoute>
              } 
            />

            {/* Landing/Home Route - Accessible to everyone */}
            <Route 
              path="/" 
              element={
                <PublicLayout>
                  <Landing />
                </PublicLayout>
              } 
            />

            {/* Catch-all route - Redirect to home */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </Suspense>
      </RBACProvider>
    </AuthProvider>
  );
}

export default App;

import React, { useState } from 'react';
import { useRBAC, PERMISSIONS } from '../../contexts/RBACContext';
import { api, User, ApiError } from '../../lib/api';
import { AdminGuard, PermissionGuard } from '../auth/RoleGuard';

interface RoleManagementProps {
  user: User;
  onRoleChanged: () => void;
}

interface RoleChangeModalProps {
  user: User;
  isOpen: boolean;
  onClose: () => void;
  onSuccess: () => void;
}

// Role change modal component
const RoleChangeModal: React.FC<RoleChangeModalProps> = ({
  user,
  isOpen,
  onClose,
  onSuccess,
}) => {
  const [selectedRole, setSelectedRole] = useState<'user' | 'admin'>(user.role);
  const [reason, setReason] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  if (!isOpen) return null;

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!reason.trim()) {
      setError('Please provide a reason for the role change');
      return;
    }

    if (selectedRole === user.role) {
      setError('Please select a different role');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      await api.updateUserRole(user.id, {
        role: selectedRole,
        reason: reason.trim(),
      });

      onSuccess();
      onClose();
    } catch (err) {
      if (err instanceof ApiError) {
        setError(err.message);
      } else {
        setError('Failed to update user role');
      }
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setSelectedRole(user.role);
    setReason('');
    setError(null);
  };

  const handleClose = () => {
    resetForm();
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
        <div className="mt-3">
          <h3 className="text-lg font-medium text-gray-900 mb-4">
            Change User Role
          </h3>

          <div className="mb-4 p-3 bg-gray-50 rounded-md">
            <div className="text-sm text-gray-600">User:</div>
            <div className="font-medium">{user.first_name} {user.last_name}</div>
            <div className="text-sm text-gray-500">{user.email}</div>
            <div className="text-sm">
              Current role: <span className="font-medium capitalize">{user.role}</span>
            </div>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                New Role
              </label>
              <select
                value={selectedRole}
                onChange={(e) => setSelectedRole(e.target.value as 'user' | 'admin')}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={loading}
              >
                <option value="user">User</option>
                <option value="admin">Admin</option>
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Reason <span className="text-red-500">*</span>
              </label>
              <textarea
                value={reason}
                onChange={(e) => setReason(e.target.value)}
                placeholder="Please provide a reason for this role change..."
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows={3}
                disabled={loading}
                required
              />
            </div>

            {error && (
              <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-md">
                <div className="text-red-600 text-sm">{error}</div>
              </div>
            )}

            <div className="flex space-x-3">
              <button
                type="submit"
                disabled={loading || !reason.trim() || selectedRole === user.role}
                className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? 'Updating...' : 'Update Role'}
              </button>
              <button
                type="button"
                onClick={handleClose}
                disabled={loading}
                className="flex-1 bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

// Role badge component
export const RoleBadge: React.FC<{ role: 'user' | 'admin'; className?: string }> = ({ 
  role, 
  className = '' 
}) => {
  const baseClasses = "inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium";
  const roleClasses = {
    user: "bg-green-100 text-green-800",
    admin: "bg-purple-100 text-purple-800",
  };

  return (
    <span className={`${baseClasses} ${roleClasses[role]} ${className}`}>
      {role.charAt(0).toUpperCase() + role.slice(1)}
    </span>
  );
};

// Role management actions component
export const RoleManagementActions: React.FC<RoleManagementProps> = ({
  user,
  onRoleChanged,
}) => {
  const [showRoleModal, setShowRoleModal] = useState(false);
  const { canManageUser } = useRBAC();

  if (!canManageUser(user.id)) {
    return null;
  }

  return (
    <PermissionGuard permission={PERMISSIONS.USER_MANAGE}>
      <div className="flex items-center space-x-2">
        <RoleBadge role={user.role} />
        <button
          onClick={() => setShowRoleModal(true)}
          className="text-blue-600 hover:text-blue-800 text-sm font-medium"
          title="Change user role"
        >
          Change Role
        </button>
      </div>

      <RoleChangeModal
        user={user}
        isOpen={showRoleModal}
        onClose={() => setShowRoleModal(false)}
        onSuccess={onRoleChanged}
      />
    </PermissionGuard>
  );
};

// Bulk role management component
interface BulkRoleManagementProps {
  selectedUsers: User[];
  onRoleChanged: () => void;
  onClearSelection: () => void;
}

export const BulkRoleManagement: React.FC<BulkRoleManagementProps> = ({
  selectedUsers,
  onRoleChanged,
  onClearSelection,
}) => {
  const [selectedRole, setSelectedRole] = useState<'user' | 'admin'>('user');
  const [reason, setReason] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  if (selectedUsers.length === 0) {
    return null;
  }

  const handleBulkRoleChange = async () => {
    if (!reason.trim()) {
      setError('Please provide a reason for the role change');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      await api.bulkUpdateUsers({
        user_ids: selectedUsers.map(u => u.id),
        action: 'role_change',
        role: selectedRole,
        reason: reason.trim(),
      });

      onRoleChanged();
      onClearSelection();
      setReason('');
    } catch (err) {
      if (err instanceof ApiError) {
        setError(err.message);
      } else {
        setError('Failed to update user roles');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <AdminGuard>
      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-medium text-blue-900">
            Bulk Role Management
          </h3>
          <button
            onClick={onClearSelection}
            className="text-blue-600 hover:text-blue-800 text-sm"
          >
            Clear Selection
          </button>
        </div>

        <div className="text-sm text-blue-700 mb-4">
          {selectedUsers.length} user{selectedUsers.length > 1 ? 's' : ''} selected
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-sm font-medium text-blue-700 mb-1">
              New Role
            </label>
            <select
              value={selectedRole}
              onChange={(e) => setSelectedRole(e.target.value as 'user' | 'admin')}
              className="w-full px-3 py-2 border border-blue-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              disabled={loading}
            >
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-blue-700 mb-1">
              Reason
            </label>
            <input
              type="text"
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              placeholder="Reason for role change..."
              className="w-full px-3 py-2 border border-blue-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              disabled={loading}
            />
          </div>

          <div className="flex items-end">
            <button
              onClick={handleBulkRoleChange}
              disabled={loading || !reason.trim()}
              className="w-full bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? 'Updating...' : 'Update Roles'}
            </button>
          </div>
        </div>

        {error && (
          <div className="mt-4 p-3 bg-red-50 border border-red-200 rounded-md">
            <div className="text-red-600 text-sm">{error}</div>
          </div>
        )}
      </div>
    </AdminGuard>
  );
};

