import React, { useState, useEffect } from 'react';
import { api, ApiError } from '../../lib/api';
import { Button } from '../ui/Button';

interface ResetPasswordFormProps {
  token?: string;
  onSuccess?: () => void;
  onBack?: () => void;
}

export const ResetPasswordForm: React.FC<ResetPasswordFormProps> = ({
  token,
  onSuccess,
  onBack,
}) => {
  const [formData, setFormData] = useState({
    password: '',
    confirm_password: '',
  });
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [isLoading, setIsLoading] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);

  useEffect(() => {
    // If no token is provided, try to get it from URL params
    if (!token) {
      const urlParams = new URLSearchParams(window.location.search);
      const urlToken = urlParams.get('token');
      if (!urlToken) {
        setErrors({ general: 'Invalid or missing reset token' });
      }
    }
  }, [token]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Clear errors when user types
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    // Password validation
    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 8) {
      newErrors.password = 'Password must be at least 8 characters long';
    }

    // Confirm password validation
    if (!formData.confirm_password) {
      newErrors.confirm_password = 'Please confirm your password';
    } else if (formData.password !== formData.confirm_password) {
      newErrors.confirm_password = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    const resetToken = token || new URLSearchParams(window.location.search).get('token');
    if (!resetToken) {
      setErrors({ general: 'Invalid or missing reset token' });
      return;
    }

    setIsLoading(true);
    setErrors({});

    try {
      await api.resetPassword({
        token: resetToken,
        password: formData.password,
        confirm_password: formData.confirm_password,
      });
      setIsSuccess(true);
      onSuccess?.();
    } catch (error) {
      if (error instanceof ApiError) {
        if (error.details) {
          setErrors(error.details);
        } else {
          setErrors({ general: error.message });
        }
      } else {
        setErrors({ general: 'An unexpected error occurred' });
      }
    } finally {
      setIsLoading(false);
    }
  };

  if (isSuccess) {
    return (
      <div className="w-full max-w-md mx-auto">
        <div className="bg-white shadow-md rounded-lg px-8 pt-6 pb-8 mb-4">
          <div className="text-center">
            <div className="mb-4">
              <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-green-100">
                <svg
                  className="h-6 w-6 text-green-600"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M5 13l4 4L19 7"
                  />
                </svg>
              </div>
            </div>

            <h2 className="text-2xl font-bold text-gray-900 mb-2">Password Reset Successful</h2>
            <p className="text-gray-600 mb-6">
              Your password has been successfully reset. You can now sign in with your new password.
            </p>

            <Button
              type="button"
              className="w-full"
              onClick={() => window.location.href = '/login'}
            >
              Go to Sign In
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full max-w-md mx-auto">
      <div className="bg-white shadow-md rounded-lg px-8 pt-6 pb-8 mb-4">
        <div className="mb-6 text-center">
          <h2 className="text-2xl font-bold text-gray-900">Set New Password</h2>
          <p className="text-gray-600 mt-2">
            Enter your new password below.
          </p>
        </div>

        {errors.general && (
          <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
            {errors.general}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
              New Password
            </label>
            <input
              className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline ${
                errors.password ? 'border-red-500' : 'border-gray-300'
              }`}
              id="password"
              name="password"
              type="password"
              placeholder="Enter your new password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            {errors.password && <p className="text-red-500 text-xs italic mt-1">{errors.password}</p>}
          </div>

          <div className="mb-6">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="confirm_password">
              Confirm New Password
            </label>
            <input
              className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline ${
                errors.confirm_password ? 'border-red-500' : 'border-gray-300'
              }`}
              id="confirm_password"
              name="confirm_password"
              type="password"
              placeholder="Confirm your new password"
              value={formData.confirm_password}
              onChange={handleChange}
              required
            />
            {errors.confirm_password && <p className="text-red-500 text-xs italic mt-1">{errors.confirm_password}</p>}
          </div>

          <div className="flex flex-col space-y-3">
            <Button
              type="submit"
              className="w-full"
              disabled={isLoading}
            >
              {isLoading ? (
                <div className="flex items-center justify-center">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Resetting Password...
                </div>
              ) : (
                'Reset Password'
              )}
            </Button>

            {onBack && (
              <button
                type="button"
                className="text-blue-600 hover:text-blue-800 text-sm font-medium"
                onClick={onBack}
              >
                Back to Sign In
              </button>
            )}
          </div>
        </form>
      </div>
    </div>
  );
};

export default ResetPasswordForm;

import React, { ReactNode } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useRBAC, UserRole, Permission, Resource, Action } from '../../contexts/RBACContext';

// Base guard props
interface BaseGuardProps {
  children: ReactNode;
  fallback?: ReactNode;
  redirectTo?: string;
  showError?: boolean;
  errorMessage?: string;
}

// Role-based guard props
interface RoleGuardProps extends BaseGuardProps {
  role?: UserRole;
  roles?: UserRole[];
  requireAll?: boolean;
}

// Permission-based guard props
interface PermissionGuardProps extends BaseGuardProps {
  permission?: Permission;
  permissions?: Permission[];
  requireAll?: boolean;
}

// Resource access guard props
interface ResourceGuardProps extends BaseGuardProps {
  resource: Resource;
  action: Action;
}

// Admin guard props
interface AdminGuardProps extends BaseGuardProps {}

// User management guard props
interface UserManagementGuardProps extends BaseGuardProps {
  targetUserId?: number;
}

// Own resource guard props
interface OwnResourceGuardProps extends BaseGuardProps {
  resourceOwnerId: number;
}

// Default error component
const DefaultError: React.FC<{ message?: string }> = ({ message }) => (
  <div className="bg-red-50 border border-red-200 rounded-lg p-4 text-center">
    <div className="text-red-600 font-medium mb-2">Access Denied</div>
    <div className="text-red-500 text-sm">
      {message || 'You don\'t have permission to access this content.'}
    </div>
  </div>
);

// Default loading component
const DefaultLoading: React.FC = () => (
  <div className="flex items-center justify-center p-4">
    <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600"></div>
    <span className="ml-2 text-gray-600">Checking permissions...</span>
  </div>
);

// Base guard component
const BaseGuard: React.FC<{
  condition: boolean;
  loading?: boolean;
  fallback?: ReactNode;
  redirectTo?: string;
  showError?: boolean;
  errorMessage?: string;
  children: ReactNode;
}> = ({ 
  condition, 
  loading = false, 
  fallback, 
  redirectTo, 
  showError = true, 
  errorMessage,
  children 
}) => {
  const { isLoading } = useAuth();

  // Show loading state
  if (isLoading || loading) {
    return <DefaultLoading />;
  }

  // Handle redirect
  if (!condition && redirectTo) {
    window.location.href = redirectTo;
    return <DefaultLoading />;
  }

  // Show content if condition is met
  if (condition) {
    return <>{children}</>;
  }

  // Show fallback or error
  if (fallback) {
    return <>{fallback}</>;
  }

  if (showError) {
    return <DefaultError message={errorMessage} />;
  }

  return null;
};

// Role Guard - Protects content based on user roles
export const RoleGuard: React.FC<RoleGuardProps> = ({
  role,
  roles,
  requireAll = false,
  children,
  ...baseProps
}) => {
  const { hasRole, hasAnyRole } = useRBAC();
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return (
      <BaseGuard
        condition={false}
        errorMessage="Please log in to access this content."
        {...baseProps}
      >
        {children}
      </BaseGuard>
    );
  }

  let condition = false;

  if (role) {
    condition = hasRole(role);
  } else if (roles) {
    if (requireAll) {
      // Check if user has all specified roles (not practical with current role system)
      condition = roles.every(r => hasRole(r));
    } else {
      // Check if user has any of the specified roles
      condition = hasAnyRole(roles);
    }
  }

  return (
    <BaseGuard condition={condition} {...baseProps}>
      {children}
    </BaseGuard>
  );
};

// Permission Guard - Protects content based on permissions
export const PermissionGuard: React.FC<PermissionGuardProps> = ({
  permission,
  permissions,
  requireAll = false,
  children,
  ...baseProps
}) => {
  const { hasPermission, hasAnyPermission, hasAllPermissions } = useRBAC();
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return (
      <BaseGuard
        condition={false}
        errorMessage="Please log in to access this content."
        {...baseProps}
      >
        {children}
      </BaseGuard>
    );
  }

  let condition = false;

  if (permission) {
    condition = hasPermission(permission);
  } else if (permissions) {
    if (requireAll) {
      condition = hasAllPermissions(permissions);
    } else {
      condition = hasAnyPermission(permissions);
    }
  }

  return (
    <BaseGuard condition={condition} {...baseProps}>
      {children}
    </BaseGuard>
  );
};

// Resource Guard - Protects content based on resource access
export const ResourceGuard: React.FC<ResourceGuardProps> = ({
  resource,
  action,
  children,
  ...baseProps
}) => {
  const { canAccessResource } = useRBAC();
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return (
      <BaseGuard
        condition={false}
        errorMessage="Please log in to access this content."
        {...baseProps}
      >
        {children}
      </BaseGuard>
    );
  }

  const condition = canAccessResource(resource, action);

  return (
    <BaseGuard condition={condition} {...baseProps}>
      {children}
    </BaseGuard>
  );
};

// Admin Guard - Protects admin-only content
export const AdminGuard: React.FC<AdminGuardProps> = ({
  children,
  ...baseProps
}) => {
  return (
    <RoleGuard
      role="admin"
      errorMessage="Administrator access required."
      {...baseProps}
    >
      {children}
    </RoleGuard>
  );
};

// User Management Guard - Protects user management content
export const UserManagementGuard: React.FC<UserManagementGuardProps> = ({
  targetUserId,
  children,
  ...baseProps
}) => {
  const { canManageUser } = useRBAC();
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return (
      <BaseGuard
        condition={false}
        errorMessage="Please log in to access this content."
        {...baseProps}
      >
        {children}
      </BaseGuard>
    );
  }

  const condition = canManageUser(targetUserId);

  return (
    <BaseGuard 
      condition={condition}
      errorMessage="You don't have permission to manage users."
      {...baseProps}
    >
      {children}
    </BaseGuard>
  );
};

// Own Resource Guard - Protects content that user owns or has permission to access
export const OwnResourceGuard: React.FC<OwnResourceGuardProps> = ({
  resourceOwnerId,
  children,
  ...baseProps
}) => {
  const { canAccessOwnResource } = useRBAC();
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return (
      <BaseGuard
        condition={false}
        errorMessage="Please log in to access this content."
        {...baseProps}
      >
        {children}
      </BaseGuard>
    );
  }

  const condition = canAccessOwnResource(resourceOwnerId);

  return (
    <BaseGuard 
      condition={condition}
      errorMessage="You don't have permission to access this resource."
      {...baseProps}
    >
      {children}
    </BaseGuard>
  );
};

// Conditional render based on permissions
interface ConditionalRenderProps {
  condition: boolean;
  children: ReactNode;
  fallback?: ReactNode;
}

export const ConditionalRender: React.FC<ConditionalRenderProps> = ({
  condition,
  children,
  fallback = null,
}) => {
  return condition ? <>{children}</> : <>{fallback}</>;
};

// Higher-order component for route protection
export const withRoleGuard = <P extends object>(
  Component: React.ComponentType<P>,
  guardProps: Omit<RoleGuardProps, 'children'>
) => {
  return (props: P) => (
    <RoleGuard {...guardProps}>
      <Component {...props} />
    </RoleGuard>
  );
};

export const withPermissionGuard = <P extends object>(
  Component: React.ComponentType<P>,
  guardProps: Omit<PermissionGuardProps, 'children'>
) => {
  return (props: P) => (
    <PermissionGuard {...guardProps}>
      <Component {...props} />
    </PermissionGuard>
  );
};

export const withAdminGuard = <P extends object>(
  Component: React.ComponentType<P>,
  guardProps?: Omit<AdminGuardProps, 'children'>
) => {
  return (props: P) => (
    <AdminGuard {...guardProps}>
      <Component {...props} />
    </AdminGuard>
  );
};

// Navigation guard hook for programmatic route protection
export const useNavigationGuard = () => {
  const { hasPermission, isAdmin } = useRBAC();
  const { isAuthenticated } = useAuth();

  const canNavigateTo = (route: string): boolean => {
    if (!isAuthenticated) {
      return false;
    }

    // Define route protection rules
    const protectedRoutes: Record<string, () => boolean> = {
      '/admin': () => isAdmin(),
      '/admin/*': () => isAdmin(),
      '/profile/edit': () => isAuthenticated,
      '/users': () => hasPermission('user:read'),
      '/users/*': () => hasPermission('user:read'),
    };

    // Check if route is protected
    for (const [pattern, checkFn] of Object.entries(protectedRoutes)) {
      if (route.match(pattern.replace('*', '.*'))) {
        return checkFn();
      }
    }

    // Allow access to non-protected routes
    return true;
  };

  const redirectIfUnauthorized = (route: string, fallbackRoute: string = '/') => {
    if (!canNavigateTo(route)) {
      window.location.href = fallbackRoute;
      return false;
    }
    return true;
  };

  return {
    canNavigateTo,
    redirectIfUnauthorized,
  };
};

