import React from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useRBAC } from '../../contexts/RBACContext';
import { LoadingSpinner } from '../ui/LoadingSpinner';
import { UnauthorizedError } from '../ui/UnauthorizedError';

interface AdminRouteProps {
  children: React.ReactNode;
  fallbackPath?: string;
  showErrorPage?: boolean;
}

/**
 * AdminRoute component that ensures user has admin role before rendering children
 * Redirects to login if not authenticated
 * Shows unauthorized error or redirects if user doesn't have admin role
 */
export const AdminRoute: React.FC<AdminRouteProps> = ({ 
  children, 
  fallbackPath = '/dashboard',
  showErrorPage = true
}) => {
  const { isAuthenticated, isLoading } = useAuth();
  const { isAdmin } = useRBAC();
  const location = useLocation();

  // Show loading spinner while checking authentication
  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-secondary-50">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  // Redirect to login if not authenticated, preserving the intended location
  if (!isAuthenticated) {
    return (
      <Navigate 
        to="/login" 
        state={{ returnTo: location.pathname + location.search }} 
        replace 
      />
    );
  }

  // Check if user has admin role
  if (!isAdmin()) {
    if (showErrorPage) {
      return (
        <UnauthorizedError
          title="Admin Access Required"
          message="You need administrator privileges to access this page."
          actionText="Go to Dashboard"
          actionPath={fallbackPath}
        />
      );
    } else {
      return <Navigate to={fallbackPath} replace />;
    }
  }

  // User is authenticated and has admin role, render children
  return <>{children}</>;
};

export default AdminRoute;

import React from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { LoadingSpinner } from '../ui/LoadingSpinner';

interface ProtectedRouteProps {
  children: React.ReactNode;
  requireEmailVerification?: boolean;
}

/**
 * ProtectedRoute component that ensures user is authenticated before rendering children
 * Redirects to login with return path if not authenticated
 * Shows loading spinner while authentication status is being checked
 */
export const ProtectedRoute: React.FC<ProtectedRouteProps> = ({ 
  children, 
  requireEmailVerification = false 
}) => {
  const { isAuthenticated, isLoading, isEmailVerified } = useAuth();
  const location = useLocation();

  // Show loading spinner while checking authentication
  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-secondary-50">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  // Redirect to login if not authenticated, preserving the intended location
  if (!isAuthenticated) {
    return (
      <Navigate 
        to="/login" 
        state={{ returnTo: location.pathname + location.search }} 
        replace 
      />
    );
  }

  // Redirect to email verification if required and not verified
  if (requireEmailVerification && !isEmailVerified) {
    return (
      <Navigate 
        to="/verify-email" 
        state={{ returnTo: location.pathname + location.search }} 
        replace 
      />
    );
  }

  // User is authenticated and verified (if required), render children
  return <>{children}</>;
};

export default ProtectedRoute;

import React from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { LoadingSpinner } from '../ui/LoadingSpinner';

interface PublicRouteProps {
  children: React.ReactNode;
  redirectPath?: string;
}

/**
 * PublicRoute component for routes that should only be accessible to unauthenticated users
 * Redirects authenticated users to dashboard or specified path
 * Used for login, register, forgot-password pages
 */
export const PublicRoute: React.FC<PublicRouteProps> = ({ 
  children, 
  redirectPath = '/dashboard' 
}) => {
  const { isAuthenticated, isLoading } = useAuth();
  const location = useLocation();

  // Show loading spinner while checking authentication
  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-secondary-50">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  // If user is authenticated, redirect to intended destination or dashboard
  if (isAuthenticated) {
    // Check if there's a return path from login flow
    const returnTo = location.state?.returnTo || redirectPath;
    return <Navigate to={returnTo} replace />;
  }

  // User is not authenticated, render the public route
  return <>{children}</>;
};

export default PublicRoute;

