import React, { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import { api, User, LoginRequest, RegisterRequest, ApiError } from '../lib/api';

// Types
interface AuthState {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  isEmailVerified: boolean;
}

interface AuthContextType extends AuthState {
  login: (data: LoginRequest) => Promise<void>;
  register: (data: RegisterRequest) => Promise<void>;
  logout: () => Promise<void>;
  logoutAll: () => Promise<void>;
  checkAuth: () => Promise<void>;
  refreshAuth: () => Promise<void>;
}

// Create context
const AuthContext = createContext<AuthContextType | undefined>(undefined);

// Provider props
interface AuthProviderProps {
  children: ReactNode;
}

// Initial state
const initialState: AuthState = {
  user: null,
  isAuthenticated: false,
  isLoading: true,
  isEmailVerified: false,
};

// AuthProvider component
export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [state, setState] = useState<AuthState>(initialState);

  // Login function
  const login = async (data: LoginRequest): Promise<void> => {
    try {
      setState(prev => ({ ...prev, isLoading: true }));

      const response = await api.login(data);

      setState({
        user: response.user,
        isAuthenticated: true,
        isLoading: false,
        isEmailVerified: response.user.email_verified,
      });
    } catch (error) {
      setState(prev => ({ ...prev, isLoading: false }));
      throw error;
    }
  };

  // Register function
  const register = async (data: RegisterRequest): Promise<void> => {
    try {
      setState(prev => ({ ...prev, isLoading: true }));

      const response = await api.register(data);

      setState({
        user: response.user,
        isAuthenticated: true,
        isLoading: false,
        isEmailVerified: response.user.email_verified,
      });
    } catch (error) {
      setState(prev => ({ ...prev, isLoading: false }));
      throw error;
    }
  };

  // Logout function
  const logout = async (): Promise<void> => {
    try {
      await api.logout();
    } catch (error) {
      console.error('Logout error:', error);
      // Continue with local logout even if API call fails
    } finally {
      setState({
        user: null,
        isAuthenticated: false,
        isLoading: false,
        isEmailVerified: false,
      });

      // Clear any stored tokens or user data
      localStorage.removeItem('user');
    }
  };

  // Logout from all devices
  const logoutAll = async (): Promise<void> => {
    try {
      await api.logoutAll();
    } catch (error) {
      console.error('Logout all error:', error);
      // Continue with local logout even if API call fails
    } finally {
      setState({
        user: null,
        isAuthenticated: false,
        isLoading: false,
        isEmailVerified: false,
      });

      // Clear any stored tokens or user data
      localStorage.removeItem('user');
    }
  };

  // Check authentication status
  const checkAuth = async (): Promise<void> => {
    try {
      setState(prev => ({ ...prev, isLoading: true }));

      const response = await api.checkAuth();

      if (response.authenticated && response.user) {
        setState({
          user: response.user,
          isAuthenticated: true,
          isLoading: false,
          isEmailVerified: response.user.email_verified,
        });
      } else {
        setState({
          user: null,
          isAuthenticated: false,
          isLoading: false,
          isEmailVerified: false,
        });
      }
    } catch (error) {
      console.error('Auth check error:', error);
      setState({
        user: null,
        isAuthenticated: false,
        isLoading: false,
        isEmailVerified: false,
      });
    }
  };

  // Refresh user data
  const refreshAuth = async (): Promise<void> => {
    if (!state.isAuthenticated) {
      return;
    }

    try {
      const user = await api.getProfile();
      setState(prev => ({
        ...prev,
        user,
        isEmailVerified: user.email_verified,
      }));
    } catch (error) {
      console.error('Refresh auth error:', error);
      // If we can't get the profile, the user might be logged out
      if (error instanceof ApiError && error.status === 401) {
        setState({
          user: null,
          isAuthenticated: false,
          isLoading: false,
          isEmailVerified: false,
        });
      }
    }
  };

  // Check authentication on mount
  useEffect(() => {
    checkAuth();
  }, []);

  // Provide context value
  const contextValue: AuthContextType = {
    ...state,
    login,
    register,
    logout,
    logoutAll,
    checkAuth,
    refreshAuth,
  };

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
};

// Custom hook to use auth context
export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);

  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }

  return context;
};

// Hook to check if user is authenticated
// Note: This hook is kept for backward compatibility, but ProtectedRoute should be used instead
export const useRequireAuth = (): AuthContextType => {
  const auth = useAuth();

  useEffect(() => {
    if (!auth.isLoading && !auth.isAuthenticated) {
      // This is now handled by ProtectedRoute component
      console.warn('useRequireAuth: User is not authenticated. Consider using ProtectedRoute component instead.');
    }
  }, [auth.isLoading, auth.isAuthenticated]);

  return auth;
};

// Hook to check if email is verified
// Note: This hook is kept for backward compatibility, but route-level verification should be used instead
export const useRequireEmailVerification = (): AuthContextType => {
  const auth = useAuth();

  useEffect(() => {
    if (!auth.isLoading && auth.isAuthenticated && !auth.isEmailVerified) {
      // This is now handled by ProtectedRoute component with requireEmailVerification prop
      console.warn('useRequireEmailVerification: Email not verified. Consider using ProtectedRoute with requireEmailVerification prop.');
    }
  }, [auth.isLoading, auth.isAuthenticated, auth.isEmailVerified]);

  return auth;
};

export default AuthContext;

import React, { createContext, useContext, ReactNode } from 'react';
import { useAuth } from './AuthContext';

// Types
export type Permission = string;
export type Resource = string;
export type Action = string;
export type UserRole = 'user' | 'admin';

// System resources
export const RESOURCES = {
  USER: 'user',
  ADMIN: 'admin', 
  PROFILE: 'profile',
  AUTH: 'auth',
  AUDIT: 'audit',
  SYSTEM: 'system',
} as const;

// Actions
export const ACTIONS = {
  READ: 'read',
  WRITE: 'write',
  CREATE: 'create',
  UPDATE: 'update',
  DELETE: 'delete',
  MANAGE: 'manage',
} as const;

// Permissions
export const PERMISSIONS = {
  // User permissions
  USER_READ: 'user:read',
  USER_WRITE: 'user:write',
  USER_CREATE: 'user:create',
  USER_UPDATE: 'user:update',
  USER_DELETE: 'user:delete',
  USER_MANAGE: 'user:manage',

  // Profile permissions (own profile)
  PROFILE_READ: 'profile:read',
  PROFILE_UPDATE: 'profile:update',

  // Admin permissions
  ADMIN_READ: 'admin:read',
  ADMIN_WRITE: 'admin:write',
  ADMIN_MANAGE: 'admin:manage',

  // Auth permissions
  AUTH_READ: 'auth:read',
  AUTH_WRITE: 'auth:write',
  AUTH_MANAGE: 'auth:manage',

  // Audit permissions
  AUDIT_READ: 'audit:read',
  AUDIT_WRITE: 'audit:write',
  AUDIT_MANAGE: 'audit:manage',

  // System permissions
  SYSTEM_READ: 'system:read',
  SYSTEM_WRITE: 'system:write',
  SYSTEM_MANAGE: 'system:manage',
} as const;

// Role permissions mapping
const ROLE_PERMISSIONS: Record<UserRole, Permission[]> = {
  user: [
    PERMISSIONS.PROFILE_READ,
    PERMISSIONS.PROFILE_UPDATE,
    PERMISSIONS.AUTH_READ,
    PERMISSIONS.AUTH_WRITE,
  ],
  admin: [
    // All user permissions
    PERMISSIONS.PROFILE_READ,
    PERMISSIONS.PROFILE_UPDATE,
    PERMISSIONS.AUTH_READ,
    PERMISSIONS.AUTH_WRITE,
    // Plus admin-specific permissions
    PERMISSIONS.USER_READ,
    PERMISSIONS.USER_WRITE,
    PERMISSIONS.USER_CREATE,
    PERMISSIONS.USER_UPDATE,
    PERMISSIONS.USER_DELETE,
    PERMISSIONS.USER_MANAGE,
    PERMISSIONS.ADMIN_READ,
    PERMISSIONS.ADMIN_WRITE,
    PERMISSIONS.ADMIN_MANAGE,
    PERMISSIONS.AUDIT_READ,
    PERMISSIONS.AUDIT_WRITE,
    PERMISSIONS.SYSTEM_READ,
  ],
};

// RBAC Context Interface
interface RBACContextType {
  // Permission checking
  hasPermission: (permission: Permission) => boolean;
  hasAnyPermission: (permissions: Permission[]) => boolean;
  hasAllPermissions: (permissions: Permission[]) => boolean;
  canAccessResource: (resource: Resource, action: Action) => boolean;

  // Role checking
  hasRole: (role: UserRole) => boolean;
  hasAnyRole: (roles: UserRole[]) => boolean;
  isAdmin: () => boolean;
  isUser: () => boolean;

  // User management
  canManageUser: (targetUserId?: number) => boolean;
  canAccessOwnResource: (resourceOwnerId: number) => boolean;

  // Utility functions
  getUserRole: () => UserRole | null;
  getUserPermissions: () => Permission[];
  buildPermission: (resource: Resource, action: Action) => Permission;
}

// Create context
const RBACContext = createContext<RBACContextType | undefined>(undefined);

// Provider props
interface RBACProviderProps {
  children: ReactNode;
}

// RBAC Provider component
export const RBACProvider: React.FC<RBACProviderProps> = ({ children }) => {
  const { user, isAuthenticated } = useAuth();

  // Get current user role
  const getUserRole = (): UserRole | null => {
    if (!isAuthenticated || !user) {
      return null;
    }
    return user.role;
  };

  // Get user permissions based on role
  const getUserPermissions = (): Permission[] => {
    const role = getUserRole();
    if (!role) {
      return [];
    }
    return ROLE_PERMISSIONS[role] || [];
  };

  // Build permission string from resource and action
  const buildPermission = (resource: Resource, action: Action): Permission => {
    return `${resource}:${action}`;
  };

  // Check if user has a specific permission
  const hasPermission = (permission: Permission): boolean => {
    const permissions = getUserPermissions();
    return permissions.includes(permission);
  };

  // Check if user has any of the specified permissions
  const hasAnyPermission = (permissions: Permission[]): boolean => {
    const userPermissions = getUserPermissions();
    return permissions.some(permission => userPermissions.includes(permission));
  };

  // Check if user has all of the specified permissions
  const hasAllPermissions = (permissions: Permission[]): boolean => {
    const userPermissions = getUserPermissions();
    return permissions.every(permission => userPermissions.includes(permission));
  };

  // Check if user can access a resource with a specific action
  const canAccessResource = (resource: Resource, action: Action): boolean => {
    const permission = buildPermission(resource, action);
    return hasPermission(permission);
  };

  // Check if user has a specific role
  const hasRole = (role: UserRole): boolean => {
    const userRole = getUserRole();
    return userRole === role;
  };

  // Check if user has any of the specified roles
  const hasAnyRole = (roles: UserRole[]): boolean => {
    const userRole = getUserRole();
    return userRole ? roles.includes(userRole) : false;
  };

  // Check if user is admin
  const isAdmin = (): boolean => {
    return hasRole('admin');
  };

  // Check if user is regular user
  const isUser = (): boolean => {
    return hasRole('user');
  };

  // Check if user can manage another user
  const canManageUser = (targetUserId?: number): boolean => {
    // Must have user management permission
    if (!hasPermission(PERMISSIONS.USER_MANAGE)) {
      return false;
    }

    // Cannot manage yourself through admin interface
    if (targetUserId && user && user.id === targetUserId) {
      return false;
    }

    return true;
  };

  // Check if user can access their own resource or has permission
  const canAccessOwnResource = (resourceOwnerId: number): boolean => {
    // Allow if accessing own resource
    if (user && user.id === resourceOwnerId) {
      return true;
    }

    // Otherwise, need appropriate admin permissions
    return hasPermission(PERMISSIONS.USER_READ);
  };

  // Context value
  const contextValue: RBACContextType = {
    hasPermission,
    hasAnyPermission,
    hasAllPermissions,
    canAccessResource,
    hasRole,
    hasAnyRole,
    isAdmin,
    isUser,
    canManageUser,
    canAccessOwnResource,
    getUserRole,
    getUserPermissions,
    buildPermission,
  };

  return (
    <RBACContext.Provider value={contextValue}>
      {children}
    </RBACContext.Provider>
  );
};

// Custom hook to use RBAC context
export const useRBAC = (): RBACContextType => {
  const context = useContext(RBACContext);

  if (context === undefined) {
    throw new Error('useRBAC must be used within an RBACProvider');
  }

  return context;
};

// Custom hooks for common checks

// Hook to check if user has a specific permission
export const usePermission = (permission: Permission): boolean => {
  const { hasPermission } = useRBAC();
  return hasPermission(permission);
};

// Hook to check if user has any of the specified permissions
export const useAnyPermission = (permissions: Permission[]): boolean => {
  const { hasAnyPermission } = useRBAC();
  return hasAnyPermission(permissions);
};

// Hook to check if user has all of the specified permissions
export const useAllPermissions = (permissions: Permission[]): boolean => {
  const { hasAllPermissions } = useRBAC();
  return hasAllPermissions(permissions);
};

// Hook to check if user can access a resource
export const useResourceAccess = (resource: Resource, action: Action): boolean => {
  const { canAccessResource } = useRBAC();
  return canAccessResource(resource, action);
};

// Hook to check if user has a specific role
export const useRole = (role: UserRole): boolean => {
  const { hasRole } = useRBAC();
  return hasRole(role);
};

// Hook to check if user is admin
export const useIsAdmin = (): boolean => {
  const { isAdmin } = useRBAC();
  return isAdmin();
};

// Hook to check if user is regular user
export const useIsUser = (): boolean => {
  const { isUser } = useRBAC();
  return isUser();
};

// Hook to require specific permission (throws error if not authorized)
export const useRequirePermission = (permission: Permission): void => {
  const { hasPermission } = useRBAC();
  const { isAuthenticated } = useAuth();

  React.useEffect(() => {
    if (!isAuthenticated) {
      throw new Error('Authentication required');
    }

    if (!hasPermission(permission)) {
      throw new Error(`Permission required: ${permission}`);
    }
  }, [isAuthenticated, hasPermission, permission]);
};

// Hook to require admin role
export const useRequireAdmin = (): void => {
  const { isAdmin } = useRBAC();
  const { isAuthenticated } = useAuth();

  React.useEffect(() => {
    if (!isAuthenticated) {
      throw new Error('Authentication required');
    }

    if (!isAdmin()) {
      throw new Error('Admin role required');
    }
  }, [isAuthenticated, isAdmin]);
};

export default RBACContext;

