	Warnings  []string `json:"warnings,omitempty"`
	RiskLevel string   `json:"risk_level"` // "low", "medium", "high", "critical"
	RequiresSecondaryAuth bool `json:"requires_secondary_auth"`
	AuditFlags []string `json:"audit_flags,omitempty"`
}

// ValidateRoleChange performs comprehensive security validation for role changes
func ValidateRoleChange(check *RoleChangeSecurityCheck) *SecurityValidationResult {
	result := &SecurityValidationResult{
		Valid:     true,
		Errors:    []string{},
		Warnings:  []string{},
		RiskLevel: "low",
		AuditFlags: []string{},
	}

	// 1. Prevent self-role escalation
	if check.AdminID == check.TargetID {
		result.Valid = false
		result.Errors = append(result.Errors, "administrators cannot modify their own role")
		result.RiskLevel = "critical"
		result.AuditFlags = append(result.AuditFlags, "self_role_modification_attempt")
	}

	// 2. Validate admin has permission to change roles
	if !HasPermission(check.AdminRole, PermissionUserManage) {
		result.Valid = false
		result.Errors = append(result.Errors, "insufficient permissions to modify user roles")
		result.RiskLevel = "high"
		result.AuditFlags = append(result.AuditFlags, "unauthorized_role_change_attempt")
	}

	// 3. Validate role transition is allowed
	if !isValidRoleTransition(check.TargetRole, check.NewRole) {
		result.Valid = false
		result.Errors = append(result.Errors, fmt.Sprintf("invalid role transition from %s to %s", check.TargetRole, check.NewRole))
		result.RiskLevel = "high"
		result.AuditFlags = append(result.AuditFlags, "invalid_role_transition")
	}

	// 4. Check for privilege escalation patterns
	if isPrivilegeEscalation(check.TargetRole, check.NewRole) {
		result.RiskLevel = "high"
		result.RequiresSecondaryAuth = true
		result.Warnings = append(result.Warnings, "role change involves privilege escalation")
		result.AuditFlags = append(result.AuditFlags, "privilege_escalation")
	}

	// 5. Validate reason is provided and meaningful
	if strings.TrimSpace(check.Reason) == "" {
		result.Valid = false
		result.Errors = append(result.Errors, "reason for role change is required")
	} else if len(strings.TrimSpace(check.Reason)) < 10 {
		result.Warnings = append(result.Warnings, "reason for role change is very brief")
		result.AuditFlags = append(result.AuditFlags, "brief_reason")
	}

	// 6. Check for suspicious patterns in reason
	suspiciousPatterns := []string{"test", "temp", "temporary", "quick", "urgent"}
	reasonLower := strings.ToLower(check.Reason)
	for _, pattern := range suspiciousPatterns {
		if strings.Contains(reasonLower, pattern) {
			result.Warnings = append(result.Warnings, fmt.Sprintf("reason contains potentially suspicious term: %s", pattern))
			result.AuditFlags = append(result.AuditFlags, "suspicious_reason")
			if result.RiskLevel == "low" {
				result.RiskLevel = "medium"
			}
			break
		}
	}

	// 7. Validate IP address and user agent
	if check.IPAddress == "" {
		result.Warnings = append(result.Warnings, "IP address not recorded")
		result.AuditFlags = append(result.AuditFlags, "missing_ip")
	}

	if check.UserAgent == "" {
		result.Warnings = append(result.Warnings, "user agent not recorded")
		result.AuditFlags = append(result.AuditFlags, "missing_user_agent")
	}

	// 8. Additional security checks for admin role assignments
	if check.NewRole == RoleAdmin {
		result.RiskLevel = "high"
		result.RequiresSecondaryAuth = true
		result.AuditFlags = append(result.AuditFlags, "admin_role_assignment")

		// Extra validation for admin assignments
		if !strings.Contains(strings.ToLower(check.Reason), "admin") && 
		   !strings.Contains(strings.ToLower(check.Reason), "administrator") {
			result.Warnings = append(result.Warnings, "admin role assignment without explicit admin-related reason")
		}
	}

	return result
}

// isValidRoleTransition checks if a role transition is allowed
func isValidRoleTransition(from, to UserRole) bool {
	// Define allowed transitions
	allowedTransitions := map[UserRole][]UserRole{
		RoleUser:  {RoleAdmin},
		RoleAdmin: {RoleUser},
	}

	validTransitions, exists := allowedTransitions[from]
	if !exists {
		return false
	}

	for _, validTo := range validTransitions {
		if to == validTo {
			return true
		}
	}

	return false
}

// isPrivilegeEscalation checks if the role change is a privilege escalation
func isPrivilegeEscalation(from, to UserRole) bool {
	// Define role hierarchy (higher number = more privileges)
	roleHierarchy := map[UserRole]int{
		RoleUser:  1,
		RoleAdmin: 2,
	}

	fromLevel, fromExists := roleHierarchy[from]
	toLevel, toExists := roleHierarchy[to]

	if !fromExists || !toExists {
		return true // Unknown role is considered escalation
	}

	return toLevel > fromLevel
}

// RoleChangeAuditEntry represents a comprehensive audit entry for role changes
type RoleChangeAuditEntry struct {
	ID                    uint                      `json:"id"`
	AdminID               uint                      `json:"admin_id"`
	AdminEmail            string                    `json:"admin_email"`
	AdminRole             UserRole                  `json:"admin_role"`
	TargetID              uint                      `json:"target_id"`
	TargetEmail           string                    `json:"target_email"`
	PreviousRole          UserRole                  `json:"previous_role"`
	NewRole               UserRole                  `json:"new_role"`
	Reason                string                    `json:"reason"`
	ValidationResult      *SecurityValidationResult `json:"validation_result"`
	IPAddress             string                    `json:"ip_address"`
	UserAgent             string                    `json:"user_agent"`
	RequestSource         string                    `json:"request_source"`
	SessionID             string                    `json:"session_id,omitempty"`
	SecondaryAuthRequired bool                      `json:"secondary_auth_required"`
	SecondaryAuthPassed   bool                      `json:"secondary_auth_passed,omitempty"`
	Status                string                    `json:"status"` // "pending", "approved", "rejected", "completed"
	CreatedAt             time.Time                 `json:"created_at"`
	CompletedAt           *time.Time                `json:"completed_at,omitempty"`
	Notes                 string                    `json:"notes,omitempty"`
}

// CreateRoleChangeAuditEntry creates a comprehensive audit entry
func CreateRoleChangeAuditEntry(
	adminUser *User,
	targetUser *User,
	newRole UserRole,
	reason string,
	ipAddress, userAgent, requestSource string,
	validationResult *SecurityValidationResult,
) *RoleChangeAuditEntry {
	return &RoleChangeAuditEntry{
		AdminID:               adminUser.ID,
		AdminEmail:            adminUser.Email,
		AdminRole:             adminUser.Role,
		TargetID:              targetUser.ID,
		TargetEmail:           targetUser.Email,
		PreviousRole:          targetUser.Role,
		NewRole:               newRole,
		Reason:                reason,
		ValidationResult:      validationResult,
		IPAddress:             ipAddress,
		UserAgent:             userAgent,
		RequestSource:         requestSource,
		SecondaryAuthRequired: validationResult.RequiresSecondaryAuth,
		Status:                determineInitialStatus(validationResult),
		CreatedAt:             time.Now(),
	}
}

// determineInitialStatus determines the initial status based on validation
func determineInitialStatus(result *SecurityValidationResult) string {
	if !result.Valid {
		return "rejected"
	}
	if result.RequiresSecondaryAuth {
		return "pending"
	}
	return "approved"
}

// Role change monitoring and alerting

// RoleChangeMonitor handles monitoring and alerting for role changes
type RoleChangeMonitor struct {
	AlertThresholds AlertThresholds `json:"alert_thresholds"`
}

// AlertThresholds defines thresholds for triggering alerts
type AlertThresholds struct {
	AdminRoleAssignmentsPerHour int           `json:"admin_role_assignments_per_hour"`
	RoleChangesPerAdmin         int           `json:"role_changes_per_admin"`
	TimeWindow                  time.Duration `json:"time_window"`
	HighRiskActionsPerDay       int           `json:"high_risk_actions_per_day"`
}

// DefaultAlertThresholds returns default alert thresholds
func DefaultAlertThresholds() AlertThresholds {
	return AlertThresholds{
		AdminRoleAssignmentsPerHour: 5,
		RoleChangesPerAdmin:         10,
		TimeWindow:                  24 * time.Hour,
		HighRiskActionsPerDay:       3,
	}
}

// SecurityAlert represents a security alert
type SecurityAlert struct {
	ID          string                 `json:"id"`
	Type        string                 `json:"type"`
	Severity    string                 `json:"severity"` // "low", "medium", "high", "critical"
	Title       string                 `json:"title"`
	Description string                 `json:"description"`
	AdminID     uint                   `json:"admin_id"`
	AdminEmail  string                 `json:"admin_email"`
	Data        map[string]interface{} `json:"data"`
	CreatedAt   time.Time              `json:"created_at"`
	Resolved    bool                   `json:"resolved"`
	ResolvedAt  *time.Time             `json:"resolved_at,omitempty"`
	Notes       string                 `json:"notes,omitempty"`
}

// GenerateSecurityAlert creates a security alert for suspicious activity
func GenerateSecurityAlert(alertType, severity, title, description string, adminUser *User, data map[string]interface{}) *SecurityAlert {
	return &SecurityAlert{
		ID:          fmt.Sprintf("%d_%s_%d", adminUser.ID, alertType, time.Now().Unix()),
		Type:        alertType,
		Severity:    severity,
		Title:       title,
		Description: description,
		AdminID:     adminUser.ID,
		AdminEmail:  adminUser.Email,
		Data:        data,
		CreatedAt:   time.Now(),
		Resolved:    false,
	}
}

// Security compliance helpers

// ComplianceRequirement represents a compliance requirement for role changes
type ComplianceRequirement struct {
	Name        string `json:"name"`
	Description string `json:"description"`
	Required    bool   `json:"required"`
	Met         bool   `json:"met"`
	Details     string `json:"details,omitempty"`
}

// CheckComplianceRequirements checks compliance requirements for role changes
func CheckComplianceRequirements(entry *RoleChangeAuditEntry) []ComplianceRequirement {
	requirements := []ComplianceRequirement{
		{
			Name:        "Audit Trail",
			Description: "Complete audit trail of role change",
			Required:    true,
			Met:         true, // Always met if entry exists
		},
		{
			Name:        "Justification",
			Description: "Valid business justification provided",
			Required:    true,
			Met:         len(strings.TrimSpace(entry.Reason)) >= 10,
			Details:     "Reason must be at least 10 characters",
		},
		{
			Name:        "Administrator Authentication",
			Description: "Administrator properly authenticated",
			Required:    true,
			Met:         entry.AdminID > 0,
		},
		{
			Name:        "IP Address Logging",
			Description: "Source IP address recorded",
			Required:    true,
			Met:         entry.IPAddress != "",
		},
		{
			Name:        "Secondary Authentication",
			Description: "Secondary authentication for high-risk changes",
			Required:    entry.SecondaryAuthRequired,
			Met:         !entry.SecondaryAuthRequired || entry.SecondaryAuthPassed,
			Details:     "Required for privilege escalation",
		},
	}

	return requirements
}

// GenerateComplianceReport generates a compliance report for role changes
func GenerateComplianceReport(entries []*RoleChangeAuditEntry) map[string]interface{} {
	report := map[string]interface{}{
		"total_role_changes":    len(entries),
		"compliance_summary":    map[string]int{},
		"failed_requirements":   []string{},
		"high_risk_changes":     0,
		"privileged_escalations": 0,
		"generated_at":          time.Now(),
	}

	for _, entry := range entries {
		requirements := CheckComplianceRequirements(entry)

		if entry.ValidationResult != nil && entry.ValidationResult.RiskLevel == "high" {
			report["high_risk_changes"] = report["high_risk_changes"].(int) + 1
		}

		if isPrivilegeEscalation(entry.PreviousRole, entry.NewRole) {
			report["privileged_escalations"] = report["privileged_escalations"].(int) + 1
		}

		for _, req := range requirements {
			if req.Required && !req.Met {
				report["failed_requirements"] = append(
					report["failed_requirements"].([]string),
					fmt.Sprintf("%s: %s", entry.TargetEmail, req.Name),
				)
			}
		}
	}

	return report
}

package domain

import (
	"database/sql/driver"
	"encoding/json"
	"errors"
	"time"

	"github.com/golang-jwt/jwt/v5"
	"gorm.io/gorm"
)

// UserRole represents the role of a user
type UserRole string

const (
	RoleUser  UserRole = "user"
	RoleAdmin UserRole = "admin"
)

// UserStatus represents the status of a user
type UserStatus string

const (
	StatusActive    UserStatus = "active"
	StatusInactive  UserStatus = "inactive"
	StatusSuspended UserStatus = "suspended"
)

// UserPreferences represents user preferences stored as JSONB
type UserPreferences struct {
	Theme         string            `json:"theme,omitempty"`           // "light", "dark", "system"
	Language      string            `json:"language,omitempty"`        // "en", "es", etc.
	Timezone      string            `json:"timezone,omitempty"`        // "UTC", "America/New_York", etc.
	Notifications NotificationPrefs `json:"notifications,omitempty"`   // notification preferences
	Privacy       PrivacyPrefs      `json:"privacy,omitempty"`         // privacy preferences
	Custom        map[string]any    `json:"custom,omitempty"`          // custom application-specific preferences
}

// Value implements the driver.Valuer interface for database storage
func (p UserPreferences) Value() (driver.Value, error) {
	return json.Marshal(p)
}

// Scan implements the sql.Scanner interface for database retrieval
func (p *UserPreferences) Scan(value interface{}) error {
	if value == nil {
		*p = UserPreferences{}
		return nil
	}

	var bytes []byte
	switch v := value.(type) {
	case []byte:
		bytes = v
	case string:
		bytes = []byte(v)
	default:
		return errors.New("cannot scan UserPreferences from non-string/[]byte type")
	}

	if len(bytes) == 0 {
		*p = UserPreferences{}
		return nil
	}

	return json.Unmarshal(bytes, p)
}

// NotificationPrefs represents notification preferences
type NotificationPrefs struct {
	Email bool `json:"email"`
	SMS   bool `json:"sms"`
	Push  bool `json:"push"`
}

// PrivacyPrefs represents privacy preferences
type PrivacyPrefs struct {
	ProfileVisible bool `json:"profile_visible"`
	ShowEmail      bool `json:"show_email"`
}

// User represents a user in the system
type User struct {
	ID               uint             `json:"id" gorm:"primarykey"`
	Email            string           `json:"email" gorm:"uniqueIndex;not null"`
	PasswordHash     string           `json:"-" gorm:"not null"`
	FirstName        string           `json:"first_name" gorm:"not null"`
	LastName         string           `json:"last_name" gorm:"not null"`
	EmailVerified    bool             `json:"email_verified" gorm:"default:false"`
	EmailVerifyToken string           `json:"-" gorm:"index"`
	Role             UserRole         `json:"role" gorm:"default:'user';not null"`
	Status           UserStatus       `json:"status" gorm:"default:'active';not null"`
	Preferences      UserPreferences  `json:"preferences" gorm:"type:jsonb;default:'{}'"`
	Avatar           string           `json:"avatar"`                                              // URL to avatar image
	LastLoginAt      *time.Time       `json:"last_login_at"`
	CreatedAt        time.Time        `json:"created_at"`
	UpdatedAt        time.Time        `json:"updated_at"`
	DeletedAt        gorm.DeletedAt   `json:"-" gorm:"index"`

	// Relationships
	RefreshTokens []RefreshToken `json:"-" gorm:"foreignKey:UserID;constraint:OnDelete:CASCADE"`
}

// IsActive checks if the user is active
func (u *User) IsActive() bool {
	return u.Status == StatusActive
}

// IsAdmin checks if the user has admin role
func (u *User) IsAdmin() bool {
	return u.Role == RoleAdmin
}

// UserResponse represents the user data returned to the client
type UserResponse struct {
	ID            uint            `json:"id"`
	Email         string          `json:"email"`
	FirstName     string          `json:"first_name"`
	LastName      string          `json:"last_name"`
	EmailVerified bool            `json:"email_verified"`
	Role          UserRole        `json:"role"`
	Status        UserStatus      `json:"status"`
	Preferences   UserPreferences `json:"preferences"`
	Avatar        string          `json:"avatar,omitempty"`
	LastLoginAt   *time.Time      `json:"last_login_at"`
	CreatedAt     time.Time       `json:"created_at"`
	UpdatedAt     time.Time       `json:"updated_at"`
}

// ToResponse converts User to UserResponse
func (u *User) ToResponse() *UserResponse {
	return &UserResponse{
		ID:            u.ID,
		Email:         u.Email,
		FirstName:     u.FirstName,
		LastName:      u.LastName,
		EmailVerified: u.EmailVerified,
		Role:          u.Role,
		Status:        u.Status,
		Preferences:   u.Preferences,
		Avatar:        u.Avatar,
		LastLoginAt:   u.LastLoginAt,
		CreatedAt:     u.CreatedAt,
		UpdatedAt:     u.UpdatedAt,
	}
}

// RefreshToken represents a refresh token for JWT authentication
type RefreshToken struct {
	ID        uint           `json:"id" gorm:"primarykey"`
	UserID    uint           `json:"user_id" gorm:"not null;index"`
	Token     string         `json:"-" gorm:"uniqueIndex;not null"`
	ExpiresAt time.Time      `json:"expires_at" gorm:"not null"`
	CreatedAt time.Time      `json:"created_at"`
	UpdatedAt time.Time      `json:"updated_at"`
	DeletedAt gorm.DeletedAt `json:"-" gorm:"index"`

	// Relationships
	User User `json:"-" gorm:"foreignKey:UserID;constraint:OnDelete:CASCADE"`
}

// IsExpired checks if the refresh token is expired
func (rt *RefreshToken) IsExpired() bool {
	return time.Now().After(rt.ExpiresAt)
}

// PasswordReset represents a password reset request
type PasswordReset struct {
	ID        uint           `json:"id" gorm:"primarykey"`
	Email     string         `json:"email" gorm:"not null;index"`
	Token     string         `json:"token" gorm:"uniqueIndex;not null"`
	ExpiresAt time.Time      `json:"expires_at" gorm:"not null"`
	Used      bool           `json:"used" gorm:"default:false"`
	CreatedAt time.Time      `json:"created_at"`
	UpdatedAt time.Time      `json:"updated_at"`
	DeletedAt gorm.DeletedAt `json:"-" gorm:"index"`
}

// IsExpired checks if the password reset token is expired
func (pr *PasswordReset) IsExpired() bool {
	return time.Now().After(pr.ExpiresAt)
}

// AuditAction represents the type of audit action
type AuditAction string

const (
	AuditActionUserCreated         AuditAction = "user_created"
	AuditActionUserUpdated         AuditAction = "user_updated"
	AuditActionUserDeleted         AuditAction = "user_deleted"
	AuditActionUserStatusChanged   AuditAction = "user_status_changed"
	AuditActionUserRoleChanged     AuditAction = "user_role_changed"
	AuditActionPasswordChanged     AuditAction = "password_changed"
	AuditActionEmailVerified       AuditAction = "email_verified"
	AuditActionLoginSuccess        AuditAction = "login_success"
	AuditActionLoginFailed         AuditAction = "login_failed"
	AuditActionLogout              AuditAction = "logout"
	AuditActionPasswordResetReq    AuditAction = "password_reset_requested"
	AuditActionPasswordResetUsed   AuditAction = "password_reset_used"
	AuditActionPreferencesUpdated  AuditAction = "preferences_updated"
)

// AuditLevel represents the severity level of the audit event
type AuditLevel string

const (
	AuditLevelInfo    AuditLevel = "info"
	AuditLevelWarning AuditLevel = "warning"
	AuditLevelError   AuditLevel = "error"
)

// AuditLog represents an audit log entry for tracking system events
type AuditLog struct {
	ID          uint                   `json:"id" gorm:"primarykey"`
	UserID      *uint                  `json:"user_id" gorm:"index"`                    // User who performed the action (nil for system actions)
	TargetID    *uint                  `json:"target_id" gorm:"index"`                  // User who was affected by the action (nil if not applicable)
	Action      AuditAction            `json:"action" gorm:"not null;index"`
	Level       AuditLevel             `json:"level" gorm:"default:'info';not null"`
	Resource    string                 `json:"resource" gorm:"not null"`                // e.g., "user", "admin", "auth"
	Description string                 `json:"description" gorm:"not null"`
	IPAddress   string                 `json:"ip_address"`
	UserAgent   string                 `json:"user_agent"`
	Metadata    map[string]interface{} `json:"metadata" gorm:"type:jsonb;default:'{}'"`  // Additional structured data
	CreatedAt   time.Time              `json:"created_at"`

	// Relationships
	User   *User `json:"user,omitempty" gorm:"foreignKey:UserID"`
	Target *User `json:"target,omitempty" gorm:"foreignKey:TargetID"`
}

// Authentication DTOs

// RegisterRequest represents a user registration request
type RegisterRequest struct {
	Email     string `json:"email" binding:"required,email"`
	Password  string `json:"password" binding:"required,min=8"`
	FirstName string `json:"first_name" binding:"required,min=1"`
	LastName  string `json:"last_name" binding:"required,min=1"`
}

// LoginRequest represents a user login request
type LoginRequest struct {
	Email    string `json:"email" binding:"required,email"`
	Password string `json:"password" binding:"required"`
}

// RefreshTokenRequest represents a token refresh request
type RefreshTokenRequest struct {
	RefreshToken string `json:"refresh_token" binding:"required"`
}

// ForgotPasswordRequest represents a forgot password request
type ForgotPasswordRequest struct {
	Email string `json:"email" binding:"required,email"`
}

// ResetPasswordRequest represents a password reset request
type ResetPasswordRequest struct {
	Token           string `json:"token" binding:"required"`
	Password        string `json:"password" binding:"required,min=8"`
	ConfirmPassword string `json:"confirm_password" binding:"required"`
}

// ChangePasswordRequest represents a password change request
type ChangePasswordRequest struct {
	CurrentPassword string `json:"current_password" binding:"required"`
	NewPassword     string `json:"new_password" binding:"required,min=8"`
	ConfirmPassword string `json:"confirm_password" binding:"required"`
}

// EmailVerificationRequest represents an email verification request
type EmailVerificationRequest struct {
	Token string `json:"token" binding:"required"`
}

// AuthResponse represents the response after successful authentication
type AuthResponse struct {
	User         *UserResponse `json:"user"`
	AccessToken  string        `json:"access_token"`
	RefreshToken string        `json:"refresh_token"`
	ExpiresIn    int64         `json:"expires_in"` // seconds
}

// MessageResponse represents a simple message response
type MessageResponse struct {
	Message string `json:"message"`
}

// ErrorResponse represents an error response
type ErrorResponse struct {
	Error   string            `json:"error"`
	Details map[string]string `json:"details,omitempty"`
}

// JWT Claims

// JWTClaims represents the claims in a JWT token
// Implements jwt.Claims interface
type JWTClaims struct {
	UserID    uint     `json:"user_id"`
	Email     string   `json:"email"`
	Role      UserRole `json:"role"`        // User role for authorization
	TokenType string   `json:"token_type"`  // "access" or "refresh"
	jwt.RegisteredClaims
}

// Valid validates the JWT claims
func (c *JWTClaims) Valid() error {
	// Check expiration using the new jwt library
	now := time.Now()
	if c.ExpiresAt != nil && now.After(c.ExpiresAt.Time) {
		return ErrTokenExpired
	}
	return nil
}

package repository

import (
	"time"

	"gorm.io/gorm"

	"github.com/acheevo/fullstack-template/internal/auth/domain"
)

// PasswordResetRepository handles database operations for password reset tokens
type PasswordResetRepository struct {
	db *gorm.DB
}

// NewPasswordResetRepository creates a new password reset repository
func NewPasswordResetRepository(db *gorm.DB) *PasswordResetRepository {
	return &PasswordResetRepository{
		db: db,
	}
}

// Create creates a new password reset token
func (r *PasswordResetRepository) Create(reset *domain.PasswordReset) error {
	return r.db.Create(reset).Error
}

// GetByToken gets a password reset by token
func (r *PasswordResetRepository) GetByToken(token string) (*domain.PasswordReset, error) {
	var reset domain.PasswordReset
	err := r.db.Where("token = ? AND used = false AND expires_at > ?", token, time.Now()).First(&reset).Error
	if err != nil {
		if err == gorm.ErrRecordNotFound {
			return nil, domain.ErrTokenNotFound
		}
		return nil, err
	}
	return &reset, nil
}

// GetByEmail gets all password reset tokens for an email
func (r *PasswordResetRepository) GetByEmail(email string) ([]*domain.PasswordReset, error) {
	var resets []*domain.PasswordReset
	err := r.db.Where("email = ?", email).Find(&resets).Error
	return resets, err
}

// MarkAsUsed marks a password reset token as used
func (r *PasswordResetRepository) MarkAsUsed(token string) error {
	return r.db.Model(&domain.PasswordReset{}).
		Where("token = ?", token).
		Update("used", true).Error
}

// Delete deletes a password reset token
func (r *PasswordResetRepository) Delete(token string) error {
	return r.db.Where("token = ?", token).Delete(&domain.PasswordReset{}).Error
}

// DeleteByEmail deletes all password reset tokens for an email
func (r *PasswordResetRepository) DeleteByEmail(email string) error {
	return r.db.Where("email = ?", email).Delete(&domain.PasswordReset{}).Error
}

// DeleteExpired deletes all expired password reset tokens
func (r *PasswordResetRepository) DeleteExpired() error {
	return r.db.Where("expires_at < ?", time.Now()).Delete(&domain.PasswordReset{}).Error
}

// DeleteUsed deletes all used password reset tokens
func (r *PasswordResetRepository) DeleteUsed() error {
	return r.db.Where("used = true").Delete(&domain.PasswordReset{}).Error
}

// Update updates a password reset token
func (r *PasswordResetRepository) Update(reset *domain.PasswordReset) error {
	return r.db.Save(reset).Error
}

// GetValidTokensCount returns the count of valid (unused and not expired) tokens for an email
func (r *PasswordResetRepository) GetValidTokensCount(email string) (int64, error) {
	var count int64
	err := r.db.Model(&domain.PasswordReset{}).
		Where("email = ? AND used = false AND expires_at > ?", email, time.Now()).
		Count(&count).Error
	return count, err
}

package repository

import (
	"time"

	"gorm.io/gorm"

	"github.com/acheevo/fullstack-template/internal/auth/domain"
)

// RefreshTokenRepository handles database operations for refresh tokens
type RefreshTokenRepository struct {
	db *gorm.DB
}

// NewRefreshTokenRepository creates a new refresh token repository
func NewRefreshTokenRepository(db *gorm.DB) *RefreshTokenRepository {
	return &RefreshTokenRepository{
		db: db,
	}
}

// Create creates a new refresh token
func (r *RefreshTokenRepository) Create(token *domain.RefreshToken) error {
	return r.db.Create(token).Error
}

// GetByToken gets a refresh token by token string
func (r *RefreshTokenRepository) GetByToken(token string) (*domain.RefreshToken, error) {
	var refreshToken domain.RefreshToken
	err := r.db.Where("token = ?", token).First(&refreshToken).Error
	if err != nil {
		if err == gorm.ErrRecordNotFound {
			return nil, domain.ErrTokenNotFound
		}
		return nil, err
	}
	return &refreshToken, nil
}

// GetByUserID gets all refresh tokens for a user
func (r *RefreshTokenRepository) GetByUserID(userID uint) ([]*domain.RefreshToken, error) {
	var tokens []*domain.RefreshToken
	err := r.db.Where("user_id = ?", userID).Find(&tokens).Error
	return tokens, err
}

// Delete deletes a refresh token
func (r *RefreshTokenRepository) Delete(token string) error {
	return r.db.Where("token = ?", token).Delete(&domain.RefreshToken{}).Error
}

// DeleteByUserID deletes all refresh tokens for a user
func (r *RefreshTokenRepository) DeleteByUserID(userID uint) error {
	return r.db.Where("user_id = ?", userID).Delete(&domain.RefreshToken{}).Error
}

// DeleteExpired deletes all expired refresh tokens
func (r *RefreshTokenRepository) DeleteExpired() error {
	return r.db.Where("expires_at < ?", time.Now()).Delete(&domain.RefreshToken{}).Error
}

// Update updates a refresh token
func (r *RefreshTokenRepository) Update(token *domain.RefreshToken) error {
	return r.db.Save(token).Error
}

// GetActiveTokensCount returns the count of active tokens for a user
func (r *RefreshTokenRepository) GetActiveTokensCount(userID uint) (int64, error) {
	var count int64
	err := r.db.Model(&domain.RefreshToken{}).
		Where("user_id = ? AND expires_at > ?", userID, time.Now()).
		Count(&count).Error
	return count, err
}

// DeleteOldestTokensForUser deletes the oldest tokens for a user, keeping only the specified limit
func (r *RefreshTokenRepository) DeleteOldestTokensForUser(userID uint, keepCount int) error {
	// Get tokens ordered by creation date (oldest first)
	var tokens []*domain.RefreshToken
	err := r.db.Where("user_id = ?", userID).
		Order("created_at ASC").
		Find(&tokens).Error
	if err != nil {
		return err
	}

	// If we have more tokens than the limit, delete the oldest ones
	if len(tokens) > keepCount {
		tokensToDelete := tokens[:len(tokens)-keepCount]
		for _, token := range tokensToDelete {
			if err := r.db.Delete(token).Error; err != nil {
				return err
			}
		}
	}

	return nil
}

package repository

import (
	"time"

	"gorm.io/gorm"

	"github.com/acheevo/fullstack-template/internal/auth/domain"
)

// UserRepository handles database operations for users
type UserRepository struct {
	db *gorm.DB
}

// NewUserRepository creates a new user repository
func NewUserRepository(db *gorm.DB) *UserRepository {
	return &UserRepository{
		db: db,
	}
}

// Create creates a new user
func (r *UserRepository) Create(user *domain.User) error {
	return r.db.Create(user).Error
}

// GetByID gets a user by ID
func (r *UserRepository) GetByID(id uint) (*domain.User, error) {
	var user domain.User
	err := r.db.First(&user, id).Error
	if err != nil {
		if err == gorm.ErrRecordNotFound {
			return nil, domain.ErrUserNotFound
		}
		return nil, err
	}
	return &user, nil
}

// GetByEmail gets a user by email
func (r *UserRepository) GetByEmail(email string) (*domain.User, error) {
	var user domain.User
	err := r.db.Where("email = ?", email).First(&user).Error
	if err != nil {
		if err == gorm.ErrRecordNotFound {
			return nil, domain.ErrUserNotFound
		}
		return nil, err
	}
	return &user, nil
}

// GetByEmailVerifyToken gets a user by email verification token
func (r *UserRepository) GetByEmailVerifyToken(token string) (*domain.User, error) {
	var user domain.User
	err := r.db.Where("email_verify_token = ?", token).First(&user).Error
	if err != nil {
		if err == gorm.ErrRecordNotFound {
			return nil, domain.ErrUserNotFound
		}
		return nil, err
	}
	return &user, nil
}

// Update updates a user
func (r *UserRepository) Update(user *domain.User) error {
	return r.db.Save(user).Error
}

// UpdateLastLogin updates the last login time for a user
func (r *UserRepository) UpdateLastLogin(userID uint) error {
	now := time.Now()
	return r.db.Model(&domain.User{}).Where("id = ?", userID).Update("last_login_at", &now).Error
}

// Delete soft deletes a user
func (r *UserRepository) Delete(id uint) error {
	return r.db.Delete(&domain.User{}, id).Error
}

// ExistsByEmail checks if a user exists by email
func (r *UserRepository) ExistsByEmail(email string) (bool, error) {
	var count int64
	err := r.db.Model(&domain.User{}).Where("email = ?", email).Count(&count).Error
	if err != nil {
		return false, err
	}
	return count > 0, nil
}

// CountUsers returns the total number of users
func (r *UserRepository) CountUsers() (int64, error) {
	var count int64
	err := r.db.Model(&domain.User{}).Count(&count).Error
	return count, err
}

// GetUsers returns a paginated list of users
func (r *UserRepository) GetUsers(limit, offset int) ([]*domain.User, error) {
	var users []*domain.User
	err := r.db.Limit(limit).Offset(offset).Find(&users).Error
	return users, err
}

package service

import (
	"fmt"
	"log/slog"
	"strings"
	"time"

	"golang.org/x/crypto/bcrypt"

	"github.com/acheevo/fullstack-template/internal/auth/domain"
	"github.com/acheevo/fullstack-template/internal/auth/repository"
	"github.com/acheevo/fullstack-template/internal/shared/config"
)

// AuthService handles authentication operations
type AuthService struct {
	config                   *config.Config
	logger                   *slog.Logger
	userRepo                 *repository.UserRepository
	refreshTokenRepo         *repository.RefreshTokenRepository
	passwordResetRepo        *repository.PasswordResetRepository
	jwtService               *JWTService
	emailService             *EmailService
}

// NewAuthService creates a new authentication service
func NewAuthService(
	config *config.Config,
	logger *slog.Logger,
	userRepo *repository.UserRepository,
	refreshTokenRepo *repository.RefreshTokenRepository,
	passwordResetRepo *repository.PasswordResetRepository,
	jwtService *JWTService,
	emailService *EmailService,
) *AuthService {
	return &AuthService{
		config:            config,
		logger:            logger,
		userRepo:          userRepo,
		refreshTokenRepo:  refreshTokenRepo,
		passwordResetRepo: passwordResetRepo,
		jwtService:        jwtService,
		emailService:      emailService,
	}
}

// Register registers a new user
func (s *AuthService) Register(req *domain.RegisterRequest) (*domain.AuthResponse, error) {
	// Check if user already exists
	exists, err := s.userRepo.ExistsByEmail(req.Email)
	if err != nil {
		s.logger.Error("failed to check if user exists", "email", req.Email, "error", err)
		return nil, fmt.Errorf("failed to check user existence: %w", err)
	}
	if exists {
		return nil, domain.ErrUserAlreadyExists
	}

	// Validate password strength
	if err := s.validatePassword(req.Password); err != nil {
		return nil, err
	}

	// Hash password
	passwordHash, err := s.hashPassword(req.Password)
	if err != nil {
		s.logger.Error("failed to hash password", "error", err)
		return nil, fmt.Errorf("failed to hash password: %w", err)
	}

	// Generate email verification token
	emailVerifyToken, err := s.jwtService.GenerateRandomToken()
	if err != nil {
		s.logger.Error("failed to generate email verification token", "error", err)
		return nil, fmt.Errorf("failed to generate email verification token: %w", err)
	}

	// Create user
	user := &domain.User{
		Email:            strings.ToLower(strings.TrimSpace(req.Email)),
		PasswordHash:     passwordHash,
		FirstName:        strings.TrimSpace(req.FirstName),
		LastName:         strings.TrimSpace(req.LastName),
		EmailVerified:    false,
		EmailVerifyToken: emailVerifyToken,
		Status:           domain.StatusActive,
	}

	if err := s.userRepo.Create(user); err != nil {
		s.logger.Error("failed to create user", "email", req.Email, "error", err)
		return nil, fmt.Errorf("failed to create user: %w", err)
	}

	// Send email verification email
	if err := s.emailService.SendEmailVerification(user.Email, emailVerifyToken, user.FirstName); err != nil {
		s.logger.Error("failed to send email verification", "email", user.Email, "error", err)
		// Don't fail registration if email fails to send
	}

	// Generate tokens
	accessToken, err := s.jwtService.GenerateAccessToken(user)
	if err != nil {
		s.logger.Error("failed to generate access token", "user_id", user.ID, "error", err)
		return nil, fmt.Errorf("failed to generate access token: %w", err)
	}

	refreshToken, err := s.createRefreshToken(user.ID)
	if err != nil {
		s.logger.Error("failed to create refresh token", "user_id", user.ID, "error", err)
		return nil, fmt.Errorf("failed to create refresh token: %w", err)
	}

	s.logger.Info("user registered successfully", "user_id", user.ID, "email", user.Email)

	return &domain.AuthResponse{
		User:         user.ToResponse(),
		AccessToken:  accessToken,
		RefreshToken: refreshToken,
		ExpiresIn:    int64(s.jwtService.GetAccessTokenDuration().Seconds()),
	}, nil
}

// Login authenticates a user and returns tokens
func (s *AuthService) Login(req *domain.LoginRequest) (*domain.AuthResponse, error) {
	// Get user by email
	user, err := s.userRepo.GetByEmail(strings.ToLower(strings.TrimSpace(req.Email)))
	if err != nil {
		if err == domain.ErrUserNotFound {
			return nil, domain.ErrInvalidCredentials
		}
		s.logger.Error("failed to get user by email", "email", req.Email, "error", err)
		return nil, fmt.Errorf("failed to get user: %w", err)
	}

	// Check if user is active
	if !user.IsActive() {
		return nil, domain.ErrUserInactive
	}

	// Verify password
	if err := s.verifyPassword(req.Password, user.PasswordHash); err != nil {
		return nil, domain.ErrInvalidCredentials
	}

	// Update last login time
	if err := s.userRepo.UpdateLastLogin(user.ID); err != nil {
		s.logger.Error("failed to update last login", "user_id", user.ID, "error", err)
		// Don't fail login if this fails
	}

	// Generate tokens
	accessToken, err := s.jwtService.GenerateAccessToken(user)
	if err != nil {
		s.logger.Error("failed to generate access token", "user_id", user.ID, "error", err)
		return nil, fmt.Errorf("failed to generate access token: %w", err)
	}

	refreshToken, err := s.createRefreshToken(user.ID)
	if err != nil {
		s.logger.Error("failed to create refresh token", "user_id", user.ID, "error", err)
		return nil, fmt.Errorf("failed to create refresh token: %w", err)
	}

	s.logger.Info("user logged in successfully", "user_id", user.ID, "email", user.Email)

	return &domain.AuthResponse{
		User:         user.ToResponse(),
		AccessToken:  accessToken,
		RefreshToken: refreshToken,
		ExpiresIn:    int64(s.jwtService.GetAccessTokenDuration().Seconds()),
	}, nil
}

// RefreshToken refreshes an access token using a refresh token
func (s *AuthService) RefreshToken(req *domain.RefreshTokenRequest) (*domain.AuthResponse, error) {
	// Get refresh token from database
	refreshToken, err := s.refreshTokenRepo.GetByToken(req.RefreshToken)
	if err != nil {
		return nil, domain.ErrInvalidToken
	}

	// Check if token is expired
	if refreshToken.IsExpired() {
		// Clean up expired token
		_ = s.refreshTokenRepo.Delete(refreshToken.Token)
		return nil, domain.ErrTokenExpired
	}

	// Get user
	user, err := s.userRepo.GetByID(refreshToken.UserID)
	if err != nil {
		return nil, fmt.Errorf("failed to get user: %w", err)
	}

	// Check if user is active
	if !user.IsActive() {
		return nil, domain.ErrUserInactive
	}

	// Generate new access token
	accessToken, err := s.jwtService.GenerateAccessToken(user)
	if err != nil {
		s.logger.Error("failed to generate access token", "user_id", user.ID, "error", err)
		return nil, fmt.Errorf("failed to generate access token: %w", err)
	}

	s.logger.Info("token refreshed successfully", "user_id", user.ID)

	return &domain.AuthResponse{
		User:         user.ToResponse(),
		AccessToken:  accessToken,
		RefreshToken: refreshToken.Token, // Return the same refresh token
		ExpiresIn:    int64(s.jwtService.GetAccessTokenDuration().Seconds()),
	}, nil
}

// Logout invalidates a refresh token
func (s *AuthService) Logout(refreshToken string) error {
	if err := s.refreshTokenRepo.Delete(refreshToken); err != nil {
		s.logger.Error("failed to delete refresh token", "error", err)
		return fmt.Errorf("failed to logout: %w", err)
	}

	s.logger.Info("user logged out successfully")
	return nil
}

// LogoutAll invalidates all refresh tokens for a user
func (s *AuthService) LogoutAll(userID uint) error {
	if err := s.refreshTokenRepo.DeleteByUserID(userID); err != nil {
		s.logger.Error("failed to delete all refresh tokens", "user_id", userID, "error", err)
		return fmt.Errorf("failed to logout from all devices: %w", err)
	}

	s.logger.Info("user logged out from all devices", "user_id", userID)
	return nil
}

// VerifyEmail verifies a user's email address
func (s *AuthService) VerifyEmail(req *domain.EmailVerificationRequest) error {
	// Get user by email verification token
	user, err := s.userRepo.GetByEmailVerifyToken(req.Token)
	if err != nil {
		return domain.ErrInvalidToken
	}

	// Mark email as verified and clear token
	user.EmailVerified = true
	user.EmailVerifyToken = ""

	if err := s.userRepo.Update(user); err != nil {
		s.logger.Error("failed to update user email verification", "user_id", user.ID, "error", err)
		return fmt.Errorf("failed to verify email: %w", err)
	}

	// Send welcome email
	if err := s.emailService.SendWelcomeEmail(user.Email, user.FirstName); err != nil {
		s.logger.Error("failed to send welcome email", "email", user.Email, "error", err)
		// Don't fail verification if welcome email fails
	}

	s.logger.Info("email verified successfully", "user_id", user.ID, "email", user.Email)
	return nil
}

// ForgotPassword initiates password reset process
func (s *AuthService) ForgotPassword(req *domain.ForgotPasswordRequest) error {
	email := strings.ToLower(strings.TrimSpace(req.Email))

	// Check if user exists
	user, err := s.userRepo.GetByEmail(email)
	if err != nil {
		if err == domain.ErrUserNotFound {
			// Don't reveal if user exists or not for security
			s.logger.Info("password reset requested for non-existent email", "email", email)
			return nil
		}
		s.logger.Error("failed to get user by email", "email", email, "error", err)
		return fmt.Errorf("failed to process password reset request: %w", err)
	}

	// Check rate limiting - don't allow too many reset requests
	count, err := s.passwordResetRepo.GetValidTokensCount(email)
	if err != nil {
		s.logger.Error("failed to get valid tokens count", "email", email, "error", err)
		return fmt.Errorf("failed to process password reset request: %w", err)
	}
	if count >= 3 {
		s.logger.Warn("too many password reset requests", "email", email, "count", count)
		return fmt.Errorf("too many password reset requests, please try again later")
	}

	// Generate reset token
	token, err := s.jwtService.GenerateRandomToken()
	if err != nil {
		s.logger.Error("failed to generate reset token", "error", err)
		return fmt.Errorf("failed to generate reset token: %w", err)
	}

	// Create password reset record
	reset := &domain.PasswordReset{
		Email:     email,
		Token:     token,
		ExpiresAt: time.Now().Add(24 * time.Hour), // 24 hours expiry
		Used:      false,
	}

	if err := s.passwordResetRepo.Create(reset); err != nil {
		s.logger.Error("failed to create password reset", "email", email, "error", err)
		return fmt.Errorf("failed to create password reset: %w", err)
	}

	// Send password reset email
	if err := s.emailService.SendPasswordReset(email, token, user.FirstName); err != nil {
		s.logger.Error("failed to send password reset email", "email", email, "error", err)
		return fmt.Errorf("failed to send password reset email: %w", err)
	}

	s.logger.Info("password reset requested", "email", email)
	return nil
}

// ResetPassword resets a user's password using a reset token
func (s *AuthService) ResetPassword(req *domain.ResetPasswordRequest) error {
	// Validate passwords match
	if req.Password != req.ConfirmPassword {
		return domain.ErrPasswordsDoNotMatch
	}

	// Validate password strength
	if err := s.validatePassword(req.Password); err != nil {
		return err
	}

	// Get password reset token
	reset, err := s.passwordResetRepo.GetByToken(req.Token)
	if err != nil {
		return domain.ErrInvalidToken
	}

	// Check if token is expired or used
	if reset.IsExpired() {
		return domain.ErrTokenExpired
	}
	if reset.Used {
		return domain.ErrTokenAlreadyUsed
	}

	// Get user
	user, err := s.userRepo.GetByEmail(reset.Email)
	if err != nil {
		return fmt.Errorf("failed to get user: %w", err)
	}

	// Hash new password
	passwordHash, err := s.hashPassword(req.Password)
	if err != nil {
		s.logger.Error("failed to hash password", "error", err)
		return fmt.Errorf("failed to hash password: %w", err)
	}

	// Update user password
	user.PasswordHash = passwordHash
	if err := s.userRepo.Update(user); err != nil {
		s.logger.Error("failed to update user password", "user_id", user.ID, "error", err)
		return fmt.Errorf("failed to update password: %w", err)
	}

	// Mark token as used
	if err := s.passwordResetRepo.MarkAsUsed(req.Token); err != nil {
		s.logger.Error("failed to mark reset token as used", "token", req.Token, "error", err)
		// Don't fail if this fails
	}

	// Invalidate all refresh tokens to force re-login
	if err := s.refreshTokenRepo.DeleteByUserID(user.ID); err != nil {
		s.logger.Error("failed to invalidate refresh tokens", "user_id", user.ID, "error", err)
		// Don't fail if this fails
	}

	s.logger.Info("password reset successfully", "user_id", user.ID, "email", user.Email)
	return nil
}

// ChangePassword changes a user's password
func (s *AuthService) ChangePassword(userID uint, req *domain.ChangePasswordRequest) error {
	// Validate passwords match
	if req.NewPassword != req.ConfirmPassword {
		return domain.ErrPasswordsDoNotMatch
	}

	// Validate password strength
	if err := s.validatePassword(req.NewPassword); err != nil {
		return err
	}

	// Get user
	user, err := s.userRepo.GetByID(userID)
	if err != nil {
		return fmt.Errorf("failed to get user: %w", err)
	}

	// Verify current password
	if err := s.verifyPassword(req.CurrentPassword, user.PasswordHash); err != nil {
		return domain.ErrInvalidCredentials
	}

	// Hash new password
	passwordHash, err := s.hashPassword(req.NewPassword)
	if err != nil {
		s.logger.Error("failed to hash password", "error", err)
		return fmt.Errorf("failed to hash password: %w", err)
	}

	// Update user password
	user.PasswordHash = passwordHash
	if err := s.userRepo.Update(user); err != nil {
		s.logger.Error("failed to update user password", "user_id", user.ID, "error", err)
		return fmt.Errorf("failed to update password: %w", err)
	}

	s.logger.Info("password changed successfully", "user_id", user.ID)
	return nil
}

// GetUserProfile gets a user's profile
func (s *AuthService) GetUserProfile(userID uint) (*domain.UserResponse, error) {
	user, err := s.userRepo.GetByID(userID)
	if err != nil {
		return nil, fmt.Errorf("failed to get user: %w", err)
	}

	return user.ToResponse(), nil
}

// ValidateAccessToken validates an access token and returns user claims
func (s *AuthService) ValidateAccessToken(tokenString string) (*domain.JWTClaims, error) {
	return s.jwtService.ValidateAccessToken(tokenString)
}

// ResendEmailVerification resends email verification email
func (s *AuthService) ResendEmailVerification(userID uint) error {
	user, err := s.userRepo.GetByID(userID)
	if err != nil {
		return fmt.Errorf("failed to get user: %w", err)
	}

	if user.EmailVerified {
		return fmt.Errorf("email already verified")
	}

	// Generate new verification token if empty
	if user.EmailVerifyToken == "" {
		token, err := s.jwtService.GenerateRandomToken()
		if err != nil {
			s.logger.Error("failed to generate email verification token", "error", err)
			return fmt.Errorf("failed to generate email verification token: %w", err)
		}
		user.EmailVerifyToken = token
		if err := s.userRepo.Update(user); err != nil {
			s.logger.Error("failed to update user email verification token", "user_id", user.ID, "error", err)
			return fmt.Errorf("failed to update user: %w", err)
		}
	}

	// Send email verification
	if err := s.emailService.SendEmailVerification(user.Email, user.EmailVerifyToken, user.FirstName); err != nil {
		s.logger.Error("failed to send email verification", "email", user.Email, "error", err)
		return fmt.Errorf("failed to send email verification: %w", err)
	}

	s.logger.Info("email verification resent", "user_id", user.ID, "email", user.Email)
	return nil
}

// Helper methods

func (s *AuthService) createRefreshToken(userID uint) (string, error) {
	// Generate refresh token
	tokenStr, err := s.jwtService.GenerateRefreshToken()
	if err != nil {
		return "", err
	}

	// Create refresh token record
	refreshToken := &domain.RefreshToken{
		UserID:    userID,
		Token:     tokenStr,
		ExpiresAt: time.Now().Add(s.jwtService.GetRefreshTokenDuration()),
	}

	if err := s.refreshTokenRepo.Create(refreshToken); err != nil {
		return "", err
	}

	// Clean up old tokens (keep max 5 per user)
	if err := s.refreshTokenRepo.DeleteOldestTokensForUser(userID, 5); err != nil {
		s.logger.Error("failed to clean up old refresh tokens", "user_id", userID, "error", err)
		// Don't fail if cleanup fails
	}

	return tokenStr, nil
}

func (s *AuthService) hashPassword(password string) (string, error) {
	bytes, err := bcrypt.GenerateFromPassword([]byte(password), bcrypt.DefaultCost)
	return string(bytes), err
}

func (s *AuthService) verifyPassword(password, hash string) error {
	return bcrypt.CompareHashAndPassword([]byte(hash), []byte(password))
}

func (s *AuthService) validatePassword(password string) error {
	if len(password) < 8 {
		return domain.ErrWeakPassword
	}
	// Add more password strength validation as needed
	return nil
}

// CleanupExpiredTokens removes expired tokens from the database
func (s *AuthService) CleanupExpiredTokens() error {
	if err := s.refreshTokenRepo.DeleteExpired(); err != nil {
		s.logger.Error("failed to cleanup expired refresh tokens", "error", err)
		return err
	}

	if err := s.passwordResetRepo.DeleteExpired(); err != nil {
		s.logger.Error("failed to cleanup expired password reset tokens", "error", err)
		return err
	}

	if err := s.passwordResetRepo.DeleteUsed(); err != nil {
		s.logger.Error("failed to cleanup used password reset tokens", "error", err)
		return err
	}

	s.logger.Info("expired tokens cleaned up successfully")
	return nil
}

package service

import (
	"bytes"
	"fmt"
	"html/template"
	"log/slog"

	"gopkg.in/gomail.v2"

	"github.com/acheevo/fullstack-template/internal/shared/config"
)

// EmailService handles email sending operations
type EmailService struct {
	config *config.Config
	logger *slog.Logger
	dialer *gomail.Dialer
}

// NewEmailService creates a new email service
func NewEmailService(config *config.Config, logger *slog.Logger) *EmailService {
	var dialer *gomail.Dialer
	if config.SMTPUsername != "" && config.SMTPPassword != "" {
		dialer = gomail.NewDialer(config.SMTPHost, config.SMTPPort, config.SMTPUsername, config.SMTPPassword)
	}

	return &EmailService{
		config: config,
		logger: logger,
		dialer: dialer,
	}
}

// SendEmailVerification sends an email verification email
func (e *EmailService) SendEmailVerification(email, token, firstName string) error {
	if e.dialer == nil {
		e.logger.Warn("email service not configured, skipping email verification", "email", email)
		return nil
	}

	verificationURL := fmt.Sprintf("%s/verify-email?token=%s", e.config.FrontendURL, token)

	subject := "Verify your email address"
	htmlBody, err := e.renderEmailVerificationTemplate(firstName, verificationURL)
	if err != nil {
		return fmt.Errorf("failed to render email template: %w", err)
	}

	textBody := fmt.Sprintf(`Hi %s,

Please verify your email address by clicking the link below:
%s

If you didn't create an account, you can safely ignore this email.

Best regards,
%s Team`, firstName, verificationURL, e.config.EmailFromName)

	return e.sendEmail(email, subject, htmlBody, textBody)
}

// SendPasswordReset sends a password reset email
func (e *EmailService) SendPasswordReset(email, token, firstName string) error {
	if e.dialer == nil {
		e.logger.Warn("email service not configured, skipping password reset email", "email", email)
		return nil
	}

	resetURL := fmt.Sprintf("%s/reset-password?token=%s", e.config.FrontendURL, token)

	subject := "Reset your password"
	htmlBody, err := e.renderPasswordResetTemplate(firstName, resetURL)
	if err != nil {
		return fmt.Errorf("failed to render email template: %w", err)
	}

	textBody := fmt.Sprintf(`Hi %s,

You requested to reset your password. Click the link below to reset it:
%s

This link will expire in 24 hours. If you didn't request this, you can safely ignore this email.

Best regards,
%s Team`, firstName, resetURL, e.config.EmailFromName)

	return e.sendEmail(email, subject, htmlBody, textBody)
}

// SendWelcomeEmail sends a welcome email to new users
func (e *EmailService) SendWelcomeEmail(email, firstName string) error {
	if e.dialer == nil {
		e.logger.Warn("email service not configured, skipping welcome email", "email", email)
		return nil
	}

	subject := fmt.Sprintf("Welcome to %s!", e.config.EmailFromName)
	htmlBody, err := e.renderWelcomeTemplate(firstName)
	if err != nil {
		return fmt.Errorf("failed to render email template: %w", err)
	}

	textBody := fmt.Sprintf(`Hi %s,

Welcome to %s! Your account has been successfully created and verified.

You can now access all the features of our platform.

Best regards,
%s Team`, firstName, e.config.EmailFromName, e.config.EmailFromName)

	return e.sendEmail(email, subject, htmlBody, textBody)
}

// sendEmail sends an email with both HTML and text content
func (e *EmailService) sendEmail(to, subject, htmlBody, textBody string) error {
	m := gomail.NewMessage()
	m.SetHeader("From", m.FormatAddress(e.config.EmailFrom, e.config.EmailFromName))
	m.SetHeader("To", to)
	m.SetHeader("Subject", subject)
	m.SetBody("text/plain", textBody)
	m.AddAlternative("text/html", htmlBody)

	if err := e.dialer.DialAndSend(m); err != nil {
		e.logger.Error("failed to send email", "to", to, "subject", subject, "error", err)
		return fmt.Errorf("failed to send email: %w", err)
	}

	e.logger.Info("email sent successfully", "to", to, "subject", subject)
	return nil
}

