package middleware

import (
	"log/slog"
	"net/http"
	"strings"

	"github.com/gin-gonic/gin"

	"github.com/acheevo/fullstack-template/internal/auth/domain"
	"github.com/acheevo/fullstack-template/internal/auth/service"
)

// AuthMiddleware provides authentication middleware
type AuthMiddleware struct {
	logger      *slog.Logger
	authService *service.AuthService
}

// NewAuthMiddleware creates a new authentication middleware
func NewAuthMiddleware(logger *slog.Logger, authService *service.AuthService) *AuthMiddleware {
	return &AuthMiddleware{
		logger:      logger,
		authService: authService,
	}
}

// RequireAuth middleware that requires valid authentication
func (m *AuthMiddleware) RequireAuth() gin.HandlerFunc {
	return func(c *gin.Context) {
		token := m.extractToken(c)
		if token == "" {
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		claims, err := m.authService.ValidateAccessToken(token)
		if err != nil {
			m.logger.Warn("invalid access token", "error", err)
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "invalid or expired token",
			})
			c.Abort()
			return
		}

		// Set user information in context
		c.Set("user_id", claims.UserID)
		c.Set("user_email", claims.Email)
		c.Set("user_role", claims.Role)
		c.Set("token_type", claims.TokenType)
		c.Set("jwt_claims", claims)

		c.Next()
	}
}

// OptionalAuth middleware that optionally authenticates if token is present
func (m *AuthMiddleware) OptionalAuth() gin.HandlerFunc {
	return func(c *gin.Context) {
		token := m.extractToken(c)
		if token == "" {
			c.Next()
			return
		}

		claims, err := m.authService.ValidateAccessToken(token)
		if err != nil {
			// Don't abort for optional auth, just continue without user context
			m.logger.Debug("invalid access token in optional auth", "error", err)
			c.Next()
			return
		}

		// Set user information in context
		c.Set("user_id", claims.UserID)
		c.Set("user_email", claims.Email)
		c.Set("user_role", claims.Role)
		c.Set("token_type", claims.TokenType)
		c.Set("jwt_claims", claims)

		c.Next()
	}
}

// RequireEmailVerified middleware that requires email to be verified
func (m *AuthMiddleware) RequireEmailVerified() gin.HandlerFunc {
	return func(c *gin.Context) {
		userID, exists := c.Get("user_id")
		if !exists {
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		uid, ok := userID.(uint)
		if !ok {
			c.JSON(http.StatusInternalServerError, domain.ErrorResponse{
				Error: "invalid user ID",
			})
			c.Abort()
			return
		}

		// Get user profile to check email verification status
		profile, err := m.authService.GetUserProfile(uid)
		if err != nil {
			m.logger.Error("failed to get user profile for email verification check", "user_id", uid, "error", err)
			c.JSON(http.StatusInternalServerError, domain.ErrorResponse{
				Error: "failed to verify user status",
			})
			c.Abort()
			return
		}

		if !profile.EmailVerified {
			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "email verification required",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// RequireActiveUser middleware that requires user to be active
func (m *AuthMiddleware) RequireActiveUser() gin.HandlerFunc {
	return func(c *gin.Context) {
		userID, exists := c.Get("user_id")
		if !exists {
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		uid, ok := userID.(uint)
		if !ok {
			c.JSON(http.StatusInternalServerError, domain.ErrorResponse{
				Error: "invalid user ID",
			})
			c.Abort()
			return
		}

		// Get user profile to check active status
		profile, err := m.authService.GetUserProfile(uid)
		if err != nil {
			m.logger.Error("failed to get user profile for active check", "user_id", uid, "error", err)
			c.JSON(http.StatusInternalServerError, domain.ErrorResponse{
				Error: "failed to verify user status",
			})
			c.Abort()
			return
		}

		if profile.Status != domain.StatusActive {
			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "user account is inactive",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// extractToken extracts the token from the request
// Checks in order: Authorization header, access_token cookie
func (m *AuthMiddleware) extractToken(c *gin.Context) string {
	// Check Authorization header first
	authHeader := c.GetHeader("Authorization")
	if authHeader != "" {
		// Bearer token format: "Bearer <token>"
		if strings.HasPrefix(authHeader, "Bearer ") {
			return strings.TrimPrefix(authHeader, "Bearer ")
		}
	}

	// Check access_token cookie
	token, err := c.Cookie("access_token")
	if err == nil && token != "" {
		return token
	}

	return ""
}

// RequireRole middleware that requires a specific role
func (m *AuthMiddleware) RequireRole(role domain.UserRole) gin.HandlerFunc {
	return func(c *gin.Context) {
		userID, exists := c.Get("user_id")
		if !exists {
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		uid, ok := userID.(uint)
		if !ok {
			c.JSON(http.StatusInternalServerError, domain.ErrorResponse{
				Error: "invalid user ID",
			})
			c.Abort()
			return
		}

		// Get user profile to check role
		profile, err := m.authService.GetUserProfile(uid)
		if err != nil {
			m.logger.Error("failed to get user profile for role check", "user_id", uid, "error", err)
			c.JSON(http.StatusInternalServerError, domain.ErrorResponse{
				Error: "failed to verify user role",
			})
			c.Abort()
			return
		}

		if profile.Role != role {
			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "insufficient permissions",
			})
			c.Abort()
			return
		}

		// Set user profile in context for convenience
		c.Set("user_profile", profile)

		c.Next()
	}
}

// RequireAdmin middleware that requires admin role
func (m *AuthMiddleware) RequireAdmin() gin.HandlerFunc {
	return m.RequireRole(domain.RoleAdmin)
}

// RequireUserRole middleware that requires user role or higher
func (m *AuthMiddleware) RequireUserRole() gin.HandlerFunc {
	return func(c *gin.Context) {
		userID, exists := c.Get("user_id")
		if !exists {
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		uid, ok := userID.(uint)
		if !ok {
			c.JSON(http.StatusInternalServerError, domain.ErrorResponse{
				Error: "invalid user ID",
			})
			c.Abort()
			return
		}

		// Get user profile to check role
		profile, err := m.authService.GetUserProfile(uid)
		if err != nil {
			m.logger.Error("failed to get user profile for role check", "user_id", uid, "error", err)
			c.JSON(http.StatusInternalServerError, domain.ErrorResponse{
				Error: "failed to verify user role",
			})
			c.Abort()
			return
		}

		// Check if user has valid role (user or admin)
		if profile.Role != domain.RoleUser && profile.Role != domain.RoleAdmin {
			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "insufficient permissions",
			})
			c.Abort()
			return
		}

		// Set user profile in context for convenience
		c.Set("user_profile", profile)

		c.Next()
	}
}

// RequireActiveUserWithRole combines active user check with role validation
func (m *AuthMiddleware) RequireActiveUserWithRole(role domain.UserRole) gin.HandlerFunc {
	return func(c *gin.Context) {
		userID, exists := c.Get("user_id")
		if !exists {
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		uid, ok := userID.(uint)
		if !ok {
			c.JSON(http.StatusInternalServerError, domain.ErrorResponse{
				Error: "invalid user ID",
			})
			c.Abort()
			return
		}

		// Get user profile to check status and role
		profile, err := m.authService.GetUserProfile(uid)
		if err != nil {
			m.logger.Error("failed to get user profile for active role check", "user_id", uid, "error", err)
			c.JSON(http.StatusInternalServerError, domain.ErrorResponse{
				Error: "failed to verify user status",
			})
			c.Abort()
			return
		}

		// Check if user is active
		if profile.Status != domain.StatusActive {
			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "user account is inactive",
			})
			c.Abort()
			return
		}

		// Check role
		if profile.Role != role {
			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "insufficient permissions",
			})
			c.Abort()
			return
		}

		// Set user profile in context for convenience
		c.Set("user_profile", profile)

		c.Next()
	}
}

// RequireActiveAdmin combines active user and admin role checks
func (m *AuthMiddleware) RequireActiveAdmin() gin.HandlerFunc {
	return m.RequireActiveUserWithRole(domain.RoleAdmin)
}

// Helper functions

// GetCurrentUserID is a helper function to get the current user ID from context
func GetCurrentUserID(c *gin.Context) (uint, bool) {
	userID, exists := c.Get("user_id")
	if !exists {
		return 0, false
	}

	uid, ok := userID.(uint)
	return uid, ok
}

// GetCurrentUserEmail is a helper function to get the current user email from context
func GetCurrentUserEmail(c *gin.Context) (string, bool) {
	email, exists := c.Get("user_email")
	if !exists {
		return "", false
	}

	userEmail, ok := email.(string)
	return userEmail, ok
}

// GetCurrentUserProfile is a helper function to get the current user profile from context
func GetCurrentUserProfile(c *gin.Context) (*domain.UserResponse, bool) {
	profile, exists := c.Get("user_profile")
	if !exists {
		return nil, false
	}

	userProfile, ok := profile.(*domain.UserResponse)
	return userProfile, ok
}

// IsAuthenticated checks if the current request is authenticated
func IsAuthenticated(c *gin.Context) bool {
	_, exists := c.Get("user_id")
	return exists
}

// IsAdmin checks if the current user has admin role
func IsAdmin(c *gin.Context) bool {
	profile, exists := GetCurrentUserProfile(c)
	if !exists {
		return false
	}
	return profile.Role == domain.RoleAdmin
}

// IsActiveUser checks if the current user is active
func IsActiveUser(c *gin.Context) bool {
	profile, exists := GetCurrentUserProfile(c)
	if !exists {
		return false
	}
	return profile.Status == domain.StatusActive
}

package middleware

import (
	"net/http"

	"github.com/gin-gonic/gin"
)

func CORS() gin.HandlerFunc {
	return func(c *gin.Context) {
		origin := c.Request.Header.Get("Origin")
		c.Header("Access-Control-Allow-Origin", origin)
		c.Header("Access-Control-Allow-Methods", "GET, POST, PUT, DELETE, OPTIONS")
		c.Header("Access-Control-Allow-Headers", "Origin, Content-Type, Content-Length, Accept-Encoding, X-CSRF-Token, Authorization")
		c.Header("Access-Control-Expose-Headers", "Content-Length")
		c.Header("Access-Control-Allow-Credentials", "true")

		if c.Request.Method == "OPTIONS" {
			c.AbortWithStatus(http.StatusNoContent)
			return
		}

		c.Next()
	}
}

package middleware

import (
	"log/slog"

	"github.com/gin-gonic/gin"
)

func Logger(logger *slog.Logger) gin.HandlerFunc {
	return gin.LoggerWithFormatter(func(param gin.LogFormatterParams) string {
		logger.Info("HTTP request",
			"method", param.Method,
			"path", param.Path,
			"status", param.StatusCode,
			"latency", param.Latency,
			"client_ip", param.ClientIP,
			"user_agent", param.Request.UserAgent(),
		)
		return ""
	})
}

package middleware

import (
	"fmt"
	"log/slog"
	"net/http"
	"sync"
	"time"

	"github.com/gin-gonic/gin"

	"github.com/acheevo/fullstack-template/internal/auth/domain"
)

// RateLimiter implements a simple in-memory rate limiter
type RateLimiter struct {
	logger   *slog.Logger
	visitors map[string]*visitor
	mu       sync.RWMutex
	rate     int           // requests per window
	window   time.Duration // time window
	cleanupInterval time.Duration // cleanup interval
}

type visitor struct {
	count      int
	lastSeen   time.Time
	resetTime  time.Time
}

// NewRateLimiter creates a new rate limiter
func NewRateLimiter(logger *slog.Logger, rate int, window time.Duration) *RateLimiter {
	rl := &RateLimiter{
		logger:          logger,
		visitors:        make(map[string]*visitor),
		rate:            rate,
		window:          window,
		cleanupInterval: time.Minute * 5, // cleanup every 5 minutes
	}

	// Start cleanup goroutine
	go rl.cleanupRoutine()

	return rl
}

// AuthRateLimit creates a rate limiter middleware for authentication endpoints
func (rl *RateLimiter) AuthRateLimit() gin.HandlerFunc {
	return func(c *gin.Context) {
		key := rl.getKey(c)

		if !rl.allow(key) {
			rl.logger.Warn("rate limit exceeded", "ip", c.ClientIP(), "key", key)
			c.JSON(http.StatusTooManyRequests, domain.ErrorResponse{
				Error: "too many requests, please try again later",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// LoginRateLimit creates a specific rate limiter for login attempts
func (rl *RateLimiter) LoginRateLimit() gin.HandlerFunc {
	return func(c *gin.Context) {
		// Apply IP-based rate limiting for login attempts
		// We don't parse the JSON here to avoid consuming the request body
		ipKey := fmt.Sprintf("login:%s", c.ClientIP())
		if !rl.allow(ipKey) {
			rl.logger.Warn("login rate limit exceeded by IP", "ip", c.ClientIP())
			c.JSON(http.StatusTooManyRequests, domain.ErrorResponse{
				Error: "too many login attempts, please try again later",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// PasswordResetRateLimit creates a rate limiter for password reset requests
func (rl *RateLimiter) PasswordResetRateLimit() gin.HandlerFunc {
	return func(c *gin.Context) {
		// Apply IP-based rate limiting for password reset requests
		// We don't parse the JSON here to avoid consuming the request body
		ipKey := fmt.Sprintf("password_reset:%s", c.ClientIP())
		if !rl.allow(ipKey) {
			rl.logger.Warn("password reset rate limit exceeded by IP", "ip", c.ClientIP())
			c.JSON(http.StatusTooManyRequests, domain.ErrorResponse{
				Error: "too many password reset requests, please try again later",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// allow checks if a request is allowed based on the rate limit
func (rl *RateLimiter) allow(key string) bool {
	rl.mu.Lock()
	defer rl.mu.Unlock()

	v, exists := rl.visitors[key]
	now := time.Now()

	if !exists {
		rl.visitors[key] = &visitor{
			count:     1,
			lastSeen:  now,
			resetTime: now.Add(rl.window),
		}
		return true
	}

	// Reset count if window has passed
	if now.After(v.resetTime) {
		v.count = 1
		v.resetTime = now.Add(rl.window)
		v.lastSeen = now
		return true
	}

	// Check if rate limit exceeded
	if v.count >= rl.rate {
		v.lastSeen = now
		return false
	}

	// Increment count and allow
	v.count++
	v.lastSeen = now
	return true
}

// getKey generates a key for the rate limiter based on IP
func (rl *RateLimiter) getKey(c *gin.Context) string {
	return fmt.Sprintf("auth:%s", c.ClientIP())
}

// cleanupRoutine removes old entries from the visitors map
func (rl *RateLimiter) cleanupRoutine() {
	ticker := time.NewTicker(rl.cleanupInterval)
	defer ticker.Stop()

	for {
		select {
		case <-ticker.C:
			rl.cleanupExpiredEntries()
		}
	}
}

// cleanupExpiredEntries removes expired entries from the visitors map
func (rl *RateLimiter) cleanupExpiredEntries() {
	rl.mu.Lock()
	defer rl.mu.Unlock()

	now := time.Now()
	cutoff := now.Add(-rl.window * 2) // Keep entries for 2 windows

	for key, v := range rl.visitors {
		if v.lastSeen.Before(cutoff) {
			delete(rl.visitors, key)
		}
	}
}

// GetRemainingRequests returns the number of remaining requests for a key
func (rl *RateLimiter) GetRemainingRequests(key string) int {
	rl.mu.RLock()
	defer rl.mu.RUnlock()

	v, exists := rl.visitors[key]
	if !exists {
		return rl.rate
	}

	// If window has passed, return full rate
	if time.Now().After(v.resetTime) {
		return rl.rate
	}

	remaining := rl.rate - v.count
	if remaining < 0 {
		return 0
	}
	return remaining
}

// GetResetTime returns when the rate limit will reset for a key
func (rl *RateLimiter) GetResetTime(key string) time.Time {
	rl.mu.RLock()
	defer rl.mu.RUnlock()

	v, exists := rl.visitors[key]
	if !exists {
		return time.Now()
	}

	return v.resetTime
}

package middleware

import (
	"log/slog"
	"net/http"
	"strconv"

	"github.com/gin-gonic/gin"

	"github.com/acheevo/fullstack-template/internal/auth/domain"
	"github.com/acheevo/fullstack-template/internal/auth/service"
)

// RBACMiddleware provides role-based access control middleware
type RBACMiddleware struct {
	logger      *slog.Logger
	authService *service.AuthService
}

// NewRBACMiddleware creates a new RBAC middleware
func NewRBACMiddleware(logger *slog.Logger, authService *service.AuthService) *RBACMiddleware {
	return &RBACMiddleware{
		logger:      logger,
		authService: authService,
	}
}

// RequirePermission middleware that requires a specific permission
func (m *RBACMiddleware) RequirePermission(permission domain.Permission) gin.HandlerFunc {
	return func(c *gin.Context) {
		// Get user role from context (set by auth middleware)
		userRole, exists := m.getUserRole(c)
		if !exists {
			m.logger.Warn("permission check failed: no user role in context", "permission", permission)
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		// Check permission
		if !domain.HasPermission(userRole, permission) {
			userID, _ := c.Get("user_id")
			m.logger.Warn("permission denied",
				"user_id", userID,
				"user_role", userRole,
				"required_permission", permission,
				"path", c.Request.URL.Path,
			)

			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "insufficient permissions",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// RequireAnyPermission middleware that requires any of the specified permissions
func (m *RBACMiddleware) RequireAnyPermission(permissions []domain.Permission) gin.HandlerFunc {
	return func(c *gin.Context) {
		userRole, exists := m.getUserRole(c)
		if !exists {
			m.logger.Warn("permission check failed: no user role in context", "permissions", permissions)
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		if !domain.HasAnyPermission(userRole, permissions) {
			userID, _ := c.Get("user_id")
			m.logger.Warn("permission denied",
				"user_id", userID,
				"user_role", userRole,
				"required_permissions", permissions,
				"path", c.Request.URL.Path,
			)

			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "insufficient permissions",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// RequireAllPermissions middleware that requires all of the specified permissions
func (m *RBACMiddleware) RequireAllPermissions(permissions []domain.Permission) gin.HandlerFunc {
	return func(c *gin.Context) {
		userRole, exists := m.getUserRole(c)
		if !exists {
			m.logger.Warn("permission check failed: no user role in context", "permissions", permissions)
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		if !domain.HasAllPermissions(userRole, permissions) {
			userID, _ := c.Get("user_id")
			m.logger.Warn("permission denied",
				"user_id", userID,
				"user_role", userRole,
				"required_permissions", permissions,
				"path", c.Request.URL.Path,
			)

			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "insufficient permissions",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// RequireResourceAccess middleware that requires access to a specific resource with an action
func (m *RBACMiddleware) RequireResourceAccess(resource domain.Resource, action domain.Action) gin.HandlerFunc {
	permission := domain.BuildPermission(resource, action)
	return m.RequirePermission(permission)
}

// RequireUserManagement middleware for user management operations
func (m *RBACMiddleware) RequireUserManagement() gin.HandlerFunc {
	return m.RequirePermission(domain.PermissionUserManage)
}

// RequireAdminAccess middleware for admin-only operations
func (m *RBACMiddleware) RequireAdminAccess() gin.HandlerFunc {
	return m.RequireRole(domain.RoleAdmin)
}

// RequireRole middleware that requires a specific role (enhanced version)
func (m *RBACMiddleware) RequireRole(role domain.UserRole) gin.HandlerFunc {
	return func(c *gin.Context) {
		userRole, exists := m.getUserRole(c)
		if !exists {
			m.logger.Warn("role check failed: no user role in context", "required_role", role)
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		if userRole != role {
			userID, _ := c.Get("user_id")
			m.logger.Warn("role check failed",
				"user_id", userID,
				"user_role", userRole,
				"required_role", role,
				"path", c.Request.URL.Path,
			)

			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "insufficient role permissions",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// RequireMinimumRole middleware that requires at least the specified role level
func (m *RBACMiddleware) RequireMinimumRole(minRole domain.UserRole) gin.HandlerFunc {
	return func(c *gin.Context) {
		userRole, exists := m.getUserRole(c)
		if !exists {
			m.logger.Warn("role check failed: no user role in context", "minimum_role", minRole)
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		// Check if user role is equal to or higher than minimum role
		if userRole != minRole && !domain.IsRoleHigherThan(userRole, minRole) {
			userID, _ := c.Get("user_id")
			m.logger.Warn("minimum role check failed",
				"user_id", userID,
				"user_role", userRole,
				"minimum_role", minRole,
				"path", c.Request.URL.Path,
			)

			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "insufficient role level",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// RequireOwnResourceOrPermission middleware for operations on own resources or with specific permission
func (m *RBACMiddleware) RequireOwnResourceOrPermission(permission domain.Permission) gin.HandlerFunc {
	return func(c *gin.Context) {
		userID, exists := m.getCurrentUserID(c)
		if !exists {
			c.JSON(http.StatusUnauthorized, domain.ErrorResponse{
				Error: "authentication required",
			})
			c.Abort()
			return
		}

		userRole, _ := m.getUserRole(c)

		// Get target user ID from URL parameter
		targetIDStr := c.Param("id")
		if targetIDStr == "" {
			c.JSON(http.StatusBadRequest, domain.ErrorResponse{
				Error: "missing user ID parameter",
			})
			c.Abort()
			return
		}

		targetID, err := strconv.ParseUint(targetIDStr, 10, 32)
		if err != nil {
			c.JSON(http.StatusBadRequest, domain.ErrorResponse{
				Error: "invalid user ID parameter",
			})
			c.Abort()
			return
		}

		// Allow if accessing own resource
		if userID == uint(targetID) {
			c.Next()
			return
		}

		// Otherwise, check for required permission
		if !domain.HasPermission(userRole, permission) {
			m.logger.Warn("resource access denied",
				"user_id", userID,
				"target_id", targetID,
				"user_role", userRole,
				"required_permission", permission,
				"path", c.Request.URL.Path,
			)

			c.JSON(http.StatusForbidden, domain.ErrorResponse{
				Error: "insufficient permissions",
			})
			c.Abort()
			return
		}

		c.Next()
	}
}

// WithPermissionLogging middleware that logs permission checks for auditing
func (m *RBACMiddleware) WithPermissionLogging() gin.HandlerFunc {
	return func(c *gin.Context) {
		userID, _ := m.getCurrentUserID(c)
		userRole, _ := m.getUserRole(c)

		m.logger.Info("API access",
			"user_id", userID,
			"user_role", userRole,
			"method", c.Request.Method,
			"path", c.Request.URL.Path,
			"ip", c.ClientIP(),
			"user_agent", c.Request.UserAgent(),
		)

		c.Next()
	}
}

// Helper functions

// getUserRole gets the user role from the context
func (m *RBACMiddleware) getUserRole(c *gin.Context) (domain.UserRole, bool) {
	// First try to get from JWT claims if available
	if claims, exists := c.Get("jwt_claims"); exists {
		if jwtClaims, ok := claims.(*domain.JWTClaims); ok {
			return jwtClaims.Role, true
		}
	}

	// Fallback to getting from user profile (set by auth middleware)
	if profile, exists := c.Get("user_profile"); exists {
		if userProfile, ok := profile.(*domain.UserResponse); ok {
			return userProfile.Role, true
		}
	}

	// If no user profile in context, try to fetch it
	userID, exists := m.getCurrentUserID(c)
	if !exists {
		return "", false
	}

	profile, err := m.authService.GetUserProfile(userID)
	if err != nil {
		m.logger.Error("failed to get user profile for role check", "user_id", userID, "error", err)
		return "", false
	}

	// Set in context for future use
	c.Set("user_profile", profile)
	return profile.Role, true
}

// getCurrentUserID gets the current user ID from context
func (m *RBACMiddleware) getCurrentUserID(c *gin.Context) (uint, bool) {
	userID, exists := c.Get("user_id")
	if !exists {
		return 0, false
	}

	uid, ok := userID.(uint)
	if !ok {
		return 0, false
	}

	return uid, true
}

// SetRoleInContext sets the user role in context (used by auth middleware)
func SetRoleInContext(c *gin.Context, role domain.UserRole) {
	c.Set("user_role", role)
}

// GetRoleFromContext gets the user role from context
func GetRoleFromContext(c *gin.Context) (domain.UserRole, bool) {
	role, exists := c.Get("user_role")
	if !exists {
		return "", false
	}

	userRole, ok := role.(domain.UserRole)
	if !ok {
		return "", false
	}

	return userRole, true
}

// HasCurrentUserPermission checks if the current user has a specific permission
func HasCurrentUserPermission(c *gin.Context, permission domain.Permission) bool {
	role, exists := GetRoleFromContext(c)
	if !exists {
		return false
	}
	return domain.HasPermission(role, permission)
}

// IsCurrentUserAdmin checks if the current user is an admin
func IsCurrentUserAdmin(c *gin.Context) bool {
	role, exists := GetRoleFromContext(c)
	if !exists {
		return false
	}
	return role == domain.RoleAdmin
}

// Convenience middleware functions

// RequireUserRead requires user read permission
func (m *RBACMiddleware) RequireUserRead() gin.HandlerFunc {
	return m.RequirePermission(domain.PermissionUserRead)
}

// RequireUserWrite requires user write permission
func (m *RBACMiddleware) RequireUserWrite() gin.HandlerFunc {
	return m.RequirePermission(domain.PermissionUserWrite)
}

// RequireProfileAccess requires profile access (own profile or admin permission)
func (m *RBACMiddleware) RequireProfileAccess() gin.HandlerFunc {
	return m.RequireOwnResourceOrPermission(domain.PermissionUserRead)
}

// RequireAuditAccess requires audit log access
func (m *RBACMiddleware) RequireAuditAccess() gin.HandlerFunc {
	return m.RequirePermission(domain.PermissionAuditRead)
}

package middleware

import (
	"log/slog"
	"net/http"

	"github.com/gin-gonic/gin"
)

func Recovery(logger *slog.Logger) gin.HandlerFunc {
	return gin.CustomRecovery(func(c *gin.Context, recovered interface{}) {
		logger.Error("panic recovered",
			"error", recovered,
			"method", c.Request.Method,
			"path", c.Request.URL.Path,
			"client_ip", c.ClientIP(),
		)

		c.JSON(http.StatusInternalServerError, gin.H{
			"error": "Internal server error",
		})
	})
}

package middleware

import (
	"crypto/subtle"
	"encoding/base64"
	"fmt"
	"log/slog"
	"net/http"
	"strings"

	"github.com/gin-gonic/gin"
	"github.com/google/uuid"

	"github.com/acheevo/fullstack-template/internal/shared/config"
	"github.com/acheevo/fullstack-template/internal/shared/errors"
)

// SecurityHeaders adds security headers to responses
func SecurityHeaders(config *config.Config) gin.HandlerFunc {
	return func(c *gin.Context) {
		// Prevent MIME type sniffing
		c.Header("X-Content-Type-Options", "nosniff")

		// Prevent clickjacking
		c.Header("X-Frame-Options", "DENY")

		// Enable XSS protection
		c.Header("X-XSS-Protection", "1; mode=block")

		// Prevent downloading of files with dangerous extensions
		c.Header("X-Download-Options", "noopen")

		// Prevent content from being embedded in frames from other origins
		c.Header("Content-Security-Policy", generateCSP(config))

		// Force HTTPS in production
		if config.IsProduction() {
			// HTTP Strict Transport Security (HSTS)
			c.Header("Strict-Transport-Security", "max-age=31536000; includeSubDomains; preload")

			// Prevent HTTP access in production
			if c.Request.Header.Get("X-Forwarded-Proto") != "https" && c.Request.TLS == nil {
				redirectURL := "https://" + c.Request.Host + c.Request.RequestURI
				c.Redirect(http.StatusMovedPermanently, redirectURL)
				c.Abort()
				return
			}
		}

		// Referrer policy
		c.Header("Referrer-Policy", "strict-origin-when-cross-origin")

		// Feature policy / Permissions policy
		c.Header("Permissions-Policy", "camera=(), microphone=(), geolocation=(), interest-cohort=()")

		c.Next()
	}
}

// generateCSP generates a Content Security Policy header
func generateCSP(config *config.Config) string {
	policies := []string{
		"default-src 'self'",
		"script-src 'self' 'unsafe-inline' 'unsafe-eval'", // Note: 'unsafe-inline' and 'unsafe-eval' should be removed in production with proper nonce/hash
		"style-src 'self' 'unsafe-inline' https://fonts.googleapis.com",
		"font-src 'self' https://fonts.gstatic.com",
		"img-src 'self' data: https:",
		"connect-src 'self'",
		"frame-ancestors 'none'",
		"base-uri 'self'",
		"form-action 'self'",
	}

	// Add frontend URL to connect-src for API calls
	if config.FrontendURL != "" {
		policies = append(policies, fmt.Sprintf("connect-src 'self' %s", config.FrontendURL))
	}

	return strings.Join(policies, "; ")
}

// CSRFProtection provides CSRF protection using double-submit cookie pattern
func CSRFProtection(config *config.Config, logger *slog.Logger) gin.HandlerFunc {
	if !config.IsFeatureEnabled("csrf_protection") {
		logger.Info("CSRF protection disabled by feature flag")
		return func(c *gin.Context) { c.Next() }
	}

	return func(c *gin.Context) {
		// Skip CSRF protection for safe methods
		if isSafeMethod(c.Request.Method) {
			c.Next()
			return
		}

		// Skip CSRF protection for API endpoints with proper authentication
		if isAPIEndpoint(c.Request.URL.Path) && hasValidAPIAuth(c) {
			c.Next()
			return
		}

		token := getCSRFToken(c, config)
		if token == "" {
			logger.Warn("CSRF token missing",
				"method", c.Request.Method,
				"path", c.Request.URL.Path,
				"ip", c.ClientIP(),
			)
			errors.AbortWithError(c, errors.New(errors.CodeForbidden, "CSRF token required"))
			return
		}

		if !validateCSRFToken(c, token, config) {
			logger.Warn("CSRF token validation failed",
				"method", c.Request.Method,
				"path", c.Request.URL.Path,
				"ip", c.ClientIP(),
				"user_agent", c.Request.UserAgent(),
			)
			errors.AbortWithError(c, errors.New(errors.CodeForbidden, "CSRF token invalid"))
			return
		}

		c.Next()
	}
}

// getCSRFToken extracts CSRF token from request
func getCSRFToken(c *gin.Context, config *config.Config) string {
	// Try header first
	token := c.GetHeader("X-CSRF-Token")
	if token != "" {
		return token
	}

	// Try form field
	token = c.PostForm("_csrf_token")
	if token != "" {
		return token
	}

	// Try query parameter (less secure, only for specific cases)
	return c.Query("_csrf_token")
}

// validateCSRFToken validates a CSRF token
func validateCSRFToken(c *gin.Context, token string, config *config.Config) bool {
	// Get the expected token from cookie
	cookie, err := c.Request.Cookie("_csrf_token")
	if err != nil {
		return false
	}

	// Use constant-time comparison to prevent timing attacks
	return subtle.ConstantTimeCompare([]byte(token), []byte(cookie.Value)) == 1
}

// GenerateCSRFToken generates a new CSRF token
func GenerateCSRFToken(c *gin.Context, config *config.Config) string {
	// Generate a random token
	token := generateSecureToken()

	// Set cookie with the token
	c.SetCookie(
		"_csrf_token",
		token,
		3600, // 1 hour
		"/",
		"", // domain
		config.IsProduction(), // secure
		true, // httpOnly
	)

	return token
}

// isSafeMethod checks if HTTP method is safe (doesn't modify state)
func isSafeMethod(method string) bool {
	safeMethods := []string{"GET", "HEAD", "OPTIONS", "TRACE"}
	for _, safe := range safeMethods {
		if method == safe {
			return true
		}
	}
	return false
}

// isAPIEndpoint checks if the path is an API endpoint
func isAPIEndpoint(path string) bool {
	return strings.HasPrefix(path, "/api/")
}

// hasValidAPIAuth checks if request has valid API authentication
func hasValidAPIAuth(c *gin.Context) bool {
	// Check for Bearer token
	auth := c.GetHeader("Authorization")
	if strings.HasPrefix(auth, "Bearer ") {
		return true
	}

	// Check for API key
	apiKey := c.GetHeader("X-API-Key")
	if apiKey != "" {
		return true
	}

	return false
}

// generateSecureToken generates a cryptographically secure random token
func generateSecureToken() string {
	return base64.URLEncoding.EncodeToString([]byte(uuid.New().String()))
}

// RequestID middleware adds a unique request ID to each request
func RequestID() gin.HandlerFunc {
	return func(c *gin.Context) {
		// Check if request ID is already provided
		requestID := c.GetHeader("X-Request-ID")
		if requestID == "" {
			requestID = uuid.New().String()
		}

		// Set in context and response header
		c.Set("request_id", requestID)
		c.Header("X-Request-ID", requestID)

		c.Next()
	}
}

// TraceID middleware adds a trace ID for distributed tracing
func TraceID() gin.HandlerFunc {
	return func(c *gin.Context) {
		// Check if trace ID is already provided
		traceID := c.GetHeader("X-Trace-ID")
		if traceID == "" {
			traceID = uuid.New().String()
		}

		// Set in context and response header
		c.Set("trace_id", traceID)
		c.Header("X-Trace-ID", traceID)

		c.Next()
	}
}

// TrustedProxies middleware validates trusted proxy headers
func TrustedProxies(config *config.Config, logger *slog.Logger) gin.HandlerFunc {
	return func(c *gin.Context) {
		// In production, validate that proxy headers come from trusted sources
		if config.IsProduction() {
			// Check X-Forwarded-For header for suspicious values
			if xff := c.GetHeader("X-Forwarded-For"); xff != "" {
				// Log for monitoring
				logger.Debug("Request with X-Forwarded-For header",
					"xff", xff,
					"remote_addr", c.Request.RemoteAddr,
					"path", c.Request.URL.Path,
				)
			}

			// Check X-Real-IP header
			if xri := c.GetHeader("X-Real-IP"); xri != "" {
				logger.Debug("Request with X-Real-IP header",
					"real_ip", xri,
					"remote_addr", c.Request.RemoteAddr,
					"path", c.Request.URL.Path,
				)
			}
		}

		c.Next()
	}
}

// InputSanitization middleware provides basic input sanitization
func InputSanitization(logger *slog.Logger) gin.HandlerFunc {
	return func(c *gin.Context) {
		// Check for potentially malicious patterns in query parameters
		for key, values := range c.Request.URL.Query() {
			for _, value := range values {
				if containsSuspiciousContent(value) {
					logger.Warn("Suspicious query parameter detected",
						"key", key,
						"value", value,
						"ip", c.ClientIP(),
						"user_agent", c.Request.UserAgent(),
						"path", c.Request.URL.Path,
					)

					errors.AbortWithError(c, errors.BadRequest("Invalid request parameters"))
					return
				}
			}
		}

		// Check User-Agent for suspicious patterns
		userAgent := c.Request.UserAgent()
		if containsSuspiciousUserAgent(userAgent) {
			logger.Warn("Suspicious User-Agent detected",
				"user_agent", userAgent,
				"ip", c.ClientIP(),
				"path", c.Request.URL.Path,
			)
		}

		c.Next()
	}
}

// containsSuspiciousContent checks for common attack patterns
func containsSuspiciousContent(content string) bool {
	suspiciousPatterns := []string{
		"<script",
		"javascript:",
		"onload=",
		"onerror=",
		"eval(",
		"alert(",
		"document.cookie",
		"document.write",
		"../",
		"..\\",
		"<iframe",
		"<object",
		"<embed",
		"data:text/html",
		"vbscript:",
	}

	lowerContent := strings.ToLower(content)
	for _, pattern := range suspiciousPatterns {
		if strings.Contains(lowerContent, pattern) {
			return true
		}
	}

	return false
}

// containsSuspiciousUserAgent checks for suspicious user agents
func containsSuspiciousUserAgent(userAgent string) bool {
	suspiciousPatterns := []string{
		"nikto",
		"sqlmap",
		"nmap",
		"masscan",
		"zap",
		"burp",
		"acunetix",
		"nessus",
		"openvas",
		"w3af",
		"havij",
		"grabber",
	}

	lowerUA := strings.ToLower(userAgent)
	for _, pattern := range suspiciousPatterns {
		if strings.Contains(lowerUA, pattern) {
			return true
		}
	}

	return false
}

// CORS middleware with security considerations
func SecureCORS(config *config.Config) gin.HandlerFunc {
	return func(c *gin.Context) {
		origin := c.Request.Header.Get("Origin")

		// Check if origin is allowed
		if isAllowedOrigin(origin, config.GetCORSOrigins()) {
			c.Header("Access-Control-Allow-Origin", origin)
		} else if config.IsDevelopment() {
			// In development, be more permissive
			c.Header("Access-Control-Allow-Origin", "*")
		}

		c.Header("Access-Control-Allow-Credentials", "true")
		c.Header("Access-Control-Allow-Headers", "Content-Type, Content-Length, Accept-Encoding, X-CSRF-Token, Authorization, accept, origin, Cache-Control, X-Requested-With, X-API-Key")
		c.Header("Access-Control-Allow-Methods", "POST, GET, OPTIONS, PUT, DELETE, PATCH")
		c.Header("Access-Control-Expose-Headers", "X-Request-ID, X-Trace-ID")
		c.Header("Access-Control-Max-Age", "86400") // 24 hours

		// Handle preflight requests
		if c.Request.Method == "OPTIONS" {
			c.AbortWithStatus(http.StatusNoContent)
			return
		}

		c.Next()
	}
}

// isAllowedOrigin checks if an origin is in the allowed list
func isAllowedOrigin(origin string, allowedOrigins []string) bool {
	for _, allowed := range allowedOrigins {
		if allowed == "*" || allowed == origin {
			return true
		}
	}
	return false
}

// ContentLengthLimit middleware limits request body size
func ContentLengthLimit(maxBytes int64) gin.HandlerFunc {
	return func(c *gin.Context) {
		if c.Request.ContentLength > maxBytes {
			errors.AbortWithError(c, errors.New(errors.CodeRequestTooLarge, 
				fmt.Sprintf("Request body too large (max: %d bytes)", maxBytes)))
			return
		}

		// Also set a limit on the request body reader
		c.Request.Body = http.MaxBytesReader(c.Writer, c.Request.Body, maxBytes)

		c.Next()
	}
}

// SecurityHeaders middleware that combines multiple security measures
func ComprehensiveSecurity(config *config.Config, logger *slog.Logger) gin.HandlerFunc {
	return gin.HandlerFunc(func(c *gin.Context) {
		// Apply security headers
		SecurityHeaders(config)(c)

		// Apply request/trace IDs
		RequestID()(c)
		TraceID()(c)

		// Apply input sanitization
		InputSanitization(logger)(c)

		// Apply trusted proxies validation
		TrustedProxies(config, logger)(c)

		// Apply content length limit (10MB default)
		ContentLengthLimit(10 * 1024 * 1024)(c)

		c.Next()
	})
}

package bootstrap

import (
	"errors"
	"log/slog"

	"golang.org/x/crypto/bcrypt"
	"gorm.io/gorm"

	"github.com/acheevo/fullstack-template/internal/auth/domain"
	"github.com/acheevo/fullstack-template/internal/shared/config"
)

// Service handles bootstrap operations for the application
type Service struct {
	config *config.Config
	db     *gorm.DB
	logger *slog.Logger
}

// NewService creates a new bootstrap service
func NewService(cfg *config.Config, db *gorm.DB, logger *slog.Logger) *Service {
	return &Service{
		config: cfg,
		db:     db,
		logger: logger,
	}
}

// Bootstrap runs all bootstrap operations
func (s *Service) Bootstrap() error {
	if !s.config.BootstrapEnabled {
		s.logger.Info("bootstrap disabled, skipping")
		return nil
	}

	s.logger.Info("starting bootstrap process")

	if err := s.createDemoUsers(); err != nil {
		s.logger.Error("failed to create demo users", "error", err)
		return err
	}

	s.logger.Info("bootstrap process completed successfully")
	return nil
}

// createDemoUsers creates the demo admin and user accounts
func (s *Service) createDemoUsers() error {
	// Create admin user
	if err := s.createUserIfNotExists(
		s.config.AdminEmail,
		s.config.AdminPassword,
		"Admin",
		"User",
		domain.RoleAdmin,
	); err != nil {
		return err
	}

	// Create demo user
	if err := s.createUserIfNotExists(
		s.config.DemoUserEmail,
		s.config.DemoUserPassword,
		"Demo",
		"User",
		domain.RoleUser,
	); err != nil {
		return err
	}

	return nil
}

// createUserIfNotExists creates a user if they don't already exist
func (s *Service) createUserIfNotExists(email, password, firstName, lastName string, role domain.UserRole) error {
	// Check if user already exists
	var existingUser domain.User
	err := s.db.Where("email = ?", email).First(&existingUser).Error
	if err == nil {
		// User exists, check if role matches
		if existingUser.Role != role {
			s.logger.Info("updating user role", "email", email, "old_role", existingUser.Role, "new_role", role)
			if err := s.db.Model(&existingUser).Update("role", role).Error; err != nil {
				return err
			}
		} else {
			s.logger.Debug("user already exists", "email", email, "role", role)
		}
		return nil
	}

	// If error is not "record not found", return the error
	if !errors.Is(err, gorm.ErrRecordNotFound) {
		return err
	}

	// Hash password
	hashedPassword, err := bcrypt.GenerateFromPassword([]byte(password), bcrypt.DefaultCost)
	if err != nil {
		return err
	}

	// Create new user
	user := &domain.User{
		Email:        email,
		PasswordHash: string(hashedPassword),
		FirstName:    firstName,
		LastName:     lastName,
		Role:         role,
		Status:       domain.StatusActive,
		EmailVerified: true, // Bootstrap users are auto-verified
		Preferences: domain.UserPreferences{
			Theme:    "light",
			Language: "en",
			Timezone: "UTC",
			Notifications: domain.NotificationPrefs{
				Email: true,
				SMS:   false,
				Push:  true,
			},
			Privacy: domain.PrivacyPrefs{
				ProfileVisible: true,
				ShowEmail:      false,
			},
			Custom: make(map[string]any),
		},
	}

	if err := s.db.Create(user).Error; err != nil {
		return err
	}

	s.logger.Info("created bootstrap user", "email", email, "role", role, "id", user.ID)
	return nil
}

// DropDemoUsers removes demo users (useful for testing)
func (s *Service) DropDemoUsers() error {
	emails := []string{s.config.AdminEmail, s.config.DemoUserEmail}

	for _, email := range emails {
		if err := s.db.Where("email = ?", email).Delete(&domain.User{}).Error; err != nil {
			s.logger.Error("failed to delete demo user", "email", email, "error", err)
			return err
		}
		s.logger.Info("deleted demo user", "email", email)
	}

	return nil
}

package config

import (
	"fmt"
	"strings"
	"time"

	"github.com/go-playground/validator/v10"
	"github.com/kelseyhightower/envconfig"
)

type Config struct {
	// Application Settings
	Environment string `envconfig:"ENVIRONMENT" default:"development" validate:"oneof=development staging production"`
	Port        string `envconfig:"PORT" default:"8080" validate:"numeric"`
	LogLevel    string `envconfig:"LOG_LEVEL" default:"info" validate:"oneof=debug info warn error"`
	AppName     string `envconfig:"APP_NAME" default:"Fullstack Template"`
	Version     string `envconfig:"APP_VERSION" default:"1.0.0"`

	// Database Configuration
	DatabaseHost     string `envconfig:"DATABASE_HOST" default:"localhost" validate:"required"`
	DatabasePort     string `envconfig:"DATABASE_PORT" default:"5432" validate:"numeric"`
	DatabaseUser     string `envconfig:"DATABASE_USER" default:"postgres" validate:"required"`
	DatabasePassword string `envconfig:"DATABASE_PASSWORD" default:"postgres"`
	DatabaseName     string `envconfig:"DATABASE_NAME" default:"fullstack_template" validate:"required"`
	DatabaseSSLMode  string `envconfig:"DATABASE_SSL_MODE" default:"disable" validate:"oneof=disable require verify-ca verify-full"`

	// Database Pool Configuration
	DBMaxIdleConns     int    `envconfig:"DB_MAX_IDLE_CONNS" default:"10" validate:"min=1,max=100"`
	DBMaxOpenConns     int    `envconfig:"DB_MAX_OPEN_CONNS" default:"100" validate:"min=1,max=1000"`
	DBConnMaxLifetime  string `envconfig:"DB_CONN_MAX_LIFETIME" default:"1h" validate:"required"`
	DBConnMaxIdleTime  string `envconfig:"DB_CONN_MAX_IDLE_TIME" default:"30m"`

	// JWT Configuration
	JWTSecret               string `envconfig:"JWT_SECRET" default:"dev-secret-change-in-production" validate:"min=32"`
	JWTAccessTokenDuration  string `envconfig:"JWT_ACCESS_TOKEN_DURATION" default:"15m" validate:"required"`
	JWTRefreshTokenDuration string `envconfig:"JWT_REFRESH_TOKEN_DURATION" default:"7d" validate:"required"`
	JWTIssuer               string `envconfig:"JWT_ISSUER" default:"fullstack-template"`

	// Email Configuration
	EmailProvider string `envconfig:"EMAIL_PROVIDER" default:"smtp" validate:"oneof=smtp sendgrid postmark mailgun"`
	EmailFrom     string `envconfig:"EMAIL_FROM" default:"noreply@localhost" validate:"email"`
	EmailFromName string `envconfig:"EMAIL_FROM_NAME" default:"App"`

	// SMTP Configuration
	SMTPHost      string `envconfig:"SMTP_HOST" default:"localhost"`
	SMTPPort      int    `envconfig:"SMTP_PORT" default:"587" validate:"min=1,max=65535"`
	SMTPUsername  string `envconfig:"SMTP_USERNAME"`
	SMTPPassword  string `envconfig:"SMTP_PASSWORD"`
	SMTPUseTLS    bool   `envconfig:"SMTP_USE_TLS" default:"true"`

	// Email Service Provider Keys
	SendGridAPIKey string `envconfig:"SENDGRID_API_KEY"`
	PostmarkAPIKey string `envconfig:"POSTMARK_API_KEY"`
	MailgunAPIKey  string `envconfig:"MAILGUN_API_KEY"`
	MailgunDomain  string `envconfig:"MAILGUN_DOMAIN"`

	// Application URLs
	FrontendURL string `envconfig:"FRONTEND_URL" default:"http://localhost:3000" validate:"url"`
	BackendURL  string `envconfig:"BACKEND_URL" default:"http://localhost:8080" validate:"url"`

	// Security Configuration
	CSRFSecret       string `envconfig:"CSRF_SECRET" default:"dev-csrf-secret-change-in-production" validate:"min=32"`
	CORSOrigins      string `envconfig:"CORS_ORIGINS" default:"http://localhost:3000,http://localhost:8080"`
	SecureHeaders    bool   `envconfig:"SECURE_HEADERS" default:"true"`
	RateLimitEnabled bool   `envconfig:"RATE_LIMIT_ENABLED" default:"true"`

	// Feature Flags
	FeatureFlags FeatureFlags `envconfig:"FEATURES"`

	// Monitoring Configuration
	MetricsEnabled   bool   `envconfig:"METRICS_ENABLED" default:"true"`
	MetricsPort      string `envconfig:"METRICS_PORT" default:"9090"`
	HealthCheckPath  string `envconfig:"HEALTH_CHECK_PATH" default:"/api/health"`
	SentryDSN        string `envconfig:"SENTRY_DSN"`
	TracingEnabled   bool   `envconfig:"TRACING_ENABLED" default:"false"`

	// Cache Configuration
	RedisURL      string `envconfig:"REDIS_URL" default:"redis://localhost:6379/0"`
	CacheEnabled  bool   `envconfig:"CACHE_ENABLED" default:"true"`
	CachePrefix   string `envconfig:"CACHE_PREFIX" default:"ft:"`
	CacheTTL      string `envconfig:"CACHE_TTL" default:"1h"`

	// File Storage Configuration
	StorageProvider string `envconfig:"STORAGE_PROVIDER" default:"local" validate:"oneof=local s3 gcs"`
	S3Bucket        string `envconfig:"S3_BUCKET"`
	S3Region        string `envconfig:"S3_REGION" default:"us-east-1"`
	GCSBucket       string `envconfig:"GCS_BUCKET"`
	LocalStoragePath string `envconfig:"LOCAL_STORAGE_PATH" default:"./uploads"`

	// Bootstrap Configuration
	BootstrapEnabled bool   `envconfig:"BOOTSTRAP_ENABLED" default:"true"`
	AdminEmail       string `envconfig:"ADMIN_EMAIL" default:"admin@example.com"`
	AdminPassword    string `envconfig:"ADMIN_PASSWORD" default:"admin123"`
	DemoUserEmail    string `envconfig:"DEMO_USER_EMAIL" default:"user@example.com"`
	DemoUserPassword string `envconfig:"DEMO_USER_PASSWORD" default:"user1234"`
}

// FeatureFlags represents application feature flags
type FeatureFlags struct {
	EmailVerification bool `envconfig:"EMAIL_VERIFICATION" default:"true"`
	TwoFactorAuth     bool `envconfig:"TWO_FACTOR_AUTH" default:"false"`
	AdminAPI          bool `envconfig:"ADMIN_API" default:"true"`
	Metrics           bool `envconfig:"METRICS" default:"true"`
	FileUploads       bool `envconfig:"FILE_UPLOADS" default:"true"`
	SocialLogin       bool `envconfig:"SOCIAL_LOGIN" default:"false"`
	EmailTemplates    bool `envconfig:"EMAIL_TEMPLATES" default:"true"`
	RateLimiting      bool `envconfig:"RATE_LIMITING" default:"true"`
	CSRFProtection    bool `envconfig:"CSRF_PROTECTION" default:"true"`
	SecurityHeaders   bool `envconfig:"SECURITY_HEADERS" default:"true"`
}

// Load loads and validates the application configuration
func Load() (*Config, error) {
	var cfg Config
	if err := envconfig.Process("", &cfg); err != nil {
		return nil, fmt.Errorf("failed to process environment config: %w", err)
	}

	// Validate configuration
	if err := cfg.Validate(); err != nil {
		return nil, fmt.Errorf("configuration validation failed: %w", err)
	}

	// Apply environment-specific defaults
	cfg.applyEnvironmentDefaults()

	// Validate critical production settings
	if cfg.IsProduction() {
		if err := cfg.validateProductionSettings(); err != nil {
			return nil, fmt.Errorf("production validation failed: %w", err)
		}
	}

	return &cfg, nil
}

// Validate validates the configuration using struct tags
func (c *Config) Validate() error {
	validate := validator.New()
	return validate.Struct(c)
}

// applyEnvironmentDefaults applies environment-specific configuration defaults
func (c *Config) applyEnvironmentDefaults() {
	switch c.Environment {
	case "production":
		// Production defaults
		if c.LogLevel == "debug" {
			c.LogLevel = "info"
		}
		c.SecureHeaders = true
		c.RateLimitEnabled = true
		c.FeatureFlags.CSRFProtection = true
		c.FeatureFlags.SecurityHeaders = true

	case "staging":
		// Staging defaults
		c.SecureHeaders = true
		c.RateLimitEnabled = true

	case "development":
		// Development defaults - already set in struct tags
		break
	}
}

// validateProductionSettings validates critical production configuration
func (c *Config) validateProductionSettings() error {
	var errors []string

	// Check for default secrets in production
	if strings.Contains(c.JWTSecret, "dev-") {
		errors = append(errors, "JWT_SECRET must be changed from default value in production")
	}

	if strings.Contains(c.CSRFSecret, "dev-") {
		errors = append(errors, "CSRF_SECRET must be changed from default value in production")
	}

	// Check database SSL mode
	if c.DatabaseSSLMode == "disable" {
		errors = append(errors, "DATABASE_SSL_MODE should not be 'disable' in production")
	}

	// Check email configuration
	if c.EmailProvider == "smtp" && c.SMTPHost == "localhost" {
		errors = append(errors, "SMTP_HOST should not be 'localhost' in production")
	}

	if len(errors) > 0 {
		return fmt.Errorf("production configuration errors: %s", strings.Join(errors, "; "))
	}

	return nil
}

func (c *Config) IsDevelopment() bool {
	return c.Environment == "development"
}

func (c *Config) DatabaseDSN() string {
	return "host=" + c.DatabaseHost +
		" port=" + c.DatabasePort +
		" user=" + c.DatabaseUser +
		" password=" + c.DatabasePassword +
		" dbname=" + c.DatabaseName +
		" sslmode=" + c.DatabaseSSLMode
}

func (c *Config) DBConnMaxLifetimeDuration() time.Duration {
	duration, err := time.ParseDuration(c.DBConnMaxLifetime)
	if err != nil {
		return time.Hour
	}
	return duration
}

func (c *Config) JWTAccessTokenDurationParsed() time.Duration {
	duration, err := time.ParseDuration(c.JWTAccessTokenDuration)
	if err != nil {
		return 15 * time.Minute
	}
	return duration
}

func (c *Config) JWTRefreshTokenDurationParsed() time.Duration {
	duration, err := time.ParseDuration(c.JWTRefreshTokenDuration)
	if err != nil {
		return 7 * 24 * time.Hour
	}
	return duration
}

// IsProduction returns true if the environment is production
func (c *Config) IsProduction() bool {
	return c.Environment == "production"
}

// IsStaging returns true if the environment is staging
func (c *Config) IsStaging() bool {
	return c.Environment == "staging"
}

// IsTest returns true if the environment is test
func (c *Config) IsTest() bool {
	return c.Environment == "test"
}

// DBConnMaxIdleTimeDuration parses the DB connection max idle time duration
func (c *Config) DBConnMaxIdleTimeDuration() time.Duration {
	duration, err := time.ParseDuration(c.DBConnMaxIdleTime)
	if err != nil {
		return 30 * time.Minute
	}
	return duration
}

// CacheTTLDuration parses the cache TTL duration
func (c *Config) CacheTTLDuration() time.Duration {
	duration, err := time.ParseDuration(c.CacheTTL)
	if err != nil {
		return time.Hour
	}
	return duration
}

// GetCORSOrigins returns the CORS origins as a slice
func (c *Config) GetCORSOrigins() []string {
	if c.CORSOrigins == "" {
		return []string{"*"}
	}
	return strings.Split(c.CORSOrigins, ",")
}

// GetEmailConfig returns email configuration based on provider
func (c *Config) GetEmailConfig() map[string]interface{} {
	config := map[string]interface{}{
		"provider":   c.EmailProvider,
		"from":       c.EmailFrom,
		"from_name":  c.EmailFromName,
	}

	switch c.EmailProvider {
	case "smtp":
		config["host"] = c.SMTPHost
		config["port"] = c.SMTPPort
		config["username"] = c.SMTPUsername
		config["password"] = c.SMTPPassword
		config["use_tls"] = c.SMTPUseTLS
	case "sendgrid":
		config["api_key"] = c.SendGridAPIKey
	case "postmark":
		config["api_key"] = c.PostmarkAPIKey
	case "mailgun":
		config["api_key"] = c.MailgunAPIKey
		config["domain"] = c.MailgunDomain
	}

	return config
}

// IsFeatureEnabled checks if a specific feature is enabled
func (c *Config) IsFeatureEnabled(feature string) bool {
	switch feature {
	case "email_verification":
		return c.FeatureFlags.EmailVerification
	case "two_factor_auth":
		return c.FeatureFlags.TwoFactorAuth
	case "admin_api":
		return c.FeatureFlags.AdminAPI
	case "metrics":
		return c.FeatureFlags.Metrics
	case "file_uploads":
		return c.FeatureFlags.FileUploads
	case "social_login":
		return c.FeatureFlags.SocialLogin
	case "email_templates":
		return c.FeatureFlags.EmailTemplates
	case "rate_limiting":
		return c.FeatureFlags.RateLimiting
	case "csrf_protection":
		return c.FeatureFlags.CSRFProtection
	case "security_headers":
		return c.FeatureFlags.SecurityHeaders
	default:
		return false
	}
}

// GetDatabaseConfig returns database configuration map
func (c *Config) GetDatabaseConfig() map[string]interface{} {
	return map[string]interface{}{
		"host":               c.DatabaseHost,
		"port":               c.DatabasePort,
		"user":               c.DatabaseUser,
		"password":           c.DatabasePassword,
		"name":               c.DatabaseName,
		"ssl_mode":           c.DatabaseSSLMode,
		"max_idle_conns":     c.DBMaxIdleConns,
		"max_open_conns":     c.DBMaxOpenConns,
		"conn_max_lifetime":  c.DBConnMaxLifetime,
		"conn_max_idle_time": c.DBConnMaxIdleTime,
	}
}

// MaskSensitiveData returns a copy of the config with sensitive data masked
func (c *Config) MaskSensitiveData() *Config {
	masked := *c
	masked.DatabasePassword = "***"
	masked.JWTSecret = "***"
	masked.CSRFSecret = "***"
	masked.SMTPPassword = "***"
	masked.SendGridAPIKey = "***"
	masked.PostmarkAPIKey = "***"
	masked.MailgunAPIKey = "***"
	return &masked
}

package database

import (
	"context"
	"database/sql"
	"log/slog"
	"time"

	"gorm.io/driver/postgres"
	"gorm.io/gorm"
	gormlogger "gorm.io/gorm/logger"

	"github.com/acheevo/fullstack-template/internal/auth/domain"
	emaildomain "github.com/acheevo/fullstack-template/internal/shared/email/domain"
	"github.com/acheevo/fullstack-template/internal/shared/database/migrations"
	"github.com/acheevo/fullstack-template/internal/shared/database/seed"
)

type DB struct {
	*gorm.DB
	sqlDB    *sql.DB
	migrator *migrations.Migrator
	seeder   *seed.Seeder
	logger   *slog.Logger
}

func New(dsn string, isDevelopment bool, logger *slog.Logger, environment string) (*DB, error) {
	logLevel := gormlogger.Silent
	if isDevelopment {
		logLevel = gormlogger.Info
	}

	gormDB, err := gorm.Open(postgres.Open(dsn), &gorm.Config{
		Logger: gormlogger.Default.LogMode(logLevel),
	})
	if err != nil {
		return nil, err
	}

	sqlDB, err := gormDB.DB()
	if err != nil {
		return nil, err
	}

	db := &DB{
		DB:       gormDB,
		sqlDB:    sqlDB,
		migrator: migrations.NewMigrator(gormDB),
		seeder:   seed.NewSeeder(gormDB, environment),
		logger:   logger,
	}

	// Initialize migrations
	db.initializeMigrations()

	// Initialize seeders
	db.initializeSeeders()

	// Auto-migrate authentication tables (legacy support)
	if err := db.migrate(); err != nil {
		return nil, err
	}

	return db, nil
}

func (db *DB) SetConnectionPool(maxIdleConns, maxOpenConns int, maxLifetime time.Duration) error {
	db.sqlDB.SetMaxIdleConns(maxIdleConns)
	db.sqlDB.SetMaxOpenConns(maxOpenConns)
	db.sqlDB.SetConnMaxLifetime(maxLifetime)
	return nil
}

func (db *DB) Close() error {
	return db.sqlDB.Close()
}

func (db *DB) Ping() error {
	return db.sqlDB.Ping()
}

// migrate runs database migrations for all models (legacy support)
