	return db.AutoMigrate(
		&domain.User{},
		&domain.RefreshToken{},
		&domain.PasswordReset{},
		&domain.AuditLog{},
		&emaildomain.QueuedEmail{},
		&emaildomain.EmailDeliveryEvent{},
	)
}

