	// Check basic connectivity
	if err := db.Ping(); err != nil {
		return err
	}

	// Check if we can perform a simple query
	var result int
	if err := db.WithContext(ctx).Raw("SELECT 1").Scan(&result).Error; err != nil {
		return err
	}

	return nil
