package migrations

import "errors"

var (
	// ErrMigrationNotFound is returned when a migration is not found
	ErrMigrationNotFound = errors.New("migration not found")

	// ErrMigrationAlreadyApplied is returned when trying to apply an already applied migration
	ErrMigrationAlreadyApplied = errors.New("migration already applied")

	// ErrNoMigrationsToRollback is returned when there are no migrations to rollback
	ErrNoMigrationsToRollback = errors.New("no migrations to rollback")

	// ErrInvalidMigrationVersion is returned when migration version is invalid
	ErrInvalidMigrationVersion = errors.New("invalid migration version")
)

package migrations

import (
	"context"
	"time"

	"gorm.io/gorm"
)

// Migration represents a database migration
type Migration struct {
	ID          uint      `gorm:"primarykey"`
	Version     string    `gorm:"uniqueIndex;not null"`
	Description string    `gorm:"not null"`
	Applied     bool      `gorm:"not null;default:false"`
	AppliedAt   *time.Time
	Checksum    string    `gorm:"not null"`
	CreatedAt   time.Time
	UpdatedAt   time.Time
}

// MigrationFunc represents a function that performs a migration
type MigrationFunc func(ctx context.Context, db *gorm.DB) error

// MigrationDefinition defines a migration with its up and down functions
type MigrationDefinition struct {
	Version     string
	Description string
	Up          MigrationFunc
	Down        MigrationFunc
}

// Migrator handles database migrations
type Migrator struct {
	db         *gorm.DB
	migrations []MigrationDefinition
}

// NewMigrator creates a new migrator instance
func NewMigrator(db *gorm.DB) *Migrator {
	return &Migrator{
		db:         db,
		migrations: make([]MigrationDefinition, 0),
	}
}

// AddMigration adds a migration to the migrator
func (m *Migrator) AddMigration(migration MigrationDefinition) {
	m.migrations = append(m.migrations, migration)
}

// GetPendingMigrations returns migrations that haven't been applied
func (m *Migrator) GetPendingMigrations(ctx context.Context) ([]MigrationDefinition, error) {
	// Ensure migrations table exists
	if err := m.db.AutoMigrate(&Migration{}); err != nil {
		return nil, err
	}

	var appliedMigrations []Migration
	if err := m.db.WithContext(ctx).Where("applied = ?", true).Find(&appliedMigrations).Error; err != nil {
		return nil, err
	}

	appliedVersions := make(map[string]bool)
	for _, migration := range appliedMigrations {
		appliedVersions[migration.Version] = true
	}

	var pending []MigrationDefinition
	for _, migration := range m.migrations {
		if !appliedVersions[migration.Version] {
			pending = append(pending, migration)
		}
	}

	return pending, nil
}

// ApplyMigrations applies all pending migrations
func (m *Migrator) ApplyMigrations(ctx context.Context) error {
	pending, err := m.GetPendingMigrations(ctx)
	if err != nil {
		return err
	}

	for _, migration := range pending {
		if err := m.applyMigration(ctx, migration); err != nil {
			return err
		}
	}

	return nil
}

// applyMigration applies a single migration
func (m *Migrator) applyMigration(ctx context.Context, migration MigrationDefinition) error {
	return m.db.Transaction(func(tx *gorm.DB) error {
		// Run the migration
		if err := migration.Up(ctx, tx); err != nil {
			return err
		}

		// Record the migration as applied
		now := time.Now()
		migrationRecord := Migration{
			Version:     migration.Version,
			Description: migration.Description,
			Applied:     true,
			AppliedAt:   &now,
			Checksum:    generateChecksum(migration.Version + migration.Description),
		}

		return tx.WithContext(ctx).Create(&migrationRecord).Error
	})
}

// RollbackMigration rolls back the last applied migration
func (m *Migrator) RollbackMigration(ctx context.Context) error {
	var lastMigration Migration
	if err := m.db.WithContext(ctx).
		Where("applied = ?", true).
		Order("applied_at DESC").
		First(&lastMigration).Error; err != nil {
		return err
	}

	// Find the migration definition
	var migrationDef *MigrationDefinition
	for _, migration := range m.migrations {
		if migration.Version == lastMigration.Version {
			migrationDef = &migration
			break
		}
	}

	if migrationDef == nil {
		return ErrMigrationNotFound
	}

	return m.db.Transaction(func(tx *gorm.DB) error {
		// Run the rollback
		if err := migrationDef.Down(ctx, tx); err != nil {
			return err
		}

		// Mark migration as not applied
		return tx.WithContext(ctx).
			Model(&lastMigration).
			Updates(map[string]interface{}{
				"applied":    false,
				"applied_at": nil,
			}).Error
	})
}

// generateChecksum generates a simple checksum for migration tracking
func generateChecksum(data string) string {
	// Simple hash function for demonstration
	// In production, you might want to use a proper hash function
	hash := uint32(0)
	for _, c := range data {
		hash = hash*31 + uint32(c)
	}
	return string(rune(hash))
}

package seed

import "errors"

var (
	// ErrSeederNotFound is returned when a seeder is not found
	ErrSeederNotFound = errors.New("seeder not found")

	// ErrSeederAlreadyApplied is returned when trying to apply an already applied seeder
	ErrSeederAlreadyApplied = errors.New("seeder already applied")

	// ErrCircularDependency is returned when there's a circular dependency in seeders
	ErrCircularDependency = errors.New("circular dependency detected in seeders")

	// ErrDependencyNotFound is returned when a dependency seeder is not found
	ErrDependencyNotFound = errors.New("dependency seeder not found")
)

package seed

import (
	"context"
	"time"

	"gorm.io/gorm"
)

// Seed represents a database seed
type Seed struct {
	ID        uint      `gorm:"primarykey"`
	Name      string    `gorm:"uniqueIndex;not null"`
	Applied   bool      `gorm:"not null;default:false"`
	AppliedAt *time.Time
	Checksum  string    `gorm:"not null"`
	CreatedAt time.Time
	UpdatedAt time.Time
}

// SeederFunc represents a function that performs seeding
type SeederFunc func(ctx context.Context, db *gorm.DB) error

// SeederDefinition defines a seeder with its function
type SeederDefinition struct {
	Name        string
	Description string
	Fn          SeederFunc
	// Dependencies list other seeders this one depends on
	Dependencies []string
	// Environment specifies which environments this seeder should run in
	// If empty, runs in all environments
	Environment []string
}

// Seeder handles database seeding
type Seeder struct {
	db      *gorm.DB
	seeders map[string]SeederDefinition
	env     string
}

// NewSeeder creates a new seeder instance
func NewSeeder(db *gorm.DB, environment string) *Seeder {
	return &Seeder{
		db:      db,
		seeders: make(map[string]SeederDefinition),
		env:     environment,
	}
}

// AddSeeder adds a seeder to the seeder registry
func (s *Seeder) AddSeeder(seeder SeederDefinition) {
	s.seeders[seeder.Name] = seeder
}

// SeedAll runs all applicable seeders for the current environment
func (s *Seeder) SeedAll(ctx context.Context) error {
	// Ensure seeds table exists
	if err := s.db.AutoMigrate(&Seed{}); err != nil {
		return err
	}

	// Get all applicable seeders for current environment
	applicable := s.getApplicableSeeders()

	// Resolve dependencies and get execution order
	ordered, err := s.resolveDependencies(applicable)
	if err != nil {
		return err
	}

	// Get already applied seeds
	applied, err := s.getAppliedSeeds(ctx)
	if err != nil {
		return err
	}

	// Run pending seeders
	for _, seederName := range ordered {
		if applied[seederName] {
			continue
		}

		seeder := s.seeders[seederName]
		if err := s.runSeeder(ctx, seeder); err != nil {
			return err
		}
	}

	return nil
}

// SeedSpecific runs a specific seeder by name
func (s *Seeder) SeedSpecific(ctx context.Context, name string) error {
	seeder, exists := s.seeders[name]
	if !exists {
		return ErrSeederNotFound
	}

	// Check if already applied
	applied, err := s.getAppliedSeeds(ctx)
	if err != nil {
		return err
	}

	if applied[name] {
		return ErrSeederAlreadyApplied
	}

	return s.runSeeder(ctx, seeder)
}

// getApplicableSeeders returns seeders that should run in current environment
func (s *Seeder) getApplicableSeeders() []string {
	var applicable []string
	for name, seeder := range s.seeders {
		if len(seeder.Environment) == 0 {
			// Run in all environments
			applicable = append(applicable, name)
			continue
		}

		for _, env := range seeder.Environment {
			if env == s.env {
				applicable = append(applicable, name)
				break
			}
		}
	}
	return applicable
}

// resolveDependencies resolves seeder dependencies and returns execution order
func (s *Seeder) resolveDependencies(seeders []string) ([]string, error) {
	visited := make(map[string]bool)
	visiting := make(map[string]bool)
	var result []string

	var visit func(string) error
	visit = func(name string) error {
		if visiting[name] {
			return ErrCircularDependency
		}
		if visited[name] {
			return nil
		}

		visiting[name] = true

		seeder, exists := s.seeders[name]
		if !exists {
			return ErrSeederNotFound
		}

		for _, dep := range seeder.Dependencies {
			if err := visit(dep); err != nil {
				return err
			}
		}

		visiting[name] = false
		visited[name] = true
		result = append(result, name)

		return nil
	}

	for _, name := range seeders {
		if err := visit(name); err != nil {
			return nil, err
		}
	}

	return result, nil
}

// getAppliedSeeds returns a map of applied seed names
func (s *Seeder) getAppliedSeeds(ctx context.Context) (map[string]bool, error) {
	var seeds []Seed
	if err := s.db.WithContext(ctx).Where("applied = ?", true).Find(&seeds).Error; err != nil {
		return nil, err
	}

	applied := make(map[string]bool)
	for _, seed := range seeds {
		applied[seed.Name] = true
	}

	return applied, nil
}

// runSeeder executes a seeder and records it as applied
func (s *Seeder) runSeeder(ctx context.Context, seeder SeederDefinition) error {
	return s.db.Transaction(func(tx *gorm.DB) error {
		// Run the seeder
		if err := seeder.Fn(ctx, tx); err != nil {
			return err
		}

		// Record as applied
		now := time.Now()
		seedRecord := Seed{
			Name:      seeder.Name,
			Applied:   true,
			AppliedAt: &now,
			Checksum:  generateChecksum(seeder.Name + seeder.Description),
		}

		return tx.WithContext(ctx).Create(&seedRecord).Error
	})
}

// generateChecksum generates a simple checksum for seed tracking
func generateChecksum(data string) string {
	hash := uint32(0)
	for _, c := range data {
		hash = hash*31 + uint32(c)
	}
	return string(rune(hash))
}

