package providers

import (
	"context"
	"crypto/tls"
	"fmt"
	"io"
	"net/smtp"
	"time"

	"gopkg.in/gomail.v2"

	"github.com/acheevo/fullstack-template/internal/shared/config"
	"github.com/acheevo/fullstack-template/internal/shared/email/domain"
)

// SMTPProvider implements the EmailProvider interface for SMTP
type SMTPProvider struct {
	config *config.Config
	dialer *gomail.Dialer
}

// NewSMTPProvider creates a new SMTP email provider
func NewSMTPProvider(cfg *config.Config) *SMTPProvider {
	dialer := gomail.NewDialer(
		cfg.SMTPHost,
		cfg.SMTPPort,
		cfg.SMTPUsername,
		cfg.SMTPPassword,
	)

	// Configure TLS
	if cfg.SMTPUseTLS {
		dialer.TLSConfig = &tls.Config{
			ServerName:         cfg.SMTPHost,
			InsecureSkipVerify: cfg.IsDevelopment(), // Only skip in development
		}
	}

	// Set authentication method
	if cfg.SMTPUsername != "" && cfg.SMTPPassword != "" {
		dialer.Auth = smtp.PlainAuth("", cfg.SMTPUsername, cfg.SMTPPassword, cfg.SMTPHost)
	}

	return &SMTPProvider{
		config: cfg,
		dialer: dialer,
	}
}

// Send sends an email message via SMTP
func (p *SMTPProvider) Send(ctx context.Context, message *domain.EmailMessage) (*domain.EmailResult, error) {
	if p.dialer == nil {
		return nil, domain.ErrEmailProviderNotConfigured
	}

	// Create the email message
	m := gomail.NewMessage()

	// Set headers
	if message.FromName != "" {
		m.SetHeader("From", m.FormatAddress(message.From, message.FromName))
	} else {
		m.SetHeader("From", message.From)
	}

	m.SetHeader("To", message.To...)
	m.SetHeader("Subject", message.Subject)

	if len(message.CC) > 0 {
		m.SetHeader("Cc", message.CC...)
	}

	if len(message.BCC) > 0 {
		m.SetHeader("Bcc", message.BCC...)
	}

	if message.ReplyTo != "" {
		m.SetHeader("Reply-To", message.ReplyTo)
	}

	// Set custom headers
	for key, value := range message.Headers {
		m.SetHeader(key, value)
	}

	// Set message ID header for tracking
	m.SetHeader("Message-ID", fmt.Sprintf("<%s@%s>", message.ID, p.config.SMTPHost))

	// Set body
	if message.TextBody != "" {
		m.SetBody("text/plain", message.TextBody)
	}

	if message.HTMLBody != "" {
		if message.TextBody != "" {
			m.AddAlternative("text/html", message.HTMLBody)
		} else {
			m.SetBody("text/html", message.HTMLBody)
		}
	}

	// Add attachments
	for _, attachment := range message.Attachments {
		if attachment.Inline {
			m.Embed(attachment.Name, gomail.SetCopyFunc(func(w io.Writer) error {
				_, err := w.Write(attachment.Data)
				return err
			}))
		} else {
			m.Attach(attachment.Name, gomail.SetCopyFunc(func(w io.Writer) error {
				_, err := w.Write(attachment.Data)
				return err
			}))
		}
	}

	// Send with timeout
	done := make(chan error, 1)
	go func() {
		done <- p.dialer.DialAndSend(m)
	}()

	select {
	case err := <-done:
		if err != nil {
			return &domain.EmailResult{
				MessageID: message.ID,
				Status:    domain.StatusFailed,
				Message:   err.Error(),
			}, err
		}

		return &domain.EmailResult{
			MessageID: message.ID,
			Status:    domain.StatusSent,
			Message:   "Email sent successfully via SMTP",
			Metadata: map[string]string{
				"provider": string(domain.ProviderSMTP),
				"host":     p.config.SMTPHost,
				"sent_at":  time.Now().UTC().Format(time.RFC3339),
			},
		}, nil

	case <-ctx.Done():
		return &domain.EmailResult{
			MessageID: message.ID,
			Status:    domain.StatusFailed,
			Message:   "SMTP send timeout",
		}, ctx.Err()
	}
}

// SendTemplate sends an email using a template (SMTP doesn't support server-side templates)
func (p *SMTPProvider) SendTemplate(ctx context.Context, templateID string, to []string, variables map[string]interface{}) (*domain.EmailResult, error) {
	// SMTP doesn't support server-side templates, this should be handled by the template engine
	return nil, fmt.Errorf("SMTP provider does not support server-side templates, use the template engine")
}

// GetDeliveryStatus gets the delivery status of an email (SMTP doesn't support delivery tracking)
func (p *SMTPProvider) GetDeliveryStatus(ctx context.Context, messageID string) (*domain.EmailDeliveryStatus, error) {
	// SMTP doesn't provide delivery status tracking
	return &domain.EmailDeliveryStatus{
		MessageID: messageID,
		Status:    domain.StatusSent, // We can only confirm it was sent, not delivered
	}, nil
}

// SupportsTemplates returns whether this provider supports server-side templates
func (p *SMTPProvider) SupportsTemplates() bool {
	return false
}

// SupportsWebhooks returns whether this provider supports webhooks
func (p *SMTPProvider) SupportsWebhooks() bool {
	return false
}

// GetProviderName returns the provider name
func (p *SMTPProvider) GetProviderName() domain.EmailProvider {
	return domain.ProviderSMTP
}

// HealthCheck performs a health check on the SMTP connection
func (p *SMTPProvider) HealthCheck(ctx context.Context) error {
	if p.dialer == nil {
		return domain.ErrEmailProviderNotConfigured
	}

	// Try to establish a connection
	conn, err := p.dialer.Dial()
	if err != nil {
		return fmt.Errorf("SMTP health check failed: %w", err)
	}
	defer conn.Close()

	return nil
}

package queue

import (
	"context"
	"encoding/json"
	"fmt"
	"log/slog"
	"time"

	"github.com/google/uuid"
	"gorm.io/gorm"

	"github.com/acheevo/fullstack-template/internal/shared/email/domain"
)

// DatabaseQueue implements EmailQueueInterface using database storage
type DatabaseQueue struct {
	db     *gorm.DB
	logger *slog.Logger
}

// NewDatabaseQueue creates a new database-backed email queue
func NewDatabaseQueue(db *gorm.DB, logger *slog.Logger) *DatabaseQueue {
	return &DatabaseQueue{
		db:     db,
		logger: logger,
	}
}

// Enqueue adds an email message to the queue
func (q *DatabaseQueue) Enqueue(ctx context.Context, message *domain.EmailMessage) error {
	// Convert message to queued email
	queuedEmail, err := q.messageToQueuedEmail(message)
	if err != nil {
		return fmt.Errorf("failed to convert message to queued email: %w", err)
	}

	// Save to database
	if err := q.db.WithContext(ctx).Create(queuedEmail).Error; err != nil {
		q.logger.Error("failed to enqueue email", "error", err, "message_id", message.ID)
		return fmt.Errorf("failed to enqueue email: %w", err)
	}

	q.logger.Info("email enqueued successfully", 
		"message_id", message.ID, 
		"to", message.To, 
		"subject", message.Subject,
		"priority", message.Priority,
	)

	return nil
}

// Dequeue retrieves emails from the queue for processing
func (q *DatabaseQueue) Dequeue(ctx context.Context, limit int) ([]*domain.QueuedEmail, error) {
	var emails []*domain.QueuedEmail

	// Get emails ready for processing (pending or retrying, and scheduled time has passed)
	now := time.Now()
	err := q.db.WithContext(ctx).
		Where("status IN (?, ?) AND (scheduled_at IS NULL OR scheduled_at <= ?)", 
			domain.StatusPending, domain.StatusRetrying, now).
		Order("priority DESC, created_at ASC").
		Limit(limit).
		Find(&emails).Error

	if err != nil {
		q.logger.Error("failed to dequeue emails", "error", err)
		return nil, fmt.Errorf("failed to dequeue emails: %w", err)
	}

	// Mark emails as sending to prevent duplicate processing
	if len(emails) > 0 {
		var ids []string
		for _, email := range emails {
			ids = append(ids, email.ID)
		}

		err = q.db.WithContext(ctx).
			Model(&domain.QueuedEmail{}).
			Where("id IN ?", ids).
			Update("status", domain.StatusSending).Error

		if err != nil {
			q.logger.Error("failed to mark emails as sending", "error", err)
			return nil, fmt.Errorf("failed to mark emails as sending: %w", err)
		}
	}

	q.logger.Debug("dequeued emails for processing", "count", len(emails))
	return emails, nil
}

// MarkSent marks an email as successfully sent
func (q *DatabaseQueue) MarkSent(ctx context.Context, emailID string, result *domain.EmailResult) error {
	updates := map[string]interface{}{
		"status":  domain.StatusSent,
		"sent_at": time.Now(),
	}

	if result != nil {
		if result.Metadata != nil {
			metadataJSON, _ := json.Marshal(result.Metadata)
			updates["metadata"] = string(metadataJSON)
		}
	}

	err := q.db.WithContext(ctx).
		Model(&domain.QueuedEmail{}).
		Where("id = ?", emailID).
		Updates(updates).Error

	if err != nil {
		q.logger.Error("failed to mark email as sent", "error", err, "email_id", emailID)
		return fmt.Errorf("failed to mark email as sent: %w", err)
	}

	q.logger.Info("email marked as sent", "email_id", emailID)
	return nil
}

// MarkFailed marks an email as failed
func (q *DatabaseQueue) MarkFailed(ctx context.Context, emailID string, failureErr error) error {
	var queuedEmail domain.QueuedEmail
	if err := q.db.WithContext(ctx).Where("id = ?", emailID).First(&queuedEmail).Error; err != nil {
		return fmt.Errorf("failed to find email: %w", err)
	}

	queuedEmail.AttemptCount++
	queuedEmail.LastError = failureErr.Error()

	// Check if we should retry or mark as permanently failed
	if queuedEmail.AttemptCount >= queuedEmail.MaxRetries {
		queuedEmail.Status = domain.StatusFailed
		q.logger.Warn("email permanently failed after max retries", 
			"email_id", emailID, 
			"attempts", queuedEmail.AttemptCount,
			"error", failureErr.Error(),
		)
	} else {
		queuedEmail.Status = domain.StatusRetrying
		// Calculate exponential backoff for next retry
		backoffSeconds := calculateBackoff(queuedEmail.AttemptCount)
		nextRetry := time.Now().Add(time.Duration(backoffSeconds) * time.Second)
		queuedEmail.ScheduledAt = &nextRetry

		q.logger.Info("email scheduled for retry", 
			"email_id", emailID, 
			"attempt", queuedEmail.AttemptCount,
			"next_retry", nextRetry,
			"error", failureErr.Error(),
		)
	}

	if err := q.db.WithContext(ctx).Save(&queuedEmail).Error; err != nil {
		q.logger.Error("failed to update email failure status", "error", err, "email_id", emailID)
		return fmt.Errorf("failed to update email failure status: %w", err)
	}

	return nil
}

// RetryFailed retries failed emails that haven't exceeded max retries
func (q *DatabaseQueue) RetryFailed(ctx context.Context, maxRetries int) error {
	result := q.db.WithContext(ctx).
		Model(&domain.QueuedEmail{}).
		Where("status = ? AND attempt_count < ?", domain.StatusFailed, maxRetries).
		Updates(map[string]interface{}{
			"status":       domain.StatusPending,
			"scheduled_at": nil,
		})

	if result.Error != nil {
		q.logger.Error("failed to retry failed emails", "error", result.Error)
		return fmt.Errorf("failed to retry failed emails: %w", result.Error)
	}

	q.logger.Info("retried failed emails", "count", result.RowsAffected)
	return nil
}

// GetStats returns queue statistics
func (q *DatabaseQueue) GetStats(ctx context.Context) (*domain.QueueStats, error) {
	stats := &domain.QueueStats{}

	// Get counts for each status
	statusCounts := []struct {
		Status string
		Count  int64
	}{}

	err := q.db.WithContext(ctx).
		Model(&domain.QueuedEmail{}).
		Select("status, COUNT(*) as count").
		Group("status").
		Find(&statusCounts).Error

	if err != nil {
		return nil, fmt.Errorf("failed to get queue stats: %w", err)
	}

	// Map counts to stats struct
	for _, sc := range statusCounts {
		switch domain.EmailStatus(sc.Status) {
		case domain.StatusPending:
			stats.Pending = sc.Count
		case domain.StatusSending:
			stats.Sending = sc.Count
		case domain.StatusSent:
			stats.Sent = sc.Count
		case domain.StatusFailed:
			stats.Failed = sc.Count
		case domain.StatusRetrying:
			stats.Retrying = sc.Count
		}
	}

	// Count scheduled emails
	err = q.db.WithContext(ctx).
		Model(&domain.QueuedEmail{}).
		Where("scheduled_at > ?", time.Now()).
		Count(&stats.Scheduled).Error

	if err != nil {
		return nil, fmt.Errorf("failed to count scheduled emails: %w", err)
	}

	return stats, nil
}

// PurgeOld removes old emails from the queue
func (q *DatabaseQueue) PurgeOld(ctx context.Context, olderThan time.Duration) error {
	cutoff := time.Now().Add(-olderThan)

	result := q.db.WithContext(ctx).
		Where("created_at < ? AND status IN (?, ?)", cutoff, domain.StatusSent, domain.StatusFailed).
		Delete(&domain.QueuedEmail{})

	if result.Error != nil {
		q.logger.Error("failed to purge old emails", "error", result.Error)
		return fmt.Errorf("failed to purge old emails: %w", result.Error)
	}

	q.logger.Info("purged old emails", "count", result.RowsAffected, "older_than", olderThan)
	return nil
}

// messageToQueuedEmail converts an EmailMessage to a QueuedEmail
func (q *DatabaseQueue) messageToQueuedEmail(message *domain.EmailMessage) (*domain.QueuedEmail, error) {
	// Generate ID if not provided
	if message.ID == "" {
		message.ID = uuid.New().String()
	}

	// Marshal complex fields to JSON
	toJSON, _ := json.Marshal(message.To)
	ccJSON, _ := json.Marshal(message.CC)
	bccJSON, _ := json.Marshal(message.BCC)
	variablesJSON, _ := json.Marshal(message.Variables)
	attachmentsJSON, _ := json.Marshal(message.Attachments)
	headersJSON, _ := json.Marshal(message.Headers)
	tagsJSON, _ := json.Marshal(message.Tags)
	metadataJSON, _ := json.Marshal(message.Metadata)

	queuedEmail := &domain.QueuedEmail{
		ID:          uuid.New().String(),
		MessageID:   message.ID,
		From:        message.From,
		FromName:    message.FromName,
		To:          string(toJSON),
		CC:          string(ccJSON),
		BCC:         string(bccJSON),
		ReplyTo:     message.ReplyTo,
		Subject:     message.Subject,
		HTMLBody:    message.HTMLBody,
		TextBody:    message.TextBody,
		TemplateID:  message.TemplateID,
		Variables:   string(variablesJSON),
		Attachments: string(attachmentsJSON),
		Headers:     string(headersJSON),
		Tags:        string(tagsJSON),
		Metadata:    string(metadataJSON),
		Priority:    message.Priority,
		Status:      domain.StatusPending,
		MaxRetries:  3, // Default max retries
		ScheduledAt: message.ScheduledAt,
		CreatedAt:   time.Now(),
		UpdatedAt:   time.Now(),
	}

	return queuedEmail, nil
}

// QueuedEmailToMessage converts a QueuedEmail back to an EmailMessage
func (q *DatabaseQueue) QueuedEmailToMessage(queuedEmail *domain.QueuedEmail) (*domain.EmailMessage, error) {
	message := &domain.EmailMessage{
		ID:       queuedEmail.MessageID,
		From:     queuedEmail.From,
		FromName: queuedEmail.FromName,
		ReplyTo:  queuedEmail.ReplyTo,
		Subject:  queuedEmail.Subject,
		HTMLBody: queuedEmail.HTMLBody,
		TextBody: queuedEmail.TextBody,
		TemplateID: queuedEmail.TemplateID,
		Priority: queuedEmail.Priority,
		ScheduledAt: queuedEmail.ScheduledAt,
		CreatedAt: queuedEmail.CreatedAt,
	}

	// Unmarshal JSON fields
	if queuedEmail.To != "" {
		json.Unmarshal([]byte(queuedEmail.To), &message.To)
	}
	if queuedEmail.CC != "" {
		json.Unmarshal([]byte(queuedEmail.CC), &message.CC)
	}
	if queuedEmail.BCC != "" {
		json.Unmarshal([]byte(queuedEmail.BCC), &message.BCC)
	}
	if queuedEmail.Variables != "" {
		json.Unmarshal([]byte(queuedEmail.Variables), &message.Variables)
	}
	if queuedEmail.Attachments != "" {
		json.Unmarshal([]byte(queuedEmail.Attachments), &message.Attachments)
	}
	if queuedEmail.Headers != "" {
		json.Unmarshal([]byte(queuedEmail.Headers), &message.Headers)
	}
	if queuedEmail.Tags != "" {
		json.Unmarshal([]byte(queuedEmail.Tags), &message.Tags)
	}
	if queuedEmail.Metadata != "" {
		json.Unmarshal([]byte(queuedEmail.Metadata), &message.Metadata)
	}

	return message, nil
}

// calculateBackoff calculates exponential backoff delay in seconds
func calculateBackoff(attempt int) int {
	// Exponential backoff: 2^attempt minutes, capped at 60 minutes
	backoff := 1 << uint(attempt) // 2^attempt
	if backoff > 60 {
		backoff = 60
	}
	return backoff * 60 // Convert to seconds
}

package email

import (
	"context"
	"fmt"
	"log/slog"
	"time"

	"github.com/google/uuid"
	"gorm.io/gorm"

	"github.com/acheevo/fullstack-template/internal/shared/config"
	"github.com/acheevo/fullstack-template/internal/shared/email/domain"
	"github.com/acheevo/fullstack-template/internal/shared/email/providers"
	"github.com/acheevo/fullstack-template/internal/shared/email/queue"
	"github.com/acheevo/fullstack-template/internal/shared/email/templates"
)

// Service is the main email service implementation
type Service struct {
	config           *config.Config
	logger           *slog.Logger
	provider         domain.EmailProviderInterface
	queue            domain.EmailQueueInterface
	templateEngine   domain.EmailTemplateEngine
}

// NewService creates a new email service
func NewService(
	cfg *config.Config,
	logger *slog.Logger,
	db interface{}, // Can be *gorm.DB or other database interface
	templateEngine domain.EmailTemplateEngine,
) (*Service, error) {
	// Create email provider based on configuration
	provider, err := createProvider(cfg)
	if err != nil {
		return nil, fmt.Errorf("failed to create email provider: %w", err)
	}

	// Create queue (assuming database queue for now)
	var emailQueue domain.EmailQueueInterface
	if gormDB, ok := db.(interface{ DB() interface{} }); ok {
		// Extract gorm.DB from the wrapper
		if actualDB, ok := gormDB.DB().(*gorm.DB); ok {
			emailQueue = queue.NewDatabaseQueue(actualDB, logger)
		}
	}

	if emailQueue == nil {
		return nil, fmt.Errorf("failed to create email queue: unsupported database type")
	}

	// Use provided template engine or create default one
	if templateEngine == nil {
		templateEngine = templates.NewDefaultTemplateEngine(logger)
	}

	service := &Service{
		config:         cfg,
		logger:         logger,
		provider:       provider,
		queue:          emailQueue,
		templateEngine: templateEngine,
	}

	return service, nil
}

// Send queues an email for asynchronous sending
func (s *Service) Send(ctx context.Context, message *domain.EmailMessage) error {
	// Set default values
	if message.ID == "" {
		message.ID = uuid.New().String()
	}

	if message.From == "" {
		message.From = s.config.EmailFrom
	}

	if message.FromName == "" {
		message.FromName = s.config.EmailFromName
	}

	if message.Priority == 0 {
		message.Priority = domain.PriorityNormal
	}

	message.CreatedAt = time.Now()

	// Validate email
	if err := s.validateMessage(message); err != nil {
		return fmt.Errorf("message validation failed: %w", err)
	}

	// Enqueue the message
	if err := s.queue.Enqueue(ctx, message); err != nil {
		s.logger.Error("failed to enqueue email", "error", err, "message_id", message.ID)
		return fmt.Errorf("failed to enqueue email: %w", err)
	}

	s.logger.Info("email queued successfully", 
		"message_id", message.ID, 
		"to", message.To, 
		"subject", message.Subject,
	)

	return nil
}

// SendTemplate sends an email using a template
func (s *Service) SendTemplate(ctx context.Context, templateID string, to []string, variables map[string]interface{}) error {
	// Render the template
	rendered, err := s.templateEngine.Render(templateID, variables)
	if err != nil {
		return fmt.Errorf("failed to render template: %w", err)
	}

	// Create email message
	message := &domain.EmailMessage{
		ID:       uuid.New().String(),
		To:       to,
		Subject:  rendered.Subject,
		HTMLBody: rendered.HTMLBody,
		TextBody: rendered.TextBody,
		TemplateID: templateID,
		Variables:  variables,
		Priority: domain.PriorityNormal,
		Metadata: map[string]string{
			"template_id": templateID,
		},
	}

	return s.Send(ctx, message)
}

// SendImmediate sends an email immediately without queuing
func (s *Service) SendImmediate(ctx context.Context, message *domain.EmailMessage) (*domain.EmailResult, error) {
	// Set default values
	if message.ID == "" {
		message.ID = uuid.New().String()
	}

	if message.From == "" {
		message.From = s.config.EmailFrom
	}

	if message.FromName == "" {
		message.FromName = s.config.EmailFromName
	}

	// Validate email
	if err := s.validateMessage(message); err != nil {
		return nil, fmt.Errorf("message validation failed: %w", err)
	}

	// Send immediately using provider
	result, err := s.provider.Send(ctx, message)
	if err != nil {
		s.logger.Error("failed to send email immediately", "error", err, "message_id", message.ID)
		return result, fmt.Errorf("failed to send email: %w", err)
	}

	s.logger.Info("email sent immediately", 
		"message_id", message.ID, 
		"to", message.To, 
		"subject", message.Subject,
		"result", result.Status,
	)

	return result, nil
}

// Schedule schedules an email for future sending
func (s *Service) Schedule(ctx context.Context, message *domain.EmailMessage, scheduledAt time.Time) error {
	message.ScheduledAt = &scheduledAt
	return s.Send(ctx, message)
}

// RegisterTemplate registers a new email template
func (s *Service) RegisterTemplate(template *domain.EmailTemplate) error {
	return s.templateEngine.RegisterTemplate(template)
}

// GetTemplate retrieves a template by ID
func (s *Service) GetTemplate(templateID string) (*domain.EmailTemplate, error) {
	return s.templateEngine.GetTemplate(templateID)
}

// ProcessQueue processes emails in the queue
func (s *Service) ProcessQueue(ctx context.Context) error {
	batchSize := 10 // Process 10 emails at a time

	emails, err := s.queue.Dequeue(ctx, batchSize)
	if err != nil {
		return fmt.Errorf("failed to dequeue emails: %w", err)
	}

	if len(emails) == 0 {
		return nil // No emails to process
	}

	s.logger.Info("processing email queue", "batch_size", len(emails))

	for _, queuedEmail := range emails {
		// Convert queued email back to message
		message, err := s.queuedEmailToMessage(queuedEmail)
		if err != nil {
			s.logger.Error("failed to convert queued email to message", 
				"error", err, 
				"email_id", queuedEmail.ID,
			)
			s.queue.MarkFailed(ctx, queuedEmail.ID, err)
			continue
		}

		// Send the email
		result, err := s.provider.Send(ctx, message)
		if err != nil {
			s.logger.Error("failed to send email from queue", 
				"error", err, 
				"email_id", queuedEmail.ID,
				"message_id", message.ID,
			)
			s.queue.MarkFailed(ctx, queuedEmail.ID, err)
			continue
		}

		// Mark as sent
		if err := s.queue.MarkSent(ctx, queuedEmail.ID, result); err != nil {
			s.logger.Error("failed to mark email as sent", 
				"error", err, 
				"email_id", queuedEmail.ID,
			)
		}
	}

	return nil
}

// GetQueueStats returns queue statistics
func (s *Service) GetQueueStats(ctx context.Context) (*domain.QueueStats, error) {
	return s.queue.GetStats(ctx)
}

// GetDeliveryStatus gets the delivery status of an email
func (s *Service) GetDeliveryStatus(ctx context.Context, messageID string) (*domain.EmailDeliveryStatus, error) {
	return s.provider.GetDeliveryStatus(ctx, messageID)
}

// GetEmailStats returns email statistics (placeholder for now)
func (s *Service) GetEmailStats(ctx context.Context) (*domain.EmailStats, error) {
	// This would typically query a database for delivery events
	// For now, return empty stats
	return &domain.EmailStats{}, nil
}

// HealthCheck performs a health check on the email service
func (s *Service) HealthCheck(ctx context.Context) error {
	// Check provider health
	if healthChecker, ok := s.provider.(interface{ HealthCheck(context.Context) error }); ok {
		if err := healthChecker.HealthCheck(ctx); err != nil {
			return fmt.Errorf("provider health check failed: %w", err)
		}
	}

	// Check queue health (basic stats query)
	_, err := s.queue.GetStats(ctx)
	if err != nil {
		return fmt.Errorf("queue health check failed: %w", err)
	}

	return nil
}

