	case "sendgrid":
		// TODO: Implement SendGrid provider
		return nil, fmt.Errorf("SendGrid provider not implemented yet")
	case "postmark":
		// TODO: Implement Postmark provider
		return nil, fmt.Errorf("Postmark provider not implemented yet")
	case "mailgun":
