package errors

import (
	"context"
	"database/sql"
	"encoding/json"
	"errors"
	"fmt"
	"log/slog"
	"net/http"
	"runtime"
	"strings"

	"github.com/gin-gonic/gin"
	"gorm.io/gorm"
)

// ErrorHandler handles application errors and converts them to HTTP responses
type ErrorHandler struct {
	logger      *slog.Logger
	environment string
	mapper      *ErrorMapper
}

// NewErrorHandler creates a new error handler
func NewErrorHandler(logger *slog.Logger, environment string) *ErrorHandler {
	return &ErrorHandler{
		logger:      logger,
		environment: environment,
		mapper:      defaultErrorMapper,
	}
}

// HandleError processes an error and sends an appropriate HTTP response
func (h *ErrorHandler) HandleError(c *gin.Context, err error) {
	if err == nil {
		return
	}

	// Convert error to AppError
	appErr := h.toAppError(err)

	// Add trace ID if available
	if traceID := getTraceID(c); traceID != "" {
		appErr.TraceID = traceID
	}

	// Log the error with appropriate level
	h.logError(c, appErr)

	// Create response
	response := h.createErrorResponse(appErr)

	// Send HTTP response
	c.JSON(appErr.HTTPStatus, response)
}

// HandleErrorWithStatus handles an error with a specific HTTP status
func (h *ErrorHandler) HandleErrorWithStatus(c *gin.Context, err error, status int) {
	appErr := h.toAppError(err)
	appErr.HTTPStatus = status
	h.HandleError(c, appErr)
}

// toAppError converts any error to an AppError
func (h *ErrorHandler) toAppError(err error) *AppError {
	// If it's already an AppError, return it
	var appErr *AppError
	if errors.As(err, &appErr) {
		return appErr
	}

	// Handle specific error types
	switch {
	case errors.Is(err, gorm.ErrRecordNotFound):
		return NotFound("Resource not found")
	case errors.Is(err, sql.ErrNoRows):
		return NotFound("Resource not found")
	case errors.Is(err, context.DeadlineExceeded):
		return New(CodeTimeoutError, "Request timeout")
	case errors.Is(err, context.Canceled):
		return New(CodeTimeoutError, "Request canceled")
	default:
		// Check for common error patterns
		errMsg := err.Error()

		// Database connection errors
		if strings.Contains(errMsg, "connection refused") || 
		   strings.Contains(errMsg, "connection reset") ||
		   strings.Contains(errMsg, "database is locked") {
			return Wrap(err, CodeDatabaseError, "Database connection error")
		}

		// Validation errors (you might want to integrate with a validation library)
		if strings.Contains(errMsg, "validation") || 
		   strings.Contains(errMsg, "invalid") {
			return Wrap(err, CodeValidationFailed, "Validation failed")
		}

		// Default to internal error
		return Wrap(err, CodeInternalError, "An unexpected error occurred")
	}
}

// createErrorResponse creates an ErrorResponse from an AppError
func (h *ErrorHandler) createErrorResponse(appErr *AppError) *ErrorResponse {
	response := &ErrorResponse{
		Error:     appErr.Code.String(),
		Code:      appErr.Code,
		Timestamp: appErr.Timestamp,
		TraceID:   appErr.TraceID,
	}

	// Determine what message to show based on environment and user-friendliness
	if h.shouldShowDetails(appErr) {
		response.Message = appErr.Message
		response.Details = appErr.Details
		response.Context = appErr.Context
	} else {
		// Show generic message for internal errors
		mapping, exists := h.mapper.GetMapping(appErr.Code)
		if exists {
			response.Message = mapping.DefaultMessage
		} else {
			response.Message = "An error occurred"
		}
	}

	return response
}

// shouldShowDetails determines whether to show error details to the client
func (h *ErrorHandler) shouldShowDetails(appErr *AppError) bool {
	// Always show details in development
	if h.environment == "development" {
		return true
	}

	// Show details for user-friendly errors
	if appErr.UserFriendly {
		return true
	}

	// Hide details for internal errors in production
	return false
}

// logError logs the error with appropriate level and context
func (h *ErrorHandler) logError(c *gin.Context, appErr *AppError) {
	// Create log context
	logCtx := []interface{}{
		"error_code", appErr.Code,
		"http_status", appErr.HTTPStatus,
		"severity", appErr.Severity,
		"trace_id", appErr.TraceID,
		"method", c.Request.Method,
		"path", c.Request.URL.Path,
		"user_agent", c.Request.UserAgent(),
		"ip", c.ClientIP(),
	}

	// Add error context if available
	if appErr.Context != nil {
		contextJSON, _ := json.Marshal(appErr.Context)
		logCtx = append(logCtx, "error_context", string(contextJSON))
	}

	// Add request ID if available
	if requestID := c.GetString("request_id"); requestID != "" {
		logCtx = append(logCtx, "request_id", requestID)
	}

	// Add user ID if available
	if userID := c.GetString("user_id"); userID != "" {
		logCtx = append(logCtx, "user_id", userID)
	}

	// Add stack trace for high severity errors in development
	if (h.environment == "development" || appErr.Severity == SeverityCritical) && appErr.Cause != nil {
		logCtx = append(logCtx, "stack_trace", getStackTrace())
	}

	// Log with appropriate level based on severity
	message := fmt.Sprintf("Error handled: %s", appErr.Message)
	if appErr.Details != "" {
		message += fmt.Sprintf(" - %s", appErr.Details)
	}

	switch appErr.Severity {
	case SeverityLow:
		h.logger.Info(message, logCtx...)
	case SeverityMedium:
		h.logger.Warn(message, logCtx...)
	case SeverityHigh:
		h.logger.Error(message, logCtx...)
	case SeverityCritical:
		h.logger.Error(message, logCtx...)
		// Could also send to external error tracking service here
	default:
		h.logger.Error(message, logCtx...)
	}
}

// RecoveryMiddleware creates a middleware for panic recovery
func (h *ErrorHandler) RecoveryMiddleware() gin.HandlerFunc {
	return gin.CustomRecovery(func(c *gin.Context, recovered interface{}) {
		var err error

		switch r := recovered.(type) {
		case error:
			err = r
		case string:
			err = errors.New(r)
		default:
			err = fmt.Errorf("panic recovered: %v", r)
		}

		// Log the panic with stack trace
		h.logger.Error("Panic recovered",
			"error", err.Error(),
			"stack_trace", getStackTrace(),
			"method", c.Request.Method,
			"path", c.Request.URL.Path,
			"trace_id", getTraceID(c),
		)

		// Handle as critical internal error
		appErr := New(CodeInternalError, "Internal server error")
		appErr.Severity = SeverityCritical
		appErr.Cause = err

		h.HandleError(c, appErr)
	})
}

// ErrorReportingMiddleware adds error context to requests
func (h *ErrorHandler) ErrorReportingMiddleware() gin.HandlerFunc {
	return func(c *gin.Context) {
		// Add error handler to context so it can be used in handlers
		c.Set("error_handler", h)

		c.Next()

		// Check for errors in the context
		if len(c.Errors) > 0 {
			// Handle the last error
			lastError := c.Errors.Last()
			h.HandleError(c, lastError.Err)
		}
	}
}

// Helper functions

// getTraceID extracts trace ID from context
func getTraceID(c *gin.Context) string {
	if traceID := c.GetString("trace_id"); traceID != "" {
		return traceID
	}
	if traceID := c.GetHeader("X-Trace-ID"); traceID != "" {
		return traceID
	}
	return ""
}

// getStackTrace returns the current stack trace
func getStackTrace() string {
	buf := make([]byte, 1024*4)
	n := runtime.Stack(buf, false)
	return string(buf[:n])
}

// String method for ErrorCode
func (ec ErrorCode) String() string {
	return string(ec)
}

// Convenience functions for handlers

// HandleGinError is a convenience function for Gin handlers
func HandleGinError(c *gin.Context, err error) {
	if handler, exists := c.Get("error_handler"); exists {
		if h, ok := handler.(*ErrorHandler); ok {
			h.HandleError(c, err)
			return
		}
	}

	// Fallback to basic error handling
	c.JSON(http.StatusInternalServerError, gin.H{
		"error": "Internal server error",
		"code":  CodeInternalError,
	})
}

// MustGetErrorHandler gets the error handler from Gin context or panics
func MustGetErrorHandler(c *gin.Context) *ErrorHandler {
	handler, exists := c.Get("error_handler")
	if !exists {
		panic("error handler not found in context")
	}

	h, ok := handler.(*ErrorHandler)
	if !ok {
		panic("invalid error handler type in context")
	}

	return h
}

// AbortWithError aborts the request with an error
func AbortWithError(c *gin.Context, err error) {
	HandleGinError(c, err)
	c.Abort()
}

// AbortWithAppError aborts the request with an AppError
func AbortWithAppError(c *gin.Context, appErr *AppError) {
	HandleGinError(c, appErr)
	c.Abort()
}

// ErrorMiddleware creates a comprehensive error handling middleware
func ErrorMiddleware(logger *slog.Logger, environment string) gin.HandlerFunc {
	handler := NewErrorHandler(logger, environment)

	return gin.HandlerFunc(func(c *gin.Context) {
		// Add error handler to context
		c.Set("error_handler", handler)

		// Continue with request
		c.Next()

		// Handle any errors that occurred during request processing
		if len(c.Errors) > 0 {
			// Get the last error (most recent)
			lastError := c.Errors.Last()

			// Only handle if response hasn't been written yet
			if !c.Writer.Written() {
				handler.HandleError(c, lastError.Err)
			}
		}
	})
}

package errors

import (
	"fmt"
	"net/http"
	"time"
)

// ErrorCode represents a unique error code
type ErrorCode string

// Standard error codes
const (
	// Client errors (4xx)
	CodeBadRequest          ErrorCode = "BAD_REQUEST"
	CodeUnauthorized        ErrorCode = "UNAUTHORIZED"
	CodeForbidden           ErrorCode = "FORBIDDEN"
	CodeNotFound            ErrorCode = "NOT_FOUND"
	CodeConflict            ErrorCode = "CONFLICT"
	CodeValidationFailed    ErrorCode = "VALIDATION_FAILED"
	CodeRateLimitExceeded   ErrorCode = "RATE_LIMIT_EXCEEDED"
	CodeRequestTooLarge     ErrorCode = "REQUEST_TOO_LARGE"
	CodeUnsupportedMedia    ErrorCode = "UNSUPPORTED_MEDIA_TYPE"

	// Authentication & Authorization
	CodeInvalidCredentials  ErrorCode = "INVALID_CREDENTIALS"
	CodeTokenExpired        ErrorCode = "TOKEN_EXPIRED"
	CodeTokenInvalid        ErrorCode = "TOKEN_INVALID"
	CodeEmailNotVerified    ErrorCode = "EMAIL_NOT_VERIFIED"
	CodeAccountLocked       ErrorCode = "ACCOUNT_LOCKED"
	CodePermissionDenied    ErrorCode = "PERMISSION_DENIED"

	// Resource errors
	CodeUserNotFound        ErrorCode = "USER_NOT_FOUND"
	CodeUserAlreadyExists   ErrorCode = "USER_ALREADY_EXISTS"
	CodeEmailAlreadyExists  ErrorCode = "EMAIL_ALREADY_EXISTS"
	CodeResourceNotFound    ErrorCode = "RESOURCE_NOT_FOUND"
	CodeResourceConflict    ErrorCode = "RESOURCE_CONFLICT"

	// Business logic errors
	CodeInvalidOperation    ErrorCode = "INVALID_OPERATION"
	CodeOperationFailed     ErrorCode = "OPERATION_FAILED"
	CodeDataInconsistent    ErrorCode = "DATA_INCONSISTENT"
	CodeBusinessRuleViolation ErrorCode = "BUSINESS_RULE_VIOLATION"

	// Server errors (5xx)
	CodeInternalError       ErrorCode = "INTERNAL_ERROR"
	CodeServiceUnavailable  ErrorCode = "SERVICE_UNAVAILABLE"
	CodeDatabaseError       ErrorCode = "DATABASE_ERROR"
	CodeExternalServiceError ErrorCode = "EXTERNAL_SERVICE_ERROR"
	CodeConfigurationError  ErrorCode = "CONFIGURATION_ERROR"
	CodeTimeoutError        ErrorCode = "TIMEOUT_ERROR"

	// Email errors
	CodeEmailSendFailed     ErrorCode = "EMAIL_SEND_FAILED"
	CodeEmailConfigError    ErrorCode = "EMAIL_CONFIG_ERROR"
	CodeTemplateNotFound    ErrorCode = "TEMPLATE_NOT_FOUND"
	CodeTemplateRenderError ErrorCode = "TEMPLATE_RENDER_ERROR"
)

// ErrorSeverity represents the severity level of an error
type ErrorSeverity string

const (
	SeverityLow      ErrorSeverity = "low"
	SeverityMedium   ErrorSeverity = "medium"
	SeverityHigh     ErrorSeverity = "high"
	SeverityCritical ErrorSeverity = "critical"
)

// AppError represents a structured application error
type AppError struct {
	Code       ErrorCode              `json:"code"`
	Message    string                 `json:"message"`
	Details    string                 `json:"details,omitempty"`
	Cause      error                  `json:"-"`
	Context    map[string]interface{} `json:"context,omitempty"`
	HTTPStatus int                    `json:"-"`
	Severity   ErrorSeverity          `json:"-"`
	Timestamp  time.Time              `json:"timestamp"`
	TraceID    string                 `json:"trace_id,omitempty"`
	UserFriendly bool                 `json:"-"`
}

// Error implements the error interface
func (e *AppError) Error() string {
	if e.Details != "" {
		return fmt.Sprintf("%s: %s - %s", e.Code, e.Message, e.Details)
	}
	return fmt.Sprintf("%s: %s", e.Code, e.Message)
}

// Unwrap returns the underlying cause error
func (e *AppError) Unwrap() error {
	return e.Cause
}

// WithContext adds context information to the error
func (e *AppError) WithContext(key string, value interface{}) *AppError {
	if e.Context == nil {
		e.Context = make(map[string]interface{})
	}
	e.Context[key] = value
	return e
}

// WithDetails adds additional details to the error
func (e *AppError) WithDetails(details string) *AppError {
	e.Details = details
	return e
}

// WithCause sets the underlying cause error
func (e *AppError) WithCause(cause error) *AppError {
	e.Cause = cause
	return e
}

// WithTraceID sets the trace ID for error tracking
func (e *AppError) WithTraceID(traceID string) *AppError {
	e.TraceID = traceID
	return e
}

// IsUserFriendly returns whether the error message is safe to show to users
func (e *AppError) IsUserFriendly() bool {
	return e.UserFriendly
}

// ErrorResponse represents the structure of error responses sent to clients
type ErrorResponse struct {
	Error     string                 `json:"error"`
	Code      ErrorCode              `json:"code"`
	Message   string                 `json:"message"`
	Details   string                 `json:"details,omitempty"`
	Context   map[string]interface{} `json:"context,omitempty"`
	Timestamp time.Time              `json:"timestamp"`
	TraceID   string                 `json:"trace_id,omitempty"`
}

// ValidationError represents a validation error with field-specific details
type ValidationError struct {
	*AppError
	Fields map[string]string `json:"fields"`
}

// NewValidationError creates a new validation error
func NewValidationError(message string, fields map[string]string) *ValidationError {
	return &ValidationError{
		AppError: &AppError{
			Code:         CodeValidationFailed,
			Message:      message,
			HTTPStatus:   http.StatusBadRequest,
			Severity:     SeverityLow,
			Timestamp:    time.Now(),
			UserFriendly: true,
		},
		Fields: fields,
	}
}

// ErrorWithFields represents an error with additional field information
type ErrorWithFields struct {
	*AppError
	Fields map[string]interface{} `json:"fields,omitempty"`
}

// NewErrorWithFields creates a new error with field information
func NewErrorWithFields(code ErrorCode, message string, fields map[string]interface{}) *ErrorWithFields {
	return &ErrorWithFields{
		AppError: New(code, message),
		Fields:   fields,
	}
}

// ErrorMapper maps error codes to HTTP status codes and default messages
type ErrorMapper struct {
	mappings map[ErrorCode]ErrorMapping
}

// ErrorMapping defines the mapping for an error code
type ErrorMapping struct {
	HTTPStatus   int
	DefaultMessage string
	Severity     ErrorSeverity
	UserFriendly bool
}

// NewErrorMapper creates a new error mapper with default mappings
func NewErrorMapper() *ErrorMapper {
	mapper := &ErrorMapper{
		mappings: make(map[ErrorCode]ErrorMapping),
	}

	// Register default mappings
	mapper.registerDefaultMappings()

	return mapper
}

// registerDefaultMappings registers the default error code mappings
func (em *ErrorMapper) registerDefaultMappings() {
	mappings := map[ErrorCode]ErrorMapping{
		// Client errors (4xx)
		CodeBadRequest:        {http.StatusBadRequest, "Bad request", SeverityLow, true},
		CodeUnauthorized:      {http.StatusUnauthorized, "Authentication required", SeverityMedium, true},
		CodeForbidden:         {http.StatusForbidden, "Access forbidden", SeverityMedium, true},
		CodeNotFound:          {http.StatusNotFound, "Resource not found", SeverityLow, true},
		CodeConflict:          {http.StatusConflict, "Resource conflict", SeverityLow, true},
		CodeValidationFailed:  {http.StatusBadRequest, "Validation failed", SeverityLow, true},
		CodeRateLimitExceeded: {http.StatusTooManyRequests, "Rate limit exceeded", SeverityMedium, true},
		CodeRequestTooLarge:   {http.StatusRequestEntityTooLarge, "Request too large", SeverityLow, true},
		CodeUnsupportedMedia:  {http.StatusUnsupportedMediaType, "Unsupported media type", SeverityLow, true},

		// Authentication & Authorization
		CodeInvalidCredentials: {http.StatusUnauthorized, "Invalid credentials", SeverityMedium, true},
		CodeTokenExpired:       {http.StatusUnauthorized, "Token expired", SeverityLow, true},
		CodeTokenInvalid:       {http.StatusUnauthorized, "Invalid token", SeverityMedium, true},
		CodeEmailNotVerified:   {http.StatusForbidden, "Email not verified", SeverityMedium, true},
		CodeAccountLocked:      {http.StatusForbidden, "Account locked", SeverityHigh, true},
		CodePermissionDenied:   {http.StatusForbidden, "Permission denied", SeverityMedium, true},

		// Resource errors
		CodeUserNotFound:       {http.StatusNotFound, "User not found", SeverityLow, true},
		CodeUserAlreadyExists:  {http.StatusConflict, "User already exists", SeverityLow, true},
		CodeEmailAlreadyExists: {http.StatusConflict, "Email already exists", SeverityLow, true},
		CodeResourceNotFound:   {http.StatusNotFound, "Resource not found", SeverityLow, true},
		CodeResourceConflict:   {http.StatusConflict, "Resource conflict", SeverityLow, true},

		// Business logic errors
		CodeInvalidOperation:       {http.StatusBadRequest, "Invalid operation", SeverityMedium, true},
		CodeOperationFailed:        {http.StatusUnprocessableEntity, "Operation failed", SeverityMedium, true},
		CodeDataInconsistent:       {http.StatusInternalServerError, "Data inconsistent", SeverityHigh, false},
		CodeBusinessRuleViolation:  {http.StatusBadRequest, "Business rule violation", SeverityMedium, true},

		// Server errors (5xx)
		CodeInternalError:        {http.StatusInternalServerError, "Internal server error", SeverityCritical, false},
		CodeServiceUnavailable:   {http.StatusServiceUnavailable, "Service unavailable", SeverityHigh, true},
		CodeDatabaseError:        {http.StatusInternalServerError, "Database error", SeverityHigh, false},
		CodeExternalServiceError: {http.StatusBadGateway, "External service error", SeverityHigh, false},
		CodeConfigurationError:   {http.StatusInternalServerError, "Configuration error", SeverityCritical, false},
		CodeTimeoutError:         {http.StatusGatewayTimeout, "Request timeout", SeverityMedium, true},

		// Email errors
		CodeEmailSendFailed:     {http.StatusInternalServerError, "Email send failed", SeverityMedium, false},
		CodeEmailConfigError:    {http.StatusInternalServerError, "Email configuration error", SeverityHigh, false},
		CodeTemplateNotFound:    {http.StatusInternalServerError, "Email template not found", SeverityMedium, false},
		CodeTemplateRenderError: {http.StatusInternalServerError, "Template render error", SeverityMedium, false},
	}

	for code, mapping := range mappings {
		em.mappings[code] = mapping
	}
}

// GetMapping returns the mapping for an error code
func (em *ErrorMapper) GetMapping(code ErrorCode) (ErrorMapping, bool) {
	mapping, exists := em.mappings[code]
	return mapping, exists
}

// RegisterMapping registers a custom error code mapping
func (em *ErrorMapper) RegisterMapping(code ErrorCode, mapping ErrorMapping) {
	em.mappings[code] = mapping
}

// Global error mapper instance
var defaultErrorMapper = NewErrorMapper()

// Factory functions for creating common errors

// New creates a new AppError with the given code and message
func New(code ErrorCode, message string) *AppError {
	mapping, exists := defaultErrorMapper.GetMapping(code)
	if !exists {
		mapping = ErrorMapping{
			HTTPStatus:   http.StatusInternalServerError,
			Severity:     SeverityMedium,
			UserFriendly: false,
		}
	}

	if message == "" {
		message = mapping.DefaultMessage
	}

	return &AppError{
		Code:         code,
		Message:      message,
		HTTPStatus:   mapping.HTTPStatus,
		Severity:     mapping.Severity,
		Timestamp:    time.Now(),
		UserFriendly: mapping.UserFriendly,
	}
}

// Newf creates a new AppError with formatted message
func Newf(code ErrorCode, format string, args ...interface{}) *AppError {
	return New(code, fmt.Sprintf(format, args...))
}

// Wrap wraps an existing error with an AppError
func Wrap(err error, code ErrorCode, message string) *AppError {
	appErr := New(code, message)
	appErr.Cause = err
	return appErr
}

// Wrapf wraps an existing error with an AppError and formatted message
func Wrapf(err error, code ErrorCode, format string, args ...interface{}) *AppError {
	return Wrap(err, code, fmt.Sprintf(format, args...))
}

// Common error factory functions

// BadRequest creates a bad request error
func BadRequest(message string) *AppError {
	return New(CodeBadRequest, message)
}

// Unauthorized creates an unauthorized error
func Unauthorized(message string) *AppError {
	return New(CodeUnauthorized, message)
}

// Forbidden creates a forbidden error
func Forbidden(message string) *AppError {
	return New(CodeForbidden, message)
}

// NotFound creates a not found error
func NotFound(message string) *AppError {
	return New(CodeNotFound, message)
}

// Conflict creates a conflict error
func Conflict(message string) *AppError {
	return New(CodeConflict, message)
}

// InternalError creates an internal server error
func InternalError(message string) *AppError {
	return New(CodeInternalError, message)
}

// ValidationFailed creates a validation error
func ValidationFailed(message string) *AppError {
	return New(CodeValidationFailed, message)
}

// DatabaseError creates a database error
func DatabaseError(err error) *AppError {
	return Wrap(err, CodeDatabaseError, "Database operation failed")
}

// ExternalServiceError creates an external service error
func ExternalServiceError(service string, err error) *AppError {
	return Wrap(err, CodeExternalServiceError, fmt.Sprintf("External service '%s' error", service))
}

package health

import (
	"context"
	"fmt"
	"log/slog"
	"runtime"
	"sync"
	"time"

	"gorm.io/gorm"

	"github.com/acheevo/fullstack-template/internal/shared/config"
	"github.com/acheevo/fullstack-template/internal/shared/email/domain"
)

// Status represents the health status
type Status string

const (
	StatusHealthy   Status = "healthy"
	StatusUnhealthy Status = "unhealthy"
	StatusDegraded  Status = "degraded"
	StatusUnknown   Status = "unknown"
)

// CheckResult represents the result of a health check
type CheckResult struct {
	Name        string                 `json:"name"`
	Status      Status                 `json:"status"`
	Message     string                 `json:"message,omitempty"`
	Duration    time.Duration          `json:"duration"`
	Timestamp   time.Time              `json:"timestamp"`
	Details     map[string]interface{} `json:"details,omitempty"`
	Error       error                  `json:"-"`
}

// HealthReport represents the overall health report
type HealthReport struct {
	Status     Status                  `json:"status"`
	Timestamp  time.Time               `json:"timestamp"`
	Duration   time.Duration           `json:"duration"`
	Version    string                  `json:"version"`
	Checks     map[string]*CheckResult `json:"checks"`
	Summary    HealthSummary           `json:"summary"`
}

// HealthSummary provides a summary of health checks
type HealthSummary struct {
	Total     int `json:"total"`
	Healthy   int `json:"healthy"`
	Unhealthy int `json:"unhealthy"`
	Degraded  int `json:"degraded"`
	Unknown   int `json:"unknown"`
}

// HealthChecker defines the interface for health checks
type HealthChecker interface {
	Name() string
	Check(ctx context.Context) *CheckResult
}

// EnhancedHealthService provides comprehensive health checking
type EnhancedHealthService struct {
	config   *config.Config
	logger   *slog.Logger
	checkers map[string]HealthChecker
	mu       sync.RWMutex
}

// NewEnhancedHealthService creates a new enhanced health service
func NewEnhancedHealthService(config *config.Config, logger *slog.Logger) *EnhancedHealthService {
	service := &EnhancedHealthService{
		config:   config,
		logger:   logger,
		checkers: make(map[string]HealthChecker),
	}

	return service
}

// RegisterChecker registers a health checker
func (h *EnhancedHealthService) RegisterChecker(checker HealthChecker) {
	h.mu.Lock()
	defer h.mu.Unlock()

	h.checkers[checker.Name()] = checker
	h.logger.Info("Health checker registered", "name", checker.Name())
}

// Check performs all health checks and returns a comprehensive report
func (h *EnhancedHealthService) Check(ctx context.Context) *HealthReport {
	start := time.Now()

	h.mu.RLock()
	checkers := make(map[string]HealthChecker, len(h.checkers))
	for name, checker := range h.checkers {
		checkers[name] = checker
	}
	h.mu.RUnlock()

	// Perform checks concurrently
	results := make(chan *CheckResult, len(checkers))
	var wg sync.WaitGroup

	for _, checker := range checkers {
		wg.Add(1)
		go func(c HealthChecker) {
			defer wg.Done()
			result := c.Check(ctx)
			results <- result
		}(checker)
	}

	// Wait for all checks to complete
	go func() {
		wg.Wait()
		close(results)
	}()

	// Collect results
	checks := make(map[string]*CheckResult)
	summary := HealthSummary{}

	for result := range results {
		checks[result.Name] = result
		summary.Total++

		switch result.Status {
		case StatusHealthy:
			summary.Healthy++
		case StatusUnhealthy:
			summary.Unhealthy++
		case StatusDegraded:
			summary.Degraded++
		default:
			summary.Unknown++
		}
	}

	// Determine overall status
	overallStatus := h.determineOverallStatus(summary)

	report := &HealthReport{
		Status:    overallStatus,
		Timestamp: time.Now(),
		Duration:  time.Since(start),
		Version:   h.config.Version,
		Checks:    checks,
		Summary:   summary,
	}

	h.logger.Info("Health check completed",
		"status", overallStatus,
		"duration", report.Duration,
		"total_checks", summary.Total,
		"healthy", summary.Healthy,
		"unhealthy", summary.Unhealthy,
	)

	return report
}

// CheckSingle performs a single health check by name
func (h *EnhancedHealthService) CheckSingle(ctx context.Context, name string) *CheckResult {
	h.mu.RLock()
	checker, exists := h.checkers[name]
	h.mu.RUnlock()

	if !exists {
		return &CheckResult{
			Name:      name,
			Status:    StatusUnknown,
			Message:   "Health checker not found",
			Timestamp: time.Now(),
		}
	}

	return checker.Check(ctx)
}

// ListCheckers returns the names of all registered health checkers
func (h *EnhancedHealthService) ListCheckers() []string {
	h.mu.RLock()
	defer h.mu.RUnlock()

	names := make([]string, 0, len(h.checkers))
	for name := range h.checkers {
		names = append(names, name)
	}

	return names
}

// determineOverallStatus determines the overall health status
func (h *EnhancedHealthService) determineOverallStatus(summary HealthSummary) Status {
	if summary.Total == 0 {
		return StatusUnknown
	}

	// If any critical checks are unhealthy, overall is unhealthy
	if summary.Unhealthy > 0 {
		return StatusUnhealthy
	}

	// If any checks are degraded, overall is degraded
	if summary.Degraded > 0 {
		return StatusDegraded
	}

	// If all checks are healthy, overall is healthy
	if summary.Healthy == summary.Total {
		return StatusHealthy
	}

	// Otherwise, unknown
	return StatusUnknown
}

// Built-in health checkers

// DatabaseHealthChecker checks database connectivity
type DatabaseHealthChecker struct {
	name string
	db   *gorm.DB
}

// NewDatabaseHealthChecker creates a new database health checker
func NewDatabaseHealthChecker(name string, db *gorm.DB) *DatabaseHealthChecker {
	return &DatabaseHealthChecker{
		name: name,
		db:   db,
	}
}

// Name returns the checker name
func (d *DatabaseHealthChecker) Name() string {
	return d.name
}

// Check performs the database health check
func (d *DatabaseHealthChecker) Check(ctx context.Context) *CheckResult {
	start := time.Now()
	result := &CheckResult{
		Name:      d.name,
		Timestamp: time.Now(),
		Details:   make(map[string]interface{}),
	}

	// Check database connection
	sqlDB, err := d.db.DB()
	if err != nil {
		result.Status = StatusUnhealthy
		result.Message = "Failed to get database connection"
		result.Error = err
		result.Duration = time.Since(start)
		return result
	}

	// Ping database
	if err := sqlDB.PingContext(ctx); err != nil {
		result.Status = StatusUnhealthy
		result.Message = "Database ping failed"
		result.Error = err
		result.Duration = time.Since(start)
		return result
	}

	// Get connection stats
	stats := sqlDB.Stats()
	result.Details["connections_open"] = stats.OpenConnections
	result.Details["connections_in_use"] = stats.InUse
	result.Details["connections_idle"] = stats.Idle
	result.Details["max_open_connections"] = stats.MaxOpenConnections
	result.Details["max_idle_connections"] = stats.MaxIdleClosed

	// Check if we're approaching connection limits
	connectionUsage := float64(stats.OpenConnections) / float64(stats.MaxOpenConnections)
	if connectionUsage > 0.9 {
		result.Status = StatusDegraded
		result.Message = fmt.Sprintf("High connection usage: %.1f%%", connectionUsage*100)
	} else {
		result.Status = StatusHealthy
		result.Message = "Database connection healthy"
	}

	result.Duration = time.Since(start)
	return result
}

// EmailHealthChecker checks email service health
type EmailHealthChecker struct {
	name         string
	emailService domain.EmailServiceInterface
}

// NewEmailHealthChecker creates a new email health checker
func NewEmailHealthChecker(name string, emailService domain.EmailServiceInterface) *EmailHealthChecker {
	return &EmailHealthChecker{
		name:         name,
		emailService: emailService,
	}
}

// Name returns the checker name
func (e *EmailHealthChecker) Name() string {
	return e.name
}

// Check performs the email service health check
func (e *EmailHealthChecker) Check(ctx context.Context) *CheckResult {
	start := time.Now()
	result := &CheckResult{
		Name:      e.name,
		Timestamp: time.Now(),
		Details:   make(map[string]interface{}),
	}

	// Check email service health
	if err := e.emailService.HealthCheck(ctx); err != nil {
		result.Status = StatusUnhealthy
		result.Message = "Email service health check failed"
		result.Error = err
		result.Duration = time.Since(start)
		return result
	}

	// Get queue stats
	queueStats, err := e.emailService.GetQueueStats(ctx)
	if err != nil {
		result.Status = StatusDegraded
		result.Message = "Failed to get email queue stats"
		result.Error = err
	} else {
		result.Details["emails_pending"] = queueStats.Pending
		result.Details["emails_sending"] = queueStats.Sending
		result.Details["emails_failed"] = queueStats.Failed

		// Check if queue is backing up
		if queueStats.Pending > 1000 {
			result.Status = StatusDegraded
			result.Message = fmt.Sprintf("Email queue backing up: %d pending", queueStats.Pending)
		} else {
			result.Status = StatusHealthy
			result.Message = "Email service healthy"
		}
	}

	result.Duration = time.Since(start)
	return result
}

