// SystemMetricsCollector collects system-level metrics
type SystemMetricsCollector struct {
	metricsCollector metrics.MetricsCollector
	defaultMetrics   *metrics.DefaultMetrics
	logger           *slog.Logger
}

// NewSystemMetricsCollector creates a new system metrics collector
func NewSystemMetricsCollector(metricsCollector metrics.MetricsCollector, logger *slog.Logger) *SystemMetricsCollector {
	return &SystemMetricsCollector{
		metricsCollector: metricsCollector,
		defaultMetrics:   metrics.GetDefaultMetrics(),
		logger:           logger,
	}
}

// StartSystemMetricsCollection starts collecting system metrics
func (s *SystemMetricsCollector) StartSystemMetricsCollection() {
	ticker := time.NewTicker(30 * time.Second) // Collect every 30 seconds
	go func() {
		defer ticker.Stop()
		for {
			select {
			case <-ticker.C:
				s.collectSystemMetrics()
			}
		}
	}()
}

// collectSystemMetrics collects various system metrics
func (s *SystemMetricsCollector) collectSystemMetrics() {
	// This would integrate with actual system monitoring
	// For now, we'll collect some basic Go runtime metrics

	// Memory stats
	var m runtime.MemStats
	runtime.ReadMemStats(&m)

	s.metricsCollector.SetGauge(s.defaultMetrics.System.MemoryUsage, float64(m.Alloc), nil)
	s.metricsCollector.SetGauge(s.defaultMetrics.System.GoroutinesCount, float64(runtime.NumGoroutine()), nil)

	// GC stats
	s.metricsCollector.ObserveHistogram(s.defaultMetrics.System.GCDuration, 
		float64(m.PauseTotalNs)/1e9, // Convert to seconds
		nil)
}

// GetAllRecorders returns all metrics recorders for easy access
func GetAllRecorders(metricsCollector metrics.MetricsCollector, logger *slog.Logger) (*EmailMetricsRecorder, *AuthMetricsRecorder, *BusinessMetricsRecorder, *SystemMetricsCollector) {
	return NewEmailMetricsRecorder(metricsCollector),
		   NewAuthMetricsRecorder(metricsCollector),
		   NewBusinessMetricsRecorder(metricsCollector),
		   NewSystemMetricsCollector(metricsCollector, logger)
}

package repository

import (
	"context"
	"time"

	"gorm.io/gorm"
)

// BaseModel represents the common fields for all database models
type BaseModel struct {
	ID        uint           `json:"id" gorm:"primarykey"`
	CreatedAt time.Time      `json:"created_at"`
	UpdatedAt time.Time      `json:"updated_at"`
	DeletedAt gorm.DeletedAt `json:"-" gorm:"index"`
}

// Repository interface defines common repository operations
type Repository[T any] interface {
	// Basic CRUD operations
	Create(ctx context.Context, entity *T) error
	GetByID(ctx context.Context, id uint) (*T, error)
	Update(ctx context.Context, entity *T) error
	Delete(ctx context.Context, id uint) error

	// Batch operations
	CreateMany(ctx context.Context, entities []T) error
	UpdateMany(ctx context.Context, entities []T) error
	DeleteMany(ctx context.Context, ids []uint) error

	// Query operations
	List(ctx context.Context, opts ListOptions) ([]T, error)
	Count(ctx context.Context, opts CountOptions) (int64, error)
	Exists(ctx context.Context, id uint) (bool, error)

	// Advanced operations
	FindWhere(ctx context.Context, conditions map[string]interface{}, opts ListOptions) ([]T, error)
	CountWhere(ctx context.Context, conditions map[string]interface{}) (int64, error)

	// Transaction support
	WithTx(tx *gorm.DB) Repository[T]
}

// ListOptions provides options for listing entities
type ListOptions struct {
	Limit      int
	Offset     int
	OrderBy    string
	OrderDir   string // "asc" or "desc"
	Preloads   []string
	Conditions map[string]interface{}
}

// CountOptions provides options for counting entities
type CountOptions struct {
	Conditions map[string]interface{}
}

// BaseRepository provides a basic implementation of Repository interface
type BaseRepository[T any] struct {
	db    *gorm.DB
	model T
}

// NewBaseRepository creates a new base repository
func NewBaseRepository[T any](db *gorm.DB) *BaseRepository[T] {
	var model T
	return &BaseRepository[T]{
		db:    db,
		model: model,
	}
}

// Create creates a new entity
func (r *BaseRepository[T]) Create(ctx context.Context, entity *T) error {
	return r.db.WithContext(ctx).Create(entity).Error
}

// GetByID retrieves an entity by ID
func (r *BaseRepository[T]) GetByID(ctx context.Context, id uint) (*T, error) {
	var entity T
	err := r.db.WithContext(ctx).First(&entity, id).Error
	if err != nil {
		return nil, err
	}
	return &entity, nil
}

// Update updates an entity
func (r *BaseRepository[T]) Update(ctx context.Context, entity *T) error {
	return r.db.WithContext(ctx).Save(entity).Error
}

// Delete soft deletes an entity by ID
func (r *BaseRepository[T]) Delete(ctx context.Context, id uint) error {
	var entity T
	return r.db.WithContext(ctx).Delete(&entity, id).Error
}

// CreateMany creates multiple entities in a batch
func (r *BaseRepository[T]) CreateMany(ctx context.Context, entities []T) error {
	if len(entities) == 0 {
		return nil
	}
	return r.db.WithContext(ctx).CreateInBatches(entities, 100).Error
}

// UpdateMany updates multiple entities
func (r *BaseRepository[T]) UpdateMany(ctx context.Context, entities []T) error {
	if len(entities) == 0 {
		return nil
	}

	return r.db.WithContext(ctx).Transaction(func(tx *gorm.DB) error {
		for _, entity := range entities {
			if err := tx.Save(&entity).Error; err != nil {
				return err
			}
		}
		return nil
	})
}

// DeleteMany soft deletes multiple entities by IDs
func (r *BaseRepository[T]) DeleteMany(ctx context.Context, ids []uint) error {
	if len(ids) == 0 {
		return nil
	}
	var entity T
	return r.db.WithContext(ctx).Delete(&entity, ids).Error
}

// List retrieves entities with options
func (r *BaseRepository[T]) List(ctx context.Context, opts ListOptions) ([]T, error) {
	var entities []T

	query := r.db.WithContext(ctx).Model(&r.model)

	// Apply conditions
	for key, value := range opts.Conditions {
		query = query.Where(key, value)
	}

	// Apply preloads
	for _, preload := range opts.Preloads {
		query = query.Preload(preload)
	}

	// Apply ordering
	if opts.OrderBy != "" {
		orderDir := "asc"
		if opts.OrderDir == "desc" {
			orderDir = "desc"
		}
		query = query.Order(opts.OrderBy + " " + orderDir)
	}

	// Apply pagination
	if opts.Limit > 0 {
		query = query.Limit(opts.Limit)
	}
	if opts.Offset > 0 {
		query = query.Offset(opts.Offset)
	}

	err := query.Find(&entities).Error
	return entities, err
}

// Count counts entities with options
func (r *BaseRepository[T]) Count(ctx context.Context, opts CountOptions) (int64, error) {
	var count int64

	query := r.db.WithContext(ctx).Model(&r.model)

	// Apply conditions
	for key, value := range opts.Conditions {
		query = query.Where(key, value)
	}

	err := query.Count(&count).Error
	return count, err
}

// Exists checks if an entity exists by ID
func (r *BaseRepository[T]) Exists(ctx context.Context, id uint) (bool, error) {
	var count int64
	err := r.db.WithContext(ctx).Model(&r.model).Where("id = ?", id).Count(&count).Error
	return count > 0, err
}

// FindWhere finds entities with custom conditions
func (r *BaseRepository[T]) FindWhere(ctx context.Context, conditions map[string]interface{}, opts ListOptions) ([]T, error) {
	// Merge conditions
	if opts.Conditions == nil {
		opts.Conditions = conditions
	} else {
		for key, value := range conditions {
			opts.Conditions[key] = value
		}
	}

	return r.List(ctx, opts)
}

// CountWhere counts entities with custom conditions
func (r *BaseRepository[T]) CountWhere(ctx context.Context, conditions map[string]interface{}) (int64, error) {
	return r.Count(ctx, CountOptions{Conditions: conditions})
}

// WithTx returns a new repository instance with the given transaction
func (r *BaseRepository[T]) WithTx(tx *gorm.DB) Repository[T] {
	return &BaseRepository[T]{
		db:    tx,
		model: r.model,
	}
}

// GetTableName returns the table name for the entity
func (r *BaseRepository[T]) GetTableName() string {
	stmt := &gorm.Statement{DB: r.db}
	stmt.Parse(&r.model)
	return stmt.Schema.Table
}

// SoftDelete permanently deletes an entity (bypass soft delete)
func (r *BaseRepository[T]) SoftDelete(ctx context.Context, id uint) error {
	var entity T
	return r.db.WithContext(ctx).Unscoped().Delete(&entity, id).Error
}

// Restore restores a soft-deleted entity
func (r *BaseRepository[T]) Restore(ctx context.Context, id uint) error {
	var entity T
	return r.db.WithContext(ctx).Unscoped().Model(&entity).Where("id = ?", id).Update("deleted_at", nil).Error
}

// BatchProcessor provides batch processing capabilities
type BatchProcessor[T any] struct {
	repo      Repository[T]
	batchSize int
}

// NewBatchProcessor creates a new batch processor
func NewBatchProcessor[T any](repo Repository[T], batchSize int) *BatchProcessor[T] {
	if batchSize <= 0 {
		batchSize = 100
	}
	return &BatchProcessor[T]{
		repo:      repo,
		batchSize: batchSize,
	}
}

// ProcessInBatches processes entities in batches
func (bp *BatchProcessor[T]) ProcessInBatches(ctx context.Context, entities []T, processor func(batch []T) error) error {
	for i := 0; i < len(entities); i += bp.batchSize {
		end := i + bp.batchSize
		if end > len(entities) {
			end = len(entities)
		}

		batch := entities[i:end]
		if err := processor(batch); err != nil {
			return err
		}
	}
	return nil
}

// QueryBuilder provides a fluent interface for building queries
type QueryBuilder[T any] struct {
	db         *gorm.DB
	conditions []func(*gorm.DB) *gorm.DB
}

// NewQueryBuilder creates a new query builder
func NewQueryBuilder[T any](db *gorm.DB) *QueryBuilder[T] {
	return &QueryBuilder[T]{
		db:         db,
		conditions: make([]func(*gorm.DB) *gorm.DB, 0),
	}
}

// Where adds a where condition
func (qb *QueryBuilder[T]) Where(query interface{}, args ...interface{}) *QueryBuilder[T] {
	qb.conditions = append(qb.conditions, func(db *gorm.DB) *gorm.DB {
		return db.Where(query, args...)
	})
	return qb
}

// Order adds an order clause
func (qb *QueryBuilder[T]) Order(value interface{}) *QueryBuilder[T] {
	qb.conditions = append(qb.conditions, func(db *gorm.DB) *gorm.DB {
		return db.Order(value)
	})
	return qb
}

// Limit adds a limit clause
func (qb *QueryBuilder[T]) Limit(limit int) *QueryBuilder[T] {
	qb.conditions = append(qb.conditions, func(db *gorm.DB) *gorm.DB {
		return db.Limit(limit)
	})
	return qb
}

// Offset adds an offset clause
func (qb *QueryBuilder[T]) Offset(offset int) *QueryBuilder[T] {
	qb.conditions = append(qb.conditions, func(db *gorm.DB) *gorm.DB {
		return db.Offset(offset)
	})
	return qb
}

// Preload adds a preload clause
func (qb *QueryBuilder[T]) Preload(query string, args ...interface{}) *QueryBuilder[T] {
	qb.conditions = append(qb.conditions, func(db *gorm.DB) *gorm.DB {
		return db.Preload(query, args...)
	})
	return qb
}

// Build builds the final query
func (qb *QueryBuilder[T]) Build() *gorm.DB {
	query := qb.db
	for _, condition := range qb.conditions {
		query = condition(query)
	}
	return query
}

// Find executes the query and returns results
func (qb *QueryBuilder[T]) Find(ctx context.Context) ([]T, error) {
	var results []T
	var model T
	err := qb.Build().WithContext(ctx).Model(&model).Find(&results).Error
	return results, err
}

// First executes the query and returns the first result
func (qb *QueryBuilder[T]) First(ctx context.Context) (*T, error) {
	var result T
	err := qb.Build().WithContext(ctx).First(&result).Error
	if err != nil {
		return nil, err
	}
	return &result, nil
}

// Count executes the query and returns the count
func (qb *QueryBuilder[T]) Count(ctx context.Context) (int64, error) {
	var count int64
	var model T
	err := qb.Build().WithContext(ctx).Model(&model).Count(&count).Error
	return count, err
}

package domain

import (
	"time"

	authdomain "github.com/acheevo/fullstack-template/internal/auth/domain"
)

// User management DTOs

// UpdateProfileRequest represents a user profile update request
type UpdateProfileRequest struct {
	FirstName string `json:"first_name" binding:"required,min=1,max=50"`
	LastName  string `json:"last_name" binding:"required,min=1,max=50"`
	Avatar    string `json:"avatar" binding:"omitempty,url"`
}

// UpdatePreferencesRequest represents a user preferences update request
type UpdatePreferencesRequest struct {
	Theme         string                           `json:"theme" binding:"omitempty,oneof=light dark system"`
	Language      string                           `json:"language" binding:"omitempty,len=2"`
	Timezone      string                           `json:"timezone" binding:"omitempty"`
	Notifications authdomain.NotificationPrefs     `json:"notifications"`
	Privacy       authdomain.PrivacyPrefs          `json:"privacy"`
	Custom        map[string]interface{}           `json:"custom"`
}

// ChangeEmailRequest represents an email change request
type ChangeEmailRequest struct {
	NewEmail string `json:"new_email" binding:"required,email"`
	Password string `json:"password" binding:"required"`
}

// UserListRequest represents a request to list users with filtering and pagination
type UserListRequest struct {
	Page      int                    `form:"page,default=1" binding:"min=1"`
	PageSize  int                    `form:"page_size,default=20" binding:"min=1,max=100"`
	Search    string                 `form:"search"`
	Role      authdomain.UserRole    `form:"role" binding:"omitempty,oneof=user admin"`
	Status    authdomain.UserStatus  `form:"status" binding:"omitempty,oneof=active inactive suspended"`
	SortBy    string                 `form:"sort_by,default=created_at" binding:"omitempty,oneof=created_at updated_at last_login_at email first_name last_name"`
	SortOrder string                 `form:"sort_order,default=desc" binding:"omitempty,oneof=asc desc"`
}

// UserListResponse represents the response for user list requests
type UserListResponse struct {
	Users      []*UserSummary `json:"users"`
	Pagination Pagination     `json:"pagination"`
}

// UserSummary represents a summary of user information for list views
type UserSummary struct {
	ID            uint                   `json:"id"`
	Email         string                 `json:"email"`
	FirstName     string                 `json:"first_name"`
	LastName      string                 `json:"last_name"`
	Role          authdomain.UserRole    `json:"role"`
	Status        authdomain.UserStatus  `json:"status"`
	EmailVerified bool                   `json:"email_verified"`
	Avatar        string                 `json:"avatar,omitempty"`
	LastLoginAt   *time.Time             `json:"last_login_at"`
	CreatedAt     time.Time              `json:"created_at"`
	UpdatedAt     time.Time              `json:"updated_at"`
}

// UserDetailResponse represents detailed user information
type UserDetailResponse struct {
	*authdomain.UserResponse
	LoginHistory []LoginHistoryEntry `json:"login_history,omitempty"`
	AuditTrail   []AuditLogEntry     `json:"audit_trail,omitempty"`
}

// LoginHistoryEntry represents a login history entry
type LoginHistoryEntry struct {
	ID        uint      `json:"id"`
	IPAddress string    `json:"ip_address"`
	UserAgent string    `json:"user_agent"`
	Success   bool      `json:"success"`
	CreatedAt time.Time `json:"created_at"`
}

// AuditLogEntry represents an audit log entry
type AuditLogEntry struct {
	ID          uint                   `json:"id"`
	Action      authdomain.AuditAction `json:"action"`
	Level       authdomain.AuditLevel  `json:"level"`
	Resource    string                 `json:"resource"`
	Description string                 `json:"description"`
	IPAddress   string                 `json:"ip_address"`
	UserAgent   string                 `json:"user_agent"`
	Metadata    map[string]interface{} `json:"metadata,omitempty"`
	CreatedAt   time.Time              `json:"created_at"`
}

// Pagination represents pagination information
type Pagination struct {
	Page       int `json:"page"`
	PageSize   int `json:"page_size"`
	Total      int `json:"total"`
	TotalPages int `json:"total_pages"`
	HasNext    bool `json:"has_next"`
	HasPrev    bool `json:"has_prev"`
}

// Dashboard response types

// DashboardResponse represents the user dashboard data
type DashboardResponse struct {
	User         *authdomain.UserResponse `json:"user"`
	Stats        *UserStats               `json:"stats"`
	RecentLogins []LoginHistoryEntry      `json:"recent_logins"`
	Notifications []NotificationItem       `json:"notifications"`
}

// UserStats represents user statistics
type UserStats struct {
	TotalLogins    int       `json:"total_logins"`
	LastLoginAt    *time.Time `json:"last_login_at"`
	AccountAge     int        `json:"account_age_days"`
	ProfileComplete bool      `json:"profile_complete"`
}

// NotificationItem represents a notification item
type NotificationItem struct {
	ID        uint               `json:"id"`
	Type      NotificationType   `json:"type"`
	Title     string             `json:"title"`
	Message   string             `json:"message"`
	Read      bool               `json:"read"`
	Priority  NotificationPriority `json:"priority"`
	CreatedAt time.Time          `json:"created_at"`
}

// NotificationType represents the type of notification
type NotificationType string

const (
	NotificationTypeInfo     NotificationType = "info"
	NotificationTypeWarning  NotificationType = "warning"
	NotificationTypeError    NotificationType = "error"
	NotificationTypeSuccess  NotificationType = "success"
)

// NotificationPriority represents the priority of a notification
type NotificationPriority string

const (
	NotificationPriorityLow    NotificationPriority = "low"
	NotificationPriorityMedium NotificationPriority = "medium"
	NotificationPriorityHigh   NotificationPriority = "high"
)

// Helper functions

// ToUserSummary converts a User to UserSummary
func ToUserSummary(u *authdomain.User) *UserSummary {
	return &UserSummary{
		ID:            u.ID,
		Email:         u.Email,
		FirstName:     u.FirstName,
		LastName:      u.LastName,
		Role:          u.Role,
		Status:        u.Status,
		EmailVerified: u.EmailVerified,
		Avatar:        u.Avatar,
		LastLoginAt:   u.LastLoginAt,
		CreatedAt:     u.CreatedAt,
		UpdatedAt:     u.UpdatedAt,
	}
}

// ToUserDetailResponse converts a User to UserDetailResponse
func ToUserDetailResponse(u *authdomain.User) *UserDetailResponse {
	return &UserDetailResponse{
		UserResponse: u.ToResponse(),
	}
}

package repository

import (
	"strings"
	"time"

	"gorm.io/gorm"

	authdomain "github.com/acheevo/fullstack-template/internal/auth/domain"
	admindomain "github.com/acheevo/fullstack-template/internal/admin/domain"
)

// AuditRepository handles audit log database operations
type AuditRepository struct {
	db *gorm.DB
}

// NewAuditRepository creates a new audit repository
func NewAuditRepository(db *gorm.DB) *AuditRepository {
	return &AuditRepository{
		db: db,
	}
}

// Create creates a new audit log entry
func (r *AuditRepository) Create(log *authdomain.AuditLog) error {
	return r.db.Create(log).Error
}

// CreateAuditEntry creates an audit log entry with minimal parameters
func (r *AuditRepository) CreateAuditEntry(
	userID *uint,
	targetID *uint,
	action authdomain.AuditAction,
	level authdomain.AuditLevel,
	resource string,
	description string,
	ipAddress string,
	userAgent string,
	metadata map[string]interface{},
) error {
	log := &authdomain.AuditLog{
		UserID:      userID,
		TargetID:    targetID,
		Action:      action,
		Level:       level,
		Resource:    resource,
		Description: description,
		IPAddress:   ipAddress,
		UserAgent:   userAgent,
		Metadata:    metadata,
		CreatedAt:   time.Now(),
	}

	return r.Create(log)
}

// List retrieves audit logs with filtering and pagination
func (r *AuditRepository) List(req *admindomain.AdminAuditLogRequest) ([]*authdomain.AuditLog, int, error) {
	var logs []*authdomain.AuditLog
	var total int64

	query := r.db.Model(&authdomain.AuditLog{}).Preload("User").Preload("Target")

	// Apply filters
	if req.UserID != nil {
		query = query.Where("user_id = ?", *req.UserID)
	}

	if req.TargetID != nil {
		query = query.Where("target_id = ?", *req.TargetID)
	}

	if req.Action != "" {
		query = query.Where("action = ?", req.Action)
	}

	if req.Level != "" {
		query = query.Where("level = ?", req.Level)
	}

	if req.Resource != "" {
		query = query.Where("resource = ?", req.Resource)
	}

	if req.IPAddress != "" {
		query = query.Where("ip_address = ?", req.IPAddress)
	}

	if req.DateFrom != nil {
		query = query.Where("created_at >= ?", *req.DateFrom)
	}

	if req.DateTo != nil {
		// Add time to end of day
		endOfDay := req.DateTo.Add(23*time.Hour + 59*time.Minute + 59*time.Second)
		query = query.Where("created_at <= ?", endOfDay)
	}

	// Count total records
	if err := query.Count(&total).Error; err != nil {
		return nil, 0, err
	}

	// Apply pagination and sorting
	offset := (req.Page - 1) * req.PageSize
	if err := query.Order("created_at DESC").Offset(offset).Limit(req.PageSize).Find(&logs).Error; err != nil {
		return nil, 0, err
	}

	return logs, int(total), nil
}

// GetByID retrieves an audit log by ID
func (r *AuditRepository) GetByID(id uint) (*authdomain.AuditLog, error) {
	var log authdomain.AuditLog
	err := r.db.Preload("User").Preload("Target").First(&log, id).Error
	if err != nil {
		if err == gorm.ErrRecordNotFound {
			return nil, admindomain.ErrAuditLogNotFound
		}
		return nil, err
	}
	return &log, nil
}

// GetUserAuditHistory retrieves audit history for a specific user
func (r *AuditRepository) GetUserAuditHistory(userID uint, limit int) ([]*authdomain.AuditLog, error) {
	var logs []*authdomain.AuditLog
	query := r.db.Where("user_id = ? OR target_id = ?", userID, userID).
		Preload("User").
		Preload("Target").
		Order("created_at DESC")

	if limit > 0 {
		query = query.Limit(limit)
	}

	err := query.Find(&logs).Error
	return logs, err
}

// GetRecentLogs retrieves recent audit logs
func (r *AuditRepository) GetRecentLogs(limit int) ([]*authdomain.AuditLog, error) {
	var logs []*authdomain.AuditLog
	err := r.db.Preload("User").Preload("Target").
		Order("created_at DESC").
		Limit(limit).
		Find(&logs).Error
	return logs, err
}

// DeleteOldLogs deletes audit logs older than the specified duration
func (r *AuditRepository) DeleteOldLogs(olderThan time.Duration) (int64, error) {
	cutoffDate := time.Now().Add(-olderThan)
	result := r.db.Where("created_at < ?", cutoffDate).Delete(&authdomain.AuditLog{})
	return result.RowsAffected, result.Error
}

// GetLogsByAction retrieves logs by specific action
func (r *AuditRepository) GetLogsByAction(action authdomain.AuditAction, limit int) ([]*authdomain.AuditLog, error) {
	var logs []*authdomain.AuditLog
	query := r.db.Where("action = ?", action).
		Preload("User").
		Preload("Target").
		Order("created_at DESC")

	if limit > 0 {
		query = query.Limit(limit)
	}

	err := query.Find(&logs).Error
	return logs, err
}

// GetLogsByLevel retrieves logs by severity level
func (r *AuditRepository) GetLogsByLevel(level authdomain.AuditLevel, limit int) ([]*authdomain.AuditLog, error) {
	var logs []*authdomain.AuditLog
	query := r.db.Where("level = ?", level).
		Preload("User").
		Preload("Target").
		Order("created_at DESC")

	if limit > 0 {
		query = query.Limit(limit)
	}

	err := query.Find(&logs).Error
	return logs, err
}

// SearchLogs searches audit logs by description
func (r *AuditRepository) SearchLogs(searchTerm string, limit int) ([]*authdomain.AuditLog, error) {
	var logs []*authdomain.AuditLog
	searchPattern := "%" + strings.ToLower(searchTerm) + "%"

	query := r.db.Where("LOWER(description) LIKE ?", searchPattern).
		Preload("User").
		Preload("Target").
		Order("created_at DESC")

	if limit > 0 {
		query = query.Limit(limit)
	}

	err := query.Find(&logs).Error
	return logs, err
}

// GetAuditStatistics retrieves audit log statistics
func (r *AuditRepository) GetAuditStatistics(days int) (*AuditStatistics, error) {
	stats := &AuditStatistics{}
	startDate := time.Now().AddDate(0, 0, -days)

	// Total logs
	r.db.Model(&authdomain.AuditLog{}).Where("created_at >= ?", startDate).Count(&stats.TotalLogs)

	// Logs by level
	r.db.Model(&authdomain.AuditLog{}).Where("level = ? AND created_at >= ?", authdomain.AuditLevelInfo, startDate).Count(&stats.InfoLogs)
	r.db.Model(&authdomain.AuditLog{}).Where("level = ? AND created_at >= ?", authdomain.AuditLevelWarning, startDate).Count(&stats.WarningLogs)
	r.db.Model(&authdomain.AuditLog{}).Where("level = ? AND created_at >= ?", authdomain.AuditLevelError, startDate).Count(&stats.ErrorLogs)

	// Most active users (top 10)
	var activeUsers []ActiveUserStat
	r.db.Model(&authdomain.AuditLog{}).
		Select("user_id, COUNT(*) as log_count").
		Where("user_id IS NOT NULL AND created_at >= ?", startDate).
		Group("user_id").
		Order("log_count DESC").
		Limit(10).
		Find(&activeUsers)
	stats.MostActiveUsers = activeUsers

	return stats, nil
}

// AuditStatistics represents audit log statistics
type AuditStatistics struct {
	TotalLogs       int64            `json:"total_logs"`
	InfoLogs        int64            `json:"info_logs"`
	WarningLogs     int64            `json:"warning_logs"`
	ErrorLogs       int64            `json:"error_logs"`
	MostActiveUsers []ActiveUserStat `json:"most_active_users"`
}

// ActiveUserStat represents active user statistics
type ActiveUserStat struct {
	UserID   uint  `json:"user_id"`
	LogCount int64 `json:"log_count"`
}

package repository

import (
	"fmt"
	"strings"
	"time"

	"gorm.io/gorm"

	authdomain "github.com/acheevo/fullstack-template/internal/auth/domain"
	"github.com/acheevo/fullstack-template/internal/user/domain"
)

// UserRepository handles user-related database operations
type UserRepository struct {
	db *gorm.DB
}

// NewUserRepository creates a new user repository
func NewUserRepository(db *gorm.DB) *UserRepository {
	return &UserRepository{
		db: db,
	}
}

// GetByID retrieves a user by ID
func (r *UserRepository) GetByID(id uint) (*authdomain.User, error) {
	var user authdomain.User
	err := r.db.First(&user, id).Error
	if err != nil {
		if err == gorm.ErrRecordNotFound {
			return nil, domain.ErrUserNotFound
		}
		return nil, err
	}
	return &user, nil
}

// Update updates a user's information
func (r *UserRepository) Update(user *authdomain.User) error {
	return r.db.Save(user).Error
}

// UpdateProfile updates a user's profile information
func (r *UserRepository) UpdateProfile(userID uint, req *domain.UpdateProfileRequest) error {
	updates := map[string]interface{}{
		"first_name": strings.TrimSpace(req.FirstName),
		"last_name":  strings.TrimSpace(req.LastName),
		"updated_at": time.Now(),
	}

	if req.Avatar != "" {
		updates["avatar"] = req.Avatar
	}

	return r.db.Model(&authdomain.User{}).Where("id = ?", userID).Updates(updates).Error
}

// UpdatePreferences updates a user's preferences
func (r *UserRepository) UpdatePreferences(userID uint, preferences authdomain.UserPreferences) error {
	return r.db.Model(&authdomain.User{}).
		Where("id = ?", userID).
		Update("preferences", preferences).Error
}

// GetPreferences retrieves a user's preferences
func (r *UserRepository) GetPreferences(userID uint) (*authdomain.UserPreferences, error) {
	var user authdomain.User
	err := r.db.Select("preferences").First(&user, userID).Error
	if err != nil {
		if err == gorm.ErrRecordNotFound {
			return nil, domain.ErrUserNotFound
		}
		return nil, err
	}
	return &user.Preferences, nil
}

// List retrieves users with filtering and pagination
func (r *UserRepository) List(req *domain.UserListRequest) ([]*authdomain.User, int, error) {
	var users []*authdomain.User
	var total int64

	query := r.db.Model(&authdomain.User{})

	// Apply filters
	if req.Search != "" {
		searchTerm := "%" + strings.ToLower(req.Search) + "%"
		query = query.Where(
			"LOWER(email) LIKE ? OR LOWER(first_name) LIKE ? OR LOWER(last_name) LIKE ?",
			searchTerm, searchTerm, searchTerm,
		)
	}

	if req.Role != "" {
		query = query.Where("role = ?", req.Role)
	}

	if req.Status != "" {
		query = query.Where("status = ?", req.Status)
	}

	// Count total records
	if err := query.Count(&total).Error; err != nil {
		return nil, 0, err
	}

	// Apply sorting
	orderClause := fmt.Sprintf("%s %s", req.SortBy, strings.ToUpper(req.SortOrder))
	query = query.Order(orderClause)

	// Apply pagination
	offset := (req.Page - 1) * req.PageSize
	if err := query.Offset(offset).Limit(req.PageSize).Find(&users).Error; err != nil {
		return nil, 0, err
	}

	return users, int(total), nil
}

// GetUserStats retrieves user statistics for dashboard
func (r *UserRepository) GetUserStats(userID uint) (*domain.UserStats, error) {
	var user authdomain.User
	err := r.db.Select("created_at, last_login_at").First(&user, userID).Error
	if err != nil {
		if err == gorm.ErrRecordNotFound {
			return nil, domain.ErrUserNotFound
		}
		return nil, err
	}

	// Calculate account age
	accountAge := int(time.Since(user.CreatedAt).Hours() / 24)

	// Get total login count (would need a login history table for accurate count)
	// For now, we'll use a placeholder
	totalLogins := 1
	if user.LastLoginAt != nil {
		totalLogins = 5 // Placeholder
	}

	// Check if profile is complete
	profileComplete := user.FirstName != "" && user.LastName != "" && user.EmailVerified

	return &domain.UserStats{
		TotalLogins:     totalLogins,
		LastLoginAt:     user.LastLoginAt,
		AccountAge:      accountAge,
		ProfileComplete: profileComplete,
	}, nil
}

// UpdateEmail updates a user's email address
func (r *UserRepository) UpdateEmail(userID uint, newEmail string) error {
	updates := map[string]interface{}{
		"email":          strings.ToLower(strings.TrimSpace(newEmail)),
		"email_verified": false, // Reset email verification when email changes
		"updated_at":     time.Now(),
	}

	return r.db.Model(&authdomain.User{}).Where("id = ?", userID).Updates(updates).Error
}

// CheckEmailExists checks if an email already exists (excluding a specific user ID)
func (r *UserRepository) CheckEmailExists(email string, excludeUserID uint) (bool, error) {
	var count int64
	query := r.db.Model(&authdomain.User{}).Where("email = ?", strings.ToLower(strings.TrimSpace(email)))

	if excludeUserID > 0 {
		query = query.Where("id != ?", excludeUserID)
	}

	err := query.Count(&count).Error
	return count > 0, err
}

// GetUsersByIDs retrieves multiple users by their IDs
func (r *UserRepository) GetUsersByIDs(ids []uint) ([]*authdomain.User, error) {
	var users []*authdomain.User
	err := r.db.Where("id IN ?", ids).Find(&users).Error
	return users, err
}

// UpdateUserRole updates a user's role
func (r *UserRepository) UpdateUserRole(userID uint, role authdomain.UserRole) error {
